css:
@-moz-document domain(d.pr) {

.backdrop.ready 
{
	box-shadow: 0px 1px 8px 2px rgba(0, 0, 0, 0.25);
	position: absolute;
	display: block;
	transform: translate(-50%, -50%);
	left: 50%;
	top: 50%;
}

.image-drop
{
	background: none repeat scroll 0% 0% rgb(229,229,229);
}


.meta 
{
    padding: 5px 0px;
    	border-bottom: 3px solid rgba(91, 133, 174, 1);
	background: none repeat scroll 0% 0% rgb(248,248,248);
	/*background: linear-gradient(to bottom, rgb(248, 248, 248) 0px, rgb(238, 238, 238) 100%) repeat scroll 0% 0% transparent;*/
	box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5), 0px 0px 0px 1px rgb(0, 0, 0);
	border-top: 1px solid rgba(255, 255, 255, 0.03);
	width: 100%;
	height: auto;
	position: fixed;
	left: 0px;
	top: 0px;
}



.share-popover
{
    position: fixed;
}

.meta .download {
    margin: 6px 30px 0px 15px;
}

.meta .share {
    margin: 6px 30px 0px 15px;
}

.code-contain 
{
    padding: 15px 0px;
    border: 10px solid rgb(200,200,200);
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
    height: 90%;
    width: 90%;
    transform: translate(5%,10%);
}

.text pre
{
    position: inherit;
    background: none repeat scroll 0% 0% rgb(255,255,255);
    padding: 15px 15px;
    border-radius: 5px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
}

.note
{
    transform: translate(0px,50px);
}

.viewport 
{
        background: none repeat scroll 0% 0% rgb(229,229,229);
}

.file-upload 
{
    transform: translate(0px,50%);
}

.share-popover.show
{
    position: fixed;
}

.html p
{
    position: inherit;
    background: none repeat scroll 0% 0% rgb(255,255,255);
    padding: 15px 15px;
    border-radius: 5px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
}


.meta .download
{
    transform: translate(-20px);
}

.meta .share
{
    transform: translate(-20px)
}

}
