background-size: 100% auto !important;
background-position: center top !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
}
.fbChatSidebar, .fbNubFlyoutBody{
/*The URL below is the background for contact list*/
background-color: transparent !important;
background-size: 100% auto !important;
background-position: center top !important;
background-repeat:no-repeat !important;
}
#pagelet_ticker, .fbChatSidebarBody, .fbNubFlyoutBodyContent>.conversationContainer,
.fbNubFlyoutBodyContent>.fbChatOrderedList{
/*Transparence of the contact list background*/
background-color: rgba(255,255,255,0.35) !important;
}
#mainContainer, #contentCol,
#contentArea, .uiListVerticalItemBorder, .fbStreamPermalinkHeader{
border: none !important;
}
#leftCol, .newsFeedComposer,
.rightColumnWrapper, #rightCol,
#headerArea, .attachmentSelectForm,
.noborder, .ci_module_list,
.fbTimelineTopSection, .fbStreamPermalinkHeader,
.fbTimelineStickyHeader>.stickyHeaderWrap>.back{
background-color: rgba(255,255,255,0.65) !important;
}
.fbTimelineTimePeriod, #contentCol,
#contentCurve, #pageFooter,
#contentArea, .fbTimelineUFI{
background-color: transparent !important;
}
.fbxWelcomeBoxBlock .fbxWelcomeBoxImg {
height: 40px !important;
width: 40px !important;
display: none;
}
.fbNubFlyoutTitlebar, .fbNubButton {
background-color: rgba(109,132,180,0.65) !important;
}
.mainWrapper, .timelineUnitContainer,
.questionsListItem>.UIImageBlock>.UIImageBlock_Content,
.noteDashboard>.notes_dashboard>.noteEntry,
#pagelet_pokes, .friendListFilterNullState,
.contentListWidth>.uiListItem>.content,
.timelineReportContainer, .noteDashboard>.uiPagerTopBorder>.summary{
background-color: rgba(255,255,255,0.65) !important;
}
.mainWrapper:hover, .timelineUnitContainer:hover,
.questionsListItem>.UIImageBlock>.UIImageBlock_Content:hover,
.noteDashboard>.notes_dashboard>.noteEntry:hover,
#pagelet_pokes:hover, .friendListFilterNullState:hover,
.contentListWidth>.uiListItem>.content:hover,
#footerContainer>.navigation:hover{
background-color: rgba(255,255,255,0.85) !important;
}
.uiUfiAddComment, .uiUfiComment, .uiUfiLike, .ci_module{
background-color: rgba(237,239,244,0.65) !important;
}
.threadRow {
background-color: rgba(235,238,244,0.65) !important;
}
.threadRow:hover {
background-color: rgba(235,238,244,0.85) !important;
}

#facebook .home #leftCol {
position: fixed !important;
}

#facebook .uiSideNav .sideNavItem {
margin-left: 0px !important;
padding-right: 0px !important;
}
#facebook .uiSideNav .item, .uiSideNav .subitem {
margin: 0px !important;
}
#facebook .uiSideNav .buttonWrap {
float: right !important;
margin-right: -10px;
}
#facebook .uiSideNav .uiSelectorMenuWrapper {
left: auto !important;
right: 0px !important;
}

#facebook .homeSideNav .sideNavItem {
height: 0px !important;
overflow: hidden !important;
transition: height 0.2s linear 0.2s !important;
-moz-transition: height 0.2s linear 0.2s !important;
-webkit-transition: height 0.2s linear 0.2s !important;
-o-transition: height 0.2s linear 0.2s !important;
}

#facebook .homeSideNav:hover .sideNavItem {
height: 21px !important;
}
#facebook .homeSideNav .sideNavItem:hover {
overflow: visible !important;
}

#facebook .homeSideNav h4:after  {
padding-left: 3px !important;
content:"  ˇ";
transition: all 0.2s linear 0.2s !important;
-moz-transition: all 0.2s linear 0.2s !important;
-webkit-transition: all 0.2s linear 0.2s !important;
-o-transition: all 0.2s linear 0.2s !important;
}
#facebook .homeSideNav:hover h4:after {
color: transparent !important;
text-shadow: none !important;
}

#facebook #pagelet_navigation .homeSideNav h4,
#facebook #pagelet_navigation .homeSideNav h4 *,
#facebook #pagelet_navigation .moreSectionsLink {
text-align: left !important;
}

#facebook #pagelet_navigation .homeSideNav h4:not(.hidden_elem) {
word-wrap: normal !important;
white-space: nowrap !important;
display: inline-block !important;}

element.style {
padding-top: 0px;
}
.timelineLayout #rightCol {
margin-right: -5px;
width: 122px;
}
.hasRightCol #rightCol {
margin-top: 0;
}
#leftCol, .newsFeedComposer, .rightColumnWrapper, #rightCol, #headerArea, .attachmentSelectForm, .noborder, .ci_module_list, .fbTimelineTopSection, .fbStreamPermalinkHeader, .fbTimelineStickyHeader>.stickyHeaderWrap>.back {
background-color: rgba(255,255,255,0.65) !important;
}
#rightCol {
margin-top: -10000px;
float: right;
padding-right: 0;
width: 244px;
word-wrap: break-word;
display: none;
}
user agent stylesheetdiv {
display: block;
}
Inherited from div#contentCol.clearfix.hasRightCol
.clearfix {
zoom: 1;
display: none}
Inherited from div#content.fb_content.clearfix
.clearfix {
zoom: 1;
}
Inherited from div#globalContainer.uiContextualLayerParent
#globalContainer {
zoom: 1;
}


