css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("stackoverflow.com"),
domain("stackexchange.com"),
domain("serverfault.com"),
domain("superuser.com") {
    .topbar {
        height:31px !important;
        background:rgba(225,225,225,.5) !important;
        box-shadow:0 -1px 1px rgba(0,0,0,.05) inset;
    }
    .topbar .topbar-icon {
        height:31px !important;
        opacity:.6;
    }
    .topbar .topbar-links .search-container input[type="text"] {
        border:1px solid rgba(0,0,0,.2) !important;
        margin-top:2px !important;
    }
    .topbar .topbar-links .topbar-menu-links a, .topbar .topbar-links .topbar-menu-links a:visited, .topbar .topbar-links .topbar-flair .reputation, .topbar .topbar-links .topbar-flair .badgecount {
        color:#aaa !important;
    }
    .topbar .topbar-links .profile-me {
        height:31px !important;
    }
    .topbar .topbar-links .topbar-menu-links a:hover, .topbar .topbar-links .profile-me:hover, .topbar .topbar-icon:hover {
        background-color:#fff !important;
        box-shadow:0 1px 1px rgba(0,0,0,.1) inset;
    }
    
    .topbar .icon-site-switcher {
        margin-right:5px !important;
        width:28px !important;
        background-position: 8px 10px !important;
        background-image: url(data:image/png;base64,) !important;
    }
    
    .topbar .topbar-links .profile-me > div > img {
        display:none !important;
    }
    .topbar .topbar-links .topbar-menu-links a, .topbar .topbar-links .topbar-menu-links a:visited {
        padding:9px 7px 0 !important;
        height:22px !important;
    }
    .topbar .topbar-links .links-container {
        padding-top:9px;
    }
    .topbar .topbar-links .profile-me {
        margin-right:0px !important;
    }
    .topbar .icon-help .triangle {
        opacity:.2;
    }
    .topbar .topbar-links .search-container input[type="text"] {
        width:50px !important;
        opacity:.7 !important;
        background-color:#fff !important;
        background-image:none !important;
        padding-left:5px !important;
        transition:width .2s;
    }
    .topbar .topbar-links .search-container input[type="text"]:focus {
        width:188px !important;
        opacity:1 !important;
    }
    .topbar .hat {
        display:none !important;
    }
}
