css:
@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix('https://www.tumblr.com/dashboard')   {
#dashboard_index .post_container > .post.is_mine.with_permalink {
height: 64px;
background: #fff
}

#dashboard_index .post.is_mine.with_permalink {
  border-radius: 5px;
}

#dashboard_index .post.is_mine.with_permalink .post_wrapper .post_content, #dashboard_index .post.is_mine.with_permalink .post_wrapper .post_tags, #dashboard_index .post.is_mine.with_permalink .post_wrapper .post_footer {
  display: none;
}

#dashboard_index .post.is_mine.with_permalink .post_header .post_info {
  position: relative;
  z-index: 1000;
}

#dashboard_index .post.is_mine.with_permalink .post_permalink {
  display: block;
  width: 100%;
  height: 100%;
  background-repeat: no-repeat;
  background-position: top right;
  background: transparent;
}

#dashboard_index .post.is_mine.with_permalink .post_permalink:before {
  background: #cecfd1;
  border-bottom-left-radius: 6px;
  border-left: 0px solid transparent;
  top: 0; 
  right: 0;
}
}
