css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("openstreetmap.org") {
  * {
    font-family:/*[[myfont]]*/!important;
  }

  .tab.geolink, .dropdown-toggle {
    color:black!important;
    background:#eee!important;
  }

  .tab.geolink:hover, .dropdown-toggle:hover {
    background:#aaa!important;
  }

  .user-menu > A.dropdown-toggle {
    background:none!important;
    
  }

  .user-menu:not(:hover):not(:active) {
    background:#eee!important;
  }

  .user-menu:hover, .user-menu:active {
    background:#aaa!important;
  }

  .user-menu {
    border:1px solid #444!important;
  }

  .username {
    color:black!important;
  }

  .caret {
    border-top-color:black!important;
  }

  nav.primary > ul > li {
    border-right: 1px solid gray!important;
  }

  nav.primary > ul > li.dropdown > a.tab {
    border-right: 1px solid lightgray!important;
  }

  nav.primary > ul {
    border:1px solid gray!important;
  }

  .search_form {
    margin-top:1em!important;
    margin-left:1em!important;
    margin-bottom:0px!important;
    padding:0px!important;
  }

  .overlay-sidebar, .overlay-sidebar *:not([class*="leaflet"]):not([class*="control"]):not([id*="map"]):not([class*="icon"]):not(input) {
    background:none!important;
  }

  form.search_form > div.query_wrapper > input[type="text"][name="query"] {
    background:white!important;
  }

  .search_form input {
    margin-bottom:0px!important;
  }

  .search_form:not(:focus):not(:hover) input {
    opacity:.5!important;
  }

  .search_form:focus input, .search_form:hover input {
    opacity:100!important;
  }

  .search_form input[type="submit"]:not(focus):not(:hover) {
    background:#eee!important;
    color:black!important;
  }

  .search_form input[type="submit"]:focus, .search_form input[type="submit"]:hover {
    background:#aaa!important;
    color:black!important;
  }

  .geolink.editlink:hover, .geolink.editlink:active, .user-menu .dropdown-menu a:hover, .user-menu .dropdown-menu a:active {
    background:#eee!important;
    color:black!important;
  }

  .user-menu .count-number {
    background:#eee!important;
  }

  .user-menu .dropdown-menu a:hover .count-number, .user-menu .dropdown-menu a:active .count-number {
    border:1px dotted darkgrey!important;
  }

  nav.secondary A {
    color:#333!important;
  }
}
