/* i really want this to be global */

body{
	padding: 0;
	margin: 0px;
	font-family: Arial;
	font-size:12px;
background:lightcyan;

}
img{
	border: none;
}

#sidebar a{
	color: white;
	text-decoration: none;

}

#sidebar a:hover{
	color: #C0C6C5;
}

#sidebar img{
	vertical-align: center;
}


#sidebar{
	font-weight:bold;
	background: #ffffff;
background: -moz-linear-gradient(top,  #ffffff 0%, #e5e5e5 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#e5e5e5));
background: -webkit-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%);
background: -o-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%);
background: -ms-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%);
background: linear-gradient(to bottom,  #ffffff 0%,#e5e5e5 100%);
font-size:15px;
	padding-left:20px;
	height: 36px;
}
#sidebar span{
	font-size:25px;
	float:left;
}
#sidebar div{
	float:right;
	font-size:15px;
}
.content, .text{
		background: #ffffff;
background: -moz-linear-gradient(top,  #ffffff 0%, #e5e5e5 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#e5e5e5));
background: -webkit-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%);
background: -o-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%);
background: -ms-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%);
background: linear-gradient(to bottom,  #ffffff 0%,#e5e5e5 100%);


margin:3px;
padding:5px;
border:1px solid lightblue;
	-webkit-border-radius: 5px;
    border-radius: 5px;
	margin-bottom:5px;
}
h3{
border:1px solid lightblue;
	-webkit-border-radius: 5px;
    border-radius: 5px;
	font-size:15px;
	color: white;
	padding-left: 5px;
	margin:3px;
background: #a9e4f7;
background: -moz-linear-gradient(top,  #a9e4f7 0%, #0fb4e7 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#a9e4f7), color-stop(100%,#0fb4e7));
background: -webkit-linear-gradient(top,  #a9e4f7 0%,#0fb4e7 100%);
background: -o-linear-gradient(top,  #a9e4f7 0%,#0fb4e7 100%);
background: -ms-linear-gradient(top,  #a9e4f7 0%,#0fb4e7 100%);
background: linear-gradient(to bottom,  #a9e4f7 0%,#0fb4e7 100%);



}
a{
	color: darkblue;
	text-decoration: none;
	font-size:14px;
}

#pied{
	padding: 3px;
background: #a9e4f7;
background: -moz-linear-gradient(top,  #a9e4f7 0%, #0fb4e7 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#a9e4f7), color-stop(100%,#0fb4e7));
background: -webkit-linear-gradient(top,  #a9e4f7 0%,#0fb4e7 100%);
background: -o-linear-gradient(top,  #a9e4f7 0%,#0fb4e7 100%);
background: -ms-linear-gradient(top,  #a9e4f7 0%,#0fb4e7 100%);
background: linear-gradient(to bottom,  #a9e4f7 0%,#0fb4e7 100%);

	color: white;
}

input[type=submit], input[type=button], .button
{
	background-image: linear-gradient(bottom, #146B97 5%, #2E8CB6 53%, #4AA8DB 77%);
background-image: -o-linear-gradient(bottom, #146B97 5%, #2E8CB6 53%, #4AA8DB 77%);
background-image: -moz-linear-gradient(bottom, #146B97 5%, #2E8CB6 53%, #4AA8DB 77%);
background-image: -webkit-linear-gradient(bottom, #146B97 5%, #2E8CB6 53%, #4AA8DB 77%);
background-image: -ms-linear-gradient(bottom, #146B97 5%, #2E8CB6 53%, #4AA8DB 77%);
text-align:center;
color:white;
font-family:Arial;
font-size:13px;
border: 1px solid black;
padding:5px;
font-size: 13px;
border-top-left-radius: 2px;
border-top-right-radius: 2px;
border-bottom-right-radius: 2px;
border-bottom-left-radius: 2px;
text-decoration:none;
margin:2px;
	width:100%;
}

.content{

}
.content ul{
	list-style-type:none;
	padding:0;
}

thead{
	display:none;
}

.ln_topic{
	width:100%;
	-webkit-border-radius: 5px;
	border:1px solid lightblue;
    border-radius: 5px;
	margin:3px;
	padding:3px;
	background: #ffffff;
background: -moz-linear-gradient(top,  #ffffff 0%, #e5e5e5 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#e5e5e5));
