background: linear-gradient(to bottom,  #ffffff 0%,#e5e5e5 100%);

	
}

.ln_topic .image_et_titre{
	text-indent:3px;
}
.image_et_titre a{
	color:black;
}
.image_et_titre a:visited{
	color:darkgrey;
}

.liste_lastmess{
	float:right;
	margin-top:-18px;
	color:grey;
}
.liste_auteur{
	text-indent:-8px;
	
}
#intc{
	margin:2px;
}
img{
	max-width:100%;
}
.bloc .text{
	background-color:#636060;

}
.date_in_topic{
	font-weight:normal;
	float:right;
	color:black;
}
.bloc_cite{
	width:98%;
	background-color:grey;
	border:1px solid black;
	padding-left:4px;

	padding-right:4px;
}
