css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

._50kd ._kso, ._50kd ._kso:before {
    background-color: rgb(250,250,250) !important;
    background-image: none !important;
    }
._kso, ._kso:before {
    background-color: rgb(210,245,210) !important;
    background-image: none !important;
    }
._50kd ._kso {
    padding: 4px 7px 5px !important;
    }
._kso {
    padding: 6px 6px 5px 7px !important;
    text-shadow: none !important;
    }
.fbNubFlyoutTitlebar {
    background-color: rgb(108,169,108) !important;
    transition: all 0.2s;
    border-color: rgb(100,100,100) !important;
    }
.fbNubFlyoutTitlebar:hover {
    background-color: rgb(118,179,118) !important;
    }
.titlebarButtonWrapper ._510p .button:active, .titlebarButtonWrapper ._510p .button:focus, .titlebarButtonWrapper ._510p .button:hover, .titlebarButtonWrapper ._510p .button:active, .titlebarButtonWrapper ._510p .openToggler .button, .titlebarButtonWrapper ._510p .openToggler .button:active, .titlebarButtonWrapper ._510p .openToggler .button:focus, .titlebarButtonWrapper ._510p .openToggler .button:hover {
    background-color: rgb(78,139,78) !important;
    }
._50m_ .titlebar .addToThread {
    background-position: -48px -571px !important;
    }
.titlebarButtonWrapper ._510p .button {
    background-position: -68px -418px !important;
    }
._50mz .titlebar .close {
    background-position: -163px -571px !important;
    }
}
