css:
@-moz-document url-prefix(http://forums.digitalspy.co.uk/) {

/* ------------------------------------------------------------------------------------------- */
/* Käytetty monen eri henkilön koodia oman koodin mukana, joten kiitos kaikille tasapuolisesti */
/* ------------------------------------------------------------------------------------------- */


/* MuroBBS logo suurilla resoluutioilla koko ruudun levyiseksi */
         td[class="navbar_paakuva"] {background-repeat: no-repeat !important;}

/* Top Menu */

        .vbmenu_control a:link {color: #000000 !important}
        .vbmenu_control {background: #f0f0f0 !important;}
        .vbmenu_option a:link {color: #000000 !important}
        .vbmenu_option {background: #d0d0d0 !important;}
        .vbmenu_hilite a:link {color: #000000 !important}
        .vbmenu_hilite {background: #d0d0d0 !important;}
        .vbmenu_popup a:link {color: #000000 !important}
        .vbmenu_popup {background: #d0d0d0 !important;}

/* Surrounding Borders */

    .main { background: #f9f9ff !important;}
   
        body {background: #ffffff !important;}
        td.alt1Active {background: #000000 !important;}
        .page {background: #f7f7ff !important;}
        table {background: #f9f9ff !important;}
        .alt1 {border: none !important;}
        .alt2 {border: none !important;}


.page a:link {color: #4444aa !important;}
.page a:visited {color: #828386 !important;}
.page a:hover {color: #000000 !important} 

.bluetext {color: #444444 !important;}

.button {color: #3b4787 !important;}


/* Post Date & Time */
        td {font-size: 10pt !important;}
        td {font-weight: normal !important;}
        td {color: #4444aa !important;}
        td {padding: 0px 8px 0px 8px !important;}
        .smallfont {font-size: 10pt !important;}

/* View First Unread Link */
        .thead {font-size: 12pt !important;}
        .thead {color: #000000 !important;}
        .thead {background: #f0f0f0 !important;}
        .thead {padding: 6px !important;}
        .thead {border: none !important;}

/* Username */
	.bigusername {font-size: 12pt !important;}
        .bigusername {font-weight: bold !important;}
        .bigusername {color: #000000 !important;}
        .bigusername {padding: 2px !important;}

/* Username Column */
         .alt2  {font-weight: normal !important;}
         .alt2 {font-size: 11pt !important;}
        .alt2 {color: #606060 !important;}
        .alt2 {background: #f0f0ff !important;}
        .alt2 {padding: 20px !important;}

/* Posts Column */
        .alt1  {font-weight: normal !important;}
        .alt1 {font-size: 10.5pt !important;}
        .alt1 {color: #000000 !important;}
        .alt1 {background: #f9f9ff !important;}
        .alt1 {padding: 20px 20px 0px 20px !important;}

/* ylileveiden kuvien mitoitus ja scrollbar jos menee yli rajojen */
         /* table[id^="post"] >tbody >tr >td.alt1 >div {overflow: auto !important; width: 800px} */

/* Quick Reply field width, height. The text entry field and background color */
         div.panel > div[style="max-width: 10px; width: 10px ! important;"]
         {max-width: 95% !important; background: #e0e0e0 !important;}
         iframe[id="vB_Editor_QR_iframe"] {height: 250px !important;}
         iframe[id="vB_Editor_QR_iframe"] {border: none !important}
         DIV[class="panel"] {border: none !important}
         DIV[class="panel"] {background: #eff0f7 !important}
         body[class="wysiwyg"] {color: #000000 !important;}
         body[class="wysiwyg"] {background: #111ddd !important;}
         .panelsurround {background: #eff0f7 !important;}
         div.panel {color: #000000 !important;}

/* Korosta moderaattorien nimien värit ja vahvista fonttia */
         a.bigusername > span[style="font-weight: bold;"]
         {position: relative !important; top: 0 !important; color: #000000; font-size: 11pt !important;}

/* Welcome Box Time */
	.time {font-size: 10pt !important;}
        .time {font-weight: bold !important;}
        .time {color: #606060 !important;}

/* Poista käyttäjien omat allekirjoitukset */
         table[id^="post"] >tbody >tr >td.alt1 >div[id^="post_message"] {display:block !important;}
         table[id^="post"] >tbody >tr >td.alt1 >div {display:none !important;}
         table[id^="post"] >tbody >tr >td.alt1 >div[style^="margin"] {display:block !important;}

/* Poista alaosan keskustelua aiheesta linkit */
        td[style^="padding-top: 20px;"] {display: none !important;}
        .rss-date {display: none !important;}
        .rss-item {display: none !important;}


/* lainauslaatikon taustavärit ja reunojen pyöristys */
   
 .posthead {background:#f0f0ff !important;}
 .quotebox {background:#eff0f7 !important;}

}
