css:
@-moz-document url-prefix("http://www.youtube.com/watch"), url-prefix("https://www.youtube.com/watch") {
#content {
    margin-left: auto!important;  
    margin-right: auto!important;
}
#player {
    margin-left: auto;  margin-right: auto; min-width: 640px; padding: 0 !important;
}

#player .watch-medium {
    width: 100%;
  	padding: 0!important;
}
  
#player .watch-large {
    width: 100%;
  	padding: 0!important;
}  
  
#player-api {
    overflow: hidden;  float: none !important; margin-left: auto;  margin-right: auto; padding-right:358px;
}

.watch-medium #player-api {
    overflow: hidden;  float: none !important;  margin-left: auto;  margin-right: auto; padding-right:0px;
}

.watch-large #player-api {
    overflow: hidden;  float: none !important;  margin-left: auto;  margin-right: auto; padding-right:0px;
}

.html5-video-player .video-click-tracking, .html5-video-player .video-stream{
    width: 100% !important;
    height: 100% !important;
    left: 0px !important;
}

#watch7-main-container {
    padding: 0 !important;
}
#watch7-headline {
    border: 0px solid white !important;
}

#watch7-user-header {
    border: 0px solid white !important;
}

#watch7-action-buttons {
    border: 0px solid white !important;
}

#watch7-action-panels {
    border: 0px solid white !important;
}

#watch7-container {
    margin-left: auto;  margin-right: auto;  width: 999px;
}

#watch-discussion {
    border: 0px solid white !important;
}

#page-container {
    margin-left: auto;  
  	margin-right: auto;  
}

#page-container #page {
    margin-left: 0px !important;
    padding-left: 0px !important;
}

#yt-masthead-content {
    margin-left: auto;  margin-right: auto;
}

.branded-page-v2-primary-col {
    width: 639px;
}

.site-left-aligned #page.watch #guide-container {
    padding: 0px 0 10px 0px;
    left: -14px;
}

.collapsed #page.watch #guide-container {
    left: 6px;
}

#guide-main {
}

.guide-module-content {
  	padding-left: 18px;
}

#guide-channels {
    width: 166px;
}

.watch7-playlist-bar-left.watch-content {
    width: 980px;
}

.watch7-playlist-bar {
    position: relative;  height: 34px;  float: none;  margin-right: auto;  margin-left: auto;  width: 1280px;
}

.watch-medium .player-height {
    height: 750px;
}
.watch-medium .player-width {
    width: 1280px;
}

.site-left-aligned.sidebar-expanded #yt-masthead, .site-left-aligned.sidebar-expanded #masthead-appbar {
	width: 999px;
  	margin-left: auto !important;
  	margin-right: auto !important;
  	float:none;
  	display:block;
  	margin-right: auto !important;
  	margin-left: auto !important;
}

.site-left-aligned.sidebar-expanded #yt-masthead, .site-left-aligned.sidebar-expanded #ad_creative_1, .site-left-aligned.sidebar-expanded #footer, .site-left-aligned.sidebar-expanded #masthead_child_div, .site-left-aligned.sidebar-expanded #masthead-expanded-lists-container, .site-left-aligned.sidebar-expanded #masthead-appbar, .site-left-aligned.sidebar-expanded #baseDiv {
    width: 999px;
    margin-left: auto !important;
    margin-right: auto !important;
    float: none;
    display: block;
    text-align: center;
}
}

@-moz-document url("http://www.youtube.com/"), url("https://www.youtube.com/"), url-prefix("http://www.youtube.com/feed/"), url-prefix("https://www.youtube.com/feed/"), url-prefix("http://www.youtube.com/channel/"), url-prefix("https://www.youtube.com/channel/"), url-prefix("http://www.youtube.com/results?s"), url-prefix("https://www.youtube.com/results?s") {
#page-container {
    margin-left: auto;  
  	margin-right: auto;
  	width:999px;
}

#page-container #page {
    margin-left: 0px !important;
    padding-left: 0px !important;
}

#yt-masthead-content {
    margin-left: auto;  margin-right: auto;
}

.branded-page-v2-primary-col {
    width: 639px;
}

#yt-masthead {
    width: 999px;     margin-left: auto !important;     margin-right: auto !important;     float:none;     display:block;
}

.site-left-aligned #yt-masthead {
    margin-right: auto !important;
    margin-left: auto !important;
}

.yt-base-gutter #footer {
    width: 999px;
    margin-left: auto !important;
    margin-right: auto !important;
    float: none;
    display: block;
    text-align: center;
}
}
