css:
@-moz-document url-prefix("http://live.nicovideo.jp/watch/lv") {
#wall img {
  display: none !important;
}

#watch_niconicoukoku_open {
  display: block !important;
  left: -215px !important;
  width: 235px !important;
  top: 0px !important;
  margin-top: 0px !important;
  box-shadow: none !important;
  z-index: 200 !important;
}

#watch_niconicoukoku_open_btn {
  display: block !important;
  /* margin-left: 32px !important; */
  z-index: 210 !important;  
}

#watch_niconicoukoku_close_btn {
  display: none !important;
  margin-left: 32px !important;
  z-index: 210 !important;  
}

#watch_niconicoukoku_box  {
  background: rgba(0,0,0,0) !important;
}

#watch_player_top_box .livetag_box {
  width: 720px !important;
}

#watch_player_top_box .livetag_box div.cover {
  width: 670px !important;
  height: 63px !important;
  text-align: left !important;
}

ul#livetags {
  width: 670px !important;
}

#watch_player_top_box .box_inner {
  width: 670px !important;
}

#watch_player_top_box {
  max-width: 720px !important;
  width: 720px !important;
  left: -120px !important;
  empty-cells: hide !important;
}

#watch_player_top_box .tag_list li.hide {
  display: inline-block !important;
  margin-right: 10px !important;
}

div.slider_view {
 /* margin-left: -360px !important; */
}


div#page_cover {
  text-align: center !important;
}

div#page {
  display: -webkit-box;
  display: -moz-box;
  -webkit-box-orient: block-axis;
  -webkit-box-direction: vertical;
  -moz-box-orient: block-axis;
  -moz-box-direction: vertical;
  margin-left: auto;
  margin-right: auto;
} 

div#page > div {
  -webkit-box-ordinal-group: 10;
  -moz-box-ordinal-group: 10;
}

div#watch_title_box {
  -webkit-box-ordinal-group: 3 !important;
  -moz-box-ordinal-group: 3 !important;
  position:static !important;
  height: 0px !important;
  padding-top: 0px !important;
  padding-bottom: 0px !important;
  z-index: 120 !important;
}
  
div#watch_player_top_box, div#watch_message_box {
  -webkit-box-ordinal-group: 3 !important;
  -moz-box-ordinal-group: 3 !important;
}

div#watch_player_top_box, div.livetag_box {
  position:relative !important;
  z-index: 100 !important;
}

ul#livetags > table {
   position: absolute !important;
   width: 440px !important;
   left: 40px !important;
   bottom: -20px !important;
   /* left: -30px !important; */
   /* bottom: 190px !important; */
   background-color: white !important;
   z-index: 200 !important;
}
      
      
div#watch_tool_top_box {
  -webkit-box-ordinal-group: 1 !important;
  -moz-box-ordinal-group: 1 !important;
  height: 73px !important;
  z-index: 120 !important;
}

div#watch_title_box div.box_inner {
  position:static !important;
  text-align: left !important;
}
    
div#watch_title_box div.thumb_area {
  position:absolute !important;
  top: 130px !important;
}

div.sidewall_cover div.official div#watch_title_box div.thumb_area {
  top: 0px !important;
}

    
div#watch_title_box div.text_area {
  position:absolute !important;
  top: 130px !important;
  /* left: 310px !important; */
  left: 15% !important;
  /* padding-left: 23% !important; */
  text-align: left !important;
}

div.sidewall_cover div.official div#watch_title_box div.text_area {
  top: 0px !important;
}
        
div#slider_container div#wall_canvas {
  display: none !important;
}

      
div#watch_tab_box {
  -webkit-box-ordinal-group: 2 !important;
  -moz-box-ordinal-group: 2 !important;
  text-align: left !important;
}
          
div#watch_tab_box + font {
  margin: 0;
  padding: 0;
  height: 0px;
  display: inline-block;
}
div#page_cover + font {
  display: none;
}
        
div.niconikoukoku {
  -webkit-box-ordinal-group: 3 !important;
  -moz-box-ordinal-group: 3 !important;
}

div#side_area {
    display: none !important;
}

/* #wall_canvas {
   position: relative !important;
   z-index: 1 !important;
} */
            
#watch_player_box, #flvplayer_container, #flvplayer_container embed {
    position: relative !important;
    z-index: 50 !important;
}
            
#zapping_area {
    position: relative !important;
}

ul.chip_list {
  display: none !important;
margin-top: 60px !important;
margin-left: 30px !important;
            
}
            
/*
div#description_tab_view.nf_detail {
  margin-top: 00px !important;
} */

@media screen and (min-width : 850px) {
  div#watch_title_box div.text_area {
    left: 12% !important;
  }
}
@media screen and (min-width : 960px) {
  div#watch_title_box div.text_area {
    left: 15% !important;
  }
}
@media screen and (min-width : 1020px) {
  div#watch_title_box div.text_area {
    left: 17% !important;
  }
}
@media screen and (min-width : 1200px) {
  div#watch_title_box div.text_area {
    left: 21% !important;
  }
}
@media screen and (min-width : 1400px) {
  div#watch_title_box div.text_area {
    left: 24% !important;
  }
}
@media screen and (min-width : 1600px) {
  div#watch_title_box div.text_area {
    left: 26% !important;
  }
}
@media screen and (min-width : 1920px) {
  div#watch_title_box div.text_area {
    left: 29% !important;
  }
}
            
            
}
