style_setting_options.value:
/* Collapse YouTube Embed to Titlebar (Clickable Link) */
.youtube_container {
  height: 40px;
  overflow: hidden;
}

/* Replace YouTube Vids with Button-styled Link */
.youtube_container iframe { display: none !important; }
.youtube_container span.link { 
  display: block !important;
}

  /* Collapse YouTube Embed Unless Hovered (buggy) */
  .youtube_container {
    min-height: 40px;
    height: 2.5em !important;
    overflow: hidden;
    border: 1px solid gray !important;
    background-color: cornsilk;
    line-height: 1ex;
  }
  .youtube_container:hover, 
  .youtube_container:active,
  .youtube_container:focus {
      height: auto !important;
  }

  .youtube_container:before {
    float: right;
    padding: 3px 5px 5px;
    background-color: white;
    border: 1px solid gray;
    border-width: 0 0 1px 1px;
    -webkit-border-bottom-left-radius: 5px;
    border-bottom-left-radius: 5px;

    text-align: center;
    font-weight: bold;
    line-height: 0.8em;
    white-space: pre-wrap;
    content: "Hover\AHere";
    cursor: pointer;
  }

  /* Collapse GIFs unless hovered */
  img[src$='.gif'],
  img[src$='.Gif'],
  img[src$='.GIF'] {
    max-height: 32px !important;
    overflow: hidden !important;

    border: 1px solid gray !important;
    background-color: cornsilk !important;
  }
  
  img[src$='.gif']:hover,
  img[src$='.Gif']:hover,
  img[src$='.GIF']:hover {
    max-height: none !important;
    overflow: auto !important;
  }
