css:
@-moz-document domain("apps.facebook.com"), regexp("https?://www\\.facebook\\.com/(?!plugins/).*") {

*{font-family:Calibri, Tahoma, Arial, sans-serif !important;}
  h4,h5 {font-size:13px !important;}
  div#navigator *{font-size:17px !important;}
  div#navigator .secondary_set *{font-size:15px !important}
  div#sidebar *{font-size:14px !important;}
  div#home_main *{font-size:14px !important;}
  div#content {font-size:13px !important;}
  div#home_sidebar *{font-size:13px !important;}
  div#dropmenu_container *{font-size:15px !important;}
  div#presence div#buddy_list_tab *{font-size:13px !important;}
  div#buddy_list *{font-size:13px !important;}
  div.friend_status span {font-size:12px !important;}
  div.minifeedwall .story_body {width: 660px !important;}
  div.minifeedwall .date_divider {font-size:13px !important;}
  h2#profile_name {font-size:23px !important;letter-spacing:-1px !important}
  td, td.label {font-size:13px !important;}
  #ssponsor, #sidebar_ads, .social_ad { display: none !important; }
  .profile_two_columns .right_column {width:720px !important;}
  #profile_top_bar {width:710px !important; padding: 0px 10px 0px 220px !important;}
  div.fcontent {width:650px !important;}
  .ffriend .fcontent .fname {font-size:14px !important;}
  #home_main .header_container {width:580px !important;}
  #home_main .feed_story_body {width:550px !important;}
  .home .status_story {width:500px !important;}
  div#profile_feedwall {font-size:11px !important;}
  .wall_post .quote, .gifts_received .quote, .photo_info .comment, .app_fbcomments_story .quote {width:420px !important;}
  .fbnew_preview_bar {display:none !important;}
  .topic .post .post_body {width:700px !important;}
  .UIStandardFrame_Content, .UIWashFrame_Content {width:940px !important;}
  .UIStandardFrame_Content h2, .UIMediaHeader_Title {font-size: 22px !important; letter-spacing: -1px !important}
  .UICompatibilityFrame_Container {padding:40px 0px 0px !important;}
  .UICompatibilityFrame_Frame {width: 927px !important;}
  .UICompatibilityFrame_Content {width: 925px !important;}
  .PillFilter_pillfilter {font-size:14px !important;}
  .dashboard_header .dh_titlebar h2 {font-size:23px !important;}
  .applications .right_column {width:690px !important;}
  .title_header h2 {font-size:20px !important;}
  .description {font-size:13px !important;}
  .title {font-size:12px !important;}
  .main_column {width:710px !important;}
  .product .info_wide {width:620px !important;}
  table.message_rows {width:942px !important;}
  .tokenizer, .tokenizer .tokenizer_input, .tokenizer .token, .typeahead_list, #facebook .tokenizer_input input, .tokenizer_input_shadow, input, .walltext, .commentable_item .comment_box .wallcontent .wallfrom {font-size: 13px !important;}
  .message_rows tr td.name_and_date {font-size: 13px !important; width: 120px !important;}
  .message_rows tr td.name_and_date span.date {font-size: 11px !important;}
  .message .body .text {width:600px !important; font-size: 14px !important;}
  .message .author_info {font-size: 13px !important;}
  .message .author_info .name {font-size: 15px !important;}
  .composer_fields .submits .sub_controls {font-size: 13px !important;}
  .composer_fields .field #subject_field, .composer_fields textarea, #compose_message div.tokenizer {width: 505px !important; font-size: 13px !important}
  .composer_fields textarea {height: 200px !important; overflow: auto !important}
  .thread_header h2 .between, .albums #results .album, .albums #results .album p {font-size: 13px !important; letter-spacing: normal !important}
  .links * {font-size: 13px !important;}
  .notifications, .updates_all {background-position: 728px 50% !important;}
  .notifications .main_column, .updates_all .main_column {width:728px !important;}
  .photo_comments_comments, #photocomment {width: 720px !important;}
  .media_header .media_gray_bg {width: 831px !important;}
  .summary_bar {padding-top: 22px !important;}
  .UIMediaHeader_TitleWash {padding-top: 48px !important;}
  .albums #results h2 {font-size: 18px !important; letter-spacing: -1px !important;}
  .fbpage_is_group #moveable_wide, #moveable_wide {width: 720px !important;}
  table.pop_dialog_table {width: 750px !important;}
  textarea { font-size: 14px !important;}
  .UIProfileBox_Title { font-size: 19px !important; letter-spacing: -1px !important;}
  #event.profile .left { width: 720px !important;}
  .UIProfileBox_ActionLink { font-size: 14px !important;}
  .minifeedwall .from_friend_story .story_content, .minifeedwall .story_content .story_info { max-width: 600px !important;}
  .minifeedwall .story .caption_meta * {font-size: 12px !important;}
  .minifeedwall .story_options .story_editor * { font-size: 10px !important;}
  .minifeedwall .story .story_time, .minifeedwall .story .status_source, .minifeedwall .story .caption_meta { font-size: 12px !important;}
  .minifeedwall .collapsed_comments .comment_bottom_add_link { margin-left: 35px !important; padding-top: 0 !important;}
  .pictures_container .name a { font-size: 12px !important;}
  .event_profile_title h3 { font-size: 19px !important; letter-spacing: -1px !important;}
  .sidebar_item_header h2 { font-size: 12px !important;}
  .pymk #pymk .friend_grid_col .fg_name { font-size: 13px !important;}
  .status_image { background-repeat: no-repeat !important; width: 40px !important; float: left !important;}
  .UITwoColumnLayout_Content { width: 730px !important;}
  .notes .wide_note .note_body { width: 600px !important;}
  .note_content { width: 600px !important;}
  .note_permalink #add_comment { padding: 10px 0px 20px 60px !important;}
  .albums #results .info { width: 680px !important;}
  .minifeedwall .story_comment_back_quote { font-size: 1.1em !important;}
  .profile .top_bar .mobile_status { font-size: 14px !important;}
  .profile .top_bar .status_source { font-size: 11px !important;}
  .profile .top_bar .mobile_status small {font-size: 10px !important;}
  .chat_conv_content p { font-size: 13px !important;}
  .chat_conv { height: 320px !important;}
  #fb_menubar, .invite_details, .aux_actions a { font-size: 15px !important;}
  #home_sponsor_nile { display: none !important; }
  .profile .box_tab .app_box_right_column .box .inside { width: 192px !important }
  .XbcFRAR { font-size: 1.3em !important; font-weight: bold !important; }
  .profile_sidebar_ads, .UIWashFrame_SidebarAds, .UIStandardFrame_SidebarAds { display: none !important; }
  .commentable_item .comment_box { font-size: 14px !important; }
  .GBThreadRow .line { font-size: 14px !important; }
  
  /* sponsored links right side */ #pagelet_ego_pane_w, /* --TEST-- Trending articles mixed into feed, keeping it personal */ .uiStreamStoryAttachmentOnly { display: none; }
  
}
