css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") { 

/* Main Changes */

#masthead .logo {display:none !important ;}
#baseDiv {padding-bottom: 0px !important;}
#masthead #lang-locale-picker-links-wrapper {display:none !important ;}
#masthead #top-margin-links-wrapper {margin: -10px 0 1px 0 !important; }
#masthead .nav-item {font-size: 13px !important; width: 80px !important; padding: 6px !important;}
div [class='bar'] {height: 28px !important;}
#search-form {padding: 2px 0 2px 250px !important; }

#masthead #upload-button { margin: -3px -1px 0 0 !important; }
div [id='watch-vid-title'] {margin: -15px 0 -3px 0 !important;}
#footer, #chrome-promo, #copyright {display: none !important;}
#masthead .search-term, #footer .search-term {height: 20px !important; padding: 0px 1px 2px 1px !important; margin-top: -3px !important;}
.search-wrapper .search-button-text, .search-wrapper .search-left-cap, .search-wrapper .search-right-cap {font-size: 11px !important; margin-top: -3px !important;}


/* Front Page Message areas */

#PRO-titlebar, #PRO-data, .feedmodule-promoted, .img-blue-bottom-cap, .feedmodule-upper-left-right-border-blue {display: none !important;} 


/* Front Page Sidebar*/

div [class='alignC xsmallText grayText padT3'] {display:none !important;}
.side-announcement-box, .img-yellow-message-top-cap.img-side-cap, .img-yellow-message-bottom-cap.img-side-cap {display:none !important;}
#homepage-side-content { margin-top: -14px !important;}
#homepage-whats-new-block, #watch-video-response {display:none !important;}

/* Video Page */

#movie_player {width: 960px !important; height: 580px !important; float: left !important;}
.watch-channel-subscribe {padding-top: 0px !important;}
#watch-other-vids {position: absolute !important; top: 649px !important; right: 5px !important;}
#watch-video-quality-setting {margin-top: -17px !important; }
#watch-channel-brand-cap, #watch-low-quality-link {display: none !important; }
#watch-channel-brand-div {padding-top: 14px !important;}
#watch-ratings-views {padding: 10px 5px 0 !important; }
#watch-high-quality-link {position: absolute !important; top: 665px !important; left: 530px !important;}


/* Subscribe Page */

#profile-side-content {padding-top: 697px !important }
.profileEmbedVideo {margin-left: -314px !important }


/* Firefox Scrollbar Width Fix */

html {overflow: -moz-scrollbars-vertical !important;}

/*Ads removal*/
#ad_creative_4, #ad_creative_2 {display:none !important;}

}
