css:
@-moz-document domain("forums.digitalspy.co.uk") {

header.main
{
color: #000 !important;
background-color: #dddddd !important;
}

div.main
{
color: #000 !important;
background-color: #dddddd !important;
}

#adposition1_container /* bar */
{
background-color: #334 !important;
height: 5px !important; /* optional height */
padding: 0 !important;
}

/* optional reposition breadcrumbs (may not work or may need adjustment) */
#breadcrumb_bar
{
position: absolute !important;
top: 75px !important;
display: table !important; /* shrink to fit content */
}

#breadcrumb_trail p
{
color: #000 !important;
}

#breadcrumb_trail .mb
{
color: #448 !important;
}

/* optional decrease vertical space above "DS Forums" */
table.forum_head > TBODY > TR:first-child > TD:first-child > TABLE:first-child > TBODY > TR:first-child
{
visibility: collapse !important;
}

table.forum_head > TBODY > TR:first-child > TD:first-child > TABLE:first-child > TBODY > TR:nth-child(2) .large.light.heading /* "DS Forums" */
{
color: #666 !important;
}

.boldlink.navytext { font-weight: bold !important; }

.nav_buttons /* nav bar */
{
color: #5583ed !important;
background-color: #eff0f7 !important;
display: table !important; /* shrink to fit content */
}

/****** optional hover effect for nav links ******/
.forum_nav
{padding: 4px !important;}
.forum_nav li a
{padding: 4px  !important;}
.forum_nav li a:hover /* nav links hover */
{
color: #eff0f7 !important;
background-color: #5583ed !important;
}


th.login_box
{
color: #fff !important;
background-color: #3b4787 !important;
}
td.login_box
{
color: #000 !important;
background-color: #e3e6f7 !important;
}

#wrapper > DIV > DIV > DIV > DIV > DIV > TABLE > TBODY > TR:last-child > TD > H1.large.light.heading /* thread title */
{
color: #2b3464 !important;
margin-top: 10px !important; /* optional added space above title */
}

.contentcontain, /* background of all inner content below thread title to near page bottom */
table.pagnavthread /* thread nav */
{
background-color: #eeeeee !important;
}

.pagnavthread + table.tborder td /* thread head */
{
color: #fff !important;
background-color: #3b4787 !important;
}

/* optional border/separator between posts */
div[id^=edit]
{
border-top:15px solid #3b4787 !important;
border-left: 1.5px solid #3b4787 !important; 
border-right: 1.5px solid #3b4787 !important; 
border-bottom: 1.5px solid #3b4787 !important; 
}

td.posthead /* post top */
{
color: #000000 !important;
font-weight: bold !important;
background-color: #ced3ec !important;
}
td.posthead a /* post # */
{
color: #000000 !important;
}
.alt2 /* post left */
{
background-color: #e3e6f7 !important;
}
.quotebox
{
font-style: italic !important;
color: #000000 !important;
background-color: #ced3ec !important;
border-style: solid !important;
border-width: 1pt !important;
border-color: #9baaf4 !important;
}
.alt1 /* post right */
{
background-color: #eff0f7 !important;
}
.quotebox a /* quote posted by */
{
color: #1515c4 !important;
font-weight: bold !important;
}

/* post text */

.alt1 {color: #000000 !important;}
.alt1 {padding: 8px 8px 2px 8px !important;}

.bigusername /* member name */
{
color: #1515c4 !important;
font-weight: bold !important;
font-size: 17px !important;
}

div[id^="postmenu"]~div.smallfont /* member rank */
{
color: #000 !important;
font-size: 13px !important;
}

div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-child(2) /* member date */
{
color: #000 !important;
}

div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-child(3) /* member location */
{
color: #000 !important;
}

div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-last-child(2) /* member posts */
{
color: #000 !important;
}

/***** replace images ******/

TABLE.pagnavthread > TBODY > TR > TD:first-child > A > IMG /* reply button */
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 144px !important;
padding-top: 29px !important;
background: url(http://imageshack.us/a/img17/5917/p9vr.png) no-repeat !important;
}


/* Quote button */
IMG[src="http://i.cdnds.net/forum/buttons/quote.png"]
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 78px !important;
padding-top: 28px !important;
background: url(http://imageshack.us/a/img51/355/g2fq.png) no-repeat !important;
}

/* New Post Envelope */
IMG[src="http://i.cdnds.net/forum/statusicon/thread_new.png"]
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 26px !important;
padding-top: 28px !important;
background: url(http://b-static.net/vbulletin/images/statusicon/thread_new.png) no-repeat !important;
}

/* No New Post Envelope */
IMG[src="http://i.cdnds.net/forum/statusicon/thread.png"]
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 26px !important;
padding-top: 28px !important;
background: url(http://b-static.net/vbulletin/images/statusicon/thread.png) no-repeat !important;
}

/* New Post In Thread You've Posted In Envelope */
IMG[src="http://i.cdnds.net/forum/statusicon/thread_dot_new.png"]
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 26px !important;
padding-top: 28px !important;
background: url(http://b-static.net/vbulletin/images/statusicon/thread_dot_new.png) no-repeat !important;
}

/* No New Post In Thread You've Posted In Envelope */
IMG[src="http://i.cdnds.net/forum/statusicon/thread_dot.png"]
{
height: 0px !important;
width: 0px !important;
/* these numbers match the new image's dimensions */
padding-left: 26px !important;
padding-top: 30px !important;
background: url(http://b-static.net/vbulletin/images/statusicon/thread_dot.png) no-repeat !important;
}

/* Locked Thread Envelope */
IMG[src="http://i.cdnds.net/forum/statusicon/thread_lock.png"]
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 26px !important;
padding-top: 28px !important;
background: url(http://b-static.net/vbulletin/images/statusicon/thread_hot_lock.png) no-repeat !important;
}

/* Edit button */ 
IMG[src="http://i.cdnds.net/forum/buttons/edit.png"]
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 78px !important;
padding-top: 28px !important;
background: url(http://imageshack.us/a/img577/8379/138s.png) no-repeat !important;
}

/* Quickreply button */
IMG[src="http://i.cdnds.net/forum/buttons/quickreply.png"]
{
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 44px !important;
padding-top: 28px !important;
background: url(http://imageshack.us/a/img11/6440/jqjq.png) no-repeat !important;
}

/* reply page form */
DIV:last-child > TABLE > TBODY > TR > TD.theadp, /* topic review head */
FORM > TABLE TD.theadp /* reply head, Additional Options head */
{
color: #fff !important;
background-color: #3b4787 !important;
}

}
