css:
@-moz-document domain("github.dev.xero.com") {

.repository-with-sidebar.with-full-navigation .repository-sidebar {
  float:left;
  width:170px;
}

#js-repo-pjax-container { 
  width: 96%;
}
            
.with-full-navigation #js-repo-pjax-container {
  width:80%;
  margin-left: 20px;
}
	

.page-pullrequest div.pull-head, .page-pullrequest .tabnav, .page-pullrequest #toc, .page-pullrequest #discussion_bucket {
  width: 100%;
  margin-left: auto;
  margin-right: auto;
}

.discussion-sidebar {
	width: 15%;
}


.container {
  width: 90%;
  margin-left: auto;
  margin-right: auto;
  padding-left: 2%;
  min-width: 1000px;
}
 
body.split-diff .new-pr-form {
    max-width: 100%;
}

.inline-comments .comment-holder,
.inline-review-comment .inline-comments .comment-holder,
.inline-review-comment .inline-comments .inline-comment-form,
.new-discussion-timeline .timeline-inline-comments .inline-comments .comment-holder,
tr.inline-comments .show-inline-comment-form, tr.inline-comments.show-inline-comment-form .inline-comment-form,
.inline-comments .inline-comment-form,
.inline-comments .inline-comment-form-container,
.timeline-new-comment {
    max-width:100%;  
}

.comment-form-head.tabnav {
    width:auto;
}

body.page-profile .profilecols .last {
  float: left;
  width: 740px;
  margin-left: 30px;
}

.repo-settings-content,
.settings-content {
  position: relative;
  float: left;
  width: 715px;
  margin-left: 30px;
}

.column { 
  float: left;
  
}

.root-pane {
  margin-right:30px;
}

.gist .column {
  width: 81%;
}

#edit .file .input textarea {
  width: 97%;
}

.pull-request-composer .discussion-topic {
  box-sizing: border-box;
  float: left;
  width: 80%;
  border-right: 1px solid #ddd;
}

table.files td.content .css-truncate {
	max-width: 100%;
}

.discussion-timeline {
	width:84%;
}

.news {
	width:65%;
}

.org-main {
	width:70%;  
}

.js-participation-graph {
	width:100%;
}
}
