css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html url("http://www.w3.org/1999/xhtml");

/*
 * Firefox Australis Windows 8 Appearance
 * by Blinky
 * Based on Stephen Horlander's mockup: https://people.mozilla.org/~shorlander/mockups-interactive/australis-interactive-mockups/windows8.html
 * and Project Chameleon: http://people.mozilla.org/~jgruen/chameleon/
 */

/*
 * Variables
 */
:root {
  --navbar-bordertopcolor: #9AA2AA;
  --toolbar-backgroundcolor: #F6F6F6;

  /* variables from browser.css*/
  --toolbarbutton-hover-bordercolor: rgba(24, 25, 26, 0.1);
  --toolbarbutton-hover-background: rgba(24, 25, 26, 0.08);
  --toolbarbutton-hover-boxshadow: none;
  --toolbarbutton-active-bordercolor: rgba(24, 25, 26, 0.2) transparent transparent;
  --toolbarbutton-active-background: rgba(24, 25, 26, 0.12);
  --toolbarbutton-active-box-shadow: 0 1px 0 0 rgba(24, 25, 26, 0.1) inset;
  
  --toolbar-button-disable-bordercolor: rgba(24, 25, 26, 0.1);
  --toolbar-button-disable-backgroundcolor: rgba(24, 25, 26, .03);
  --toolbar-button-disable-boxshadow: none;
  --toolbar-button-open-backgroundcolor: #177EE5;
  --panelui-button-separator-bordercolor: rgba(24, 25, 26, 0.15);
}

:root:-moz-lwtheme #navigator-toolbox > toolbar:not(#toolbar-menubar) {
  --toolbarbutton-hover-bordercolor: rgba(255, 255, 255, 0.4);
  --toolbarbutton-hover-background: rgba(255, 255, 255, 0.3);
  --toolbarbutton-hover-boxshadow: none;
  --toolbarbutton-active-bordercolor: rgba(255, 255, 255, 0.45) transparent transparent;
  --toolbarbutton-active-background: rgba(255, 250, 255, 0.4);
  --toolbarbutton-active-box-shadow: 0 1px 0 0 rgba(255, 255, 255, 0.35) inset;
  --toolbar-button-disable-bordercolor: rgba(255, 255, 255, 0.2);
  --toolbar-button-disable-backgroundcolor: rgba(255, 255, 255, .1);
  --panelui-button-separator-bordercolor: rgba(248, 248, 248, 0.35);
  --panelui-button-backgroundcolor: none;
}

:root[privatebrowsingmode] :-moz-any(findbar, #navigator-toolbox) {
  --navbar-bordertopcolor: transparent;
  --toolbar-backgroundcolor: #555555;
  --toolbarbutton-hover-bordercolor: rgba(24, 25, 26, .3);
  --toolbarbutton-hover-background: rgba(24, 25, 26, .18);
  --toolbarbutton-hover-boxshadow: none;
  --toolbarbutton-active-bordercolor: rgba(24, 25, 26, .4) transparent transparent;
  --toolbarbutton-active-background: rgba(24, 25, 26, .22);
  --toolbarbutton-active-box-shadow: 0 1px 0 0 rgba(24, 25, 26, .2) inset;
  --toolbar-button-disable-bordercolor: rgba(24, 25, 26, .2);
  --toolbar-button-disable-backgroundcolor: rgba(24, 25, 26, .1);
  --toolbar-button-open-backgroundcolor: hsl(280, 82%, 41%);
  --panelui-button-separator-bordercolor: rgba(24, 25, 26, 0.4);
  --panelui-button-backgroundcolor: hsla(280, 82%, 41%, .6);
}

  
/*
 * Tabs
 */
#TabsToolbar:not([tabsontop]) {
  background: none !important;
}

.tabbrowser-tab .tab-close-button {
  -moz-margin-start: 0 !important;
}

:-moz-any(.tab-background-start,.tab-background-end)::before {
  content: "" !important;
  display: -moz-box !important;
  min-height: 30px !important;
  width: 30px !important;
  pointer-events: auto !important;
}

.tab-background-start:-moz-locale-dir(ltr)::before,
.tab-background-end:-moz-locale-dir(rtl)::before {
  clip-path: url(chrome://browser/content/browser.xul#tab-curve-clip-path-start) !important;
}

.tab-background-end:-moz-locale-dir(ltr)::before,
.tab-background-start:-moz-locale-dir(rtl)::before {
  clip-path: url(chrome://browser/content/browser.xul#tab-curve-clip-path-end) !important;
}

.tab-background-start:-moz-locale-dir(ltr)::after,
.tab-background-end:-moz-locale-dir(rtl)::after {
  background-image: url(chrome://browser/skin/tabbrowser/tab-stroke-start@2x.png) !important;
}

.tab-background-end:-moz-locale-dir(ltr)::after,
.tab-background-start:-moz-locale-dir(rtl)::after {
  background-image: url(chrome://browser/skin/tabbrowser/tab-stroke-end@2x.png) !important;
}

.tab-background-middle[visuallyselected=true]:not(:-moz-lwtheme) {
  background-image: url(chrome://browser/skin/tabbrowser/tab-active-middle@2x.png),
                    linear-gradient(transparent, transparent 2px, var(--toolbar-backgroundcolor) 2px, var(--toolbar-backgroundcolor)),
                    none !important;
}

:root[privatebrowsingmode] .tab-background-middle[visuallyselected=true]:not(:-moz-lwtheme) {
  background: linear-gradient(transparent, transparent 2px, var(--toolbar-backgroundcolor) 2px, var(--toolbar-backgroundcolor)) padding-box !important;
}

:root[privatebrowsingmode] .tab-background-end::after,
:root[privatebrowsingmode] .tab-background-start::after {
  background-image: url(chrome://browser/skin/tabbrowser/tab-stroke-end@2x.png) !important;
  display: none !important;
}

:root[privatebrowsingmode] #nav-bar {
  margin-top: 0 !important;
  border-top: 0 !important;
}
  
:-moz-any(.tab-background-start, .tab-background-end)[visuallyselected=true]:not(:-moz-lwtheme)::before {
  background: var(--toolbar-backgroundcolor) !important;
}

#tabbrowser-tabs:not([overflow="true"]) .tabbrowser-tab[pinned]:not([selected=true]):not([tabsontop]):hover,
.tabbrowser-tab:not([pinned]):not([selected=true]):not([tabsontop]):hover {
  position: relative !important;
  z-index: 1 !important;
}

.tabbrowser-tab[selected=true]:-moz-lwtheme :-moz-any(.tab-background-end,.tab-background-start)::before {
  min-height: 31.5px !important;
  margin-bottom: -0.5px !important;
}

/*
 * Remove border radius
 */
#main-menubar,
#downloads-indicator-progress > spacer,
#downloads-indicator-progress,
#nav-bar, .findbar-textbox, .autocomplete-richlistitem, #notification-popup-box,
#searchFilter, #searchbar .searchbar-textbox, #urlbar,
#widget-overflow-list > toolbarbutton,
.customizationmode-button,
tabmodalprompt .mainContainer,
menu, menuitem {
  border-radius: 0 !important;
}

/*
 * Toolbars
 */
#main-window[customizing="true"] #navigator-toolbox > toolbar:not(#TabsToolbar):not(#toolbar-menubar) {
  visibility: visible !important;
  border-left: 1px solid rgba(10, 31, 51, 0.35) !important;
  border-right: 1px solid rgba(10, 31, 51, 0.35) !important;
}

#placesToolbar, #placesList, #detailsDeck,
#navigator-toolbox > toolbar:not(#TabsToolbar):not(#toolbar-menubar):not(:-moz-lwtheme),
notification[value="translation"],
#browser-bottombox:not(:-moz-lwtheme),
.browserContainer > findbar {
  background: var(--toolbar-backgroundcolor) !important;
}

#nav-bar:not(:-moz-lwtheme) {
  background-image: linear-gradient(#FBFBFB 0%, #F1F1F1 1px) !important;
  box-shadow: none !important;
  border-top-color: var(--navbar-bordertopcolor) !important;
}

#PersonalToolbar {
  padding: 2px 4px 5.5px !important;
  margin-top: -3px !important;
  position: relative !important;
  z-index: 2 !important;
}

#main-window[customizing=true] #PersonalToolbar {
  margin-top: -1px !important;
}

#main-window:not([customizing=true]):not([devtoolstheme]) #PersonalToolbar:not(:-moz-lwtheme):not([tabsontop])::after {
  content: "" !important;
  display: -moz-box !important;
  position: fixed !important;
  right: 1px !important;
  float: right !important;
  margin-top: -8px !important;
  width: 50px !important;
  height: 8px !important;
  background: linear-gradient(rgba(241,241,241,0), rgba(241,241,241,1) 80%) !important;
  pointer-events: none !important;
}

#main-window:not([customizing=true]) #PersonalToolbar:-moz-locale-dir(rtl)::after {
  left: 0 !important;
}

#main-window[privatebrowsingmode]:not([customizing=true]) #PersonalToolbar:not([tabsontop])::after {
  background: linear-gradient(rgba(85,85,85,0), rgba(85,85,85,1) 80%) !important;
}

#personal-bookmarks {
  min-height: 0 !important;
}

.bookmark-item > .toolbarbutton-menu-dropmarker {
  display: -moz-box !important;
  -moz-margin-start: 4px !important;
  -moz-margin-end: 4px !important;
  list-style-image: url("chrome://browser/skin/toolbarbutton-dropdown-arrow.png") !important;
}

#main-window:-moz-any([privatebrowsingmode],:-moz-lwtheme-brighttext) .bookmark-item > .toolbarbutton-menu-dropmarker {
  list-style-image: url("chrome://browser/skin/toolbarbutton-dropdown-arrow-inverted.png") !important;
}

#PersonalToolbar toolbarbutton {
  -moz-appearance: none !important;
  height: 22px !important;
}

#PersonalToolbar toolbaritem > separator {
  margin: 0 !important;
}

#main-window #PersonalToolbar toolbarbutton:not([type=menu-button]) {
  margin: 0 !important;
  padding: 0px 6px !important;
}

#placesToolbar {
  padding: 4px !important;
}

#placeContent {
  background: #FDFDFD !important;
}

#placesToolbar :-moz-any(toolbarbutton,menu,menuitem) {
  -moz-appearance: none !important;
  padding: 1px 4px !important;
}

#placesToolbar :-moz-any(toolbarbutton,menu,menuitem,textbox) {
  height: 23px !important;
}

#nav-bar-customization-target > .toolbarbutton-1 .toolbarbutton-icon {
  max-height: 24px !important;
}

#navigator-toolbox:not(:-moz-lwtheme)::after {
  background: #C1C1C1 !important;
  height: 0 !important;
  border-bottom: 1px solid #C1C1C1 !important;
}

/*
 * Back-Forward button
 */
#nav-bar-customization-target :-moz-any(#back-button,#forward-button) > .toolbarbutton-icon {
  border: 0px solid rgba(24, 25, 26, 0.27) !important;
  background: rgba(250,251,252,.5) !important;
  box-shadow: 0 0 0 1px rgba(24, 25, 26, 0.35) !important;
  transition-property: background-color, box-shadow !important;
}

#nav-bar-customization-target :-moz-any(#back-button,#forward-button):hover:not([disabled=true]) > .toolbarbutton-icon {
  background: rgba(24, 25, 26, 0.08) !important;
}

#nav-bar-customization-target :-moz-any(#back-button,#forward-button):-moz-any(:hover:active,[open=true]):not([disabled=true]) > .toolbarbutton-icon {
  background: rgba(24, 25, 26, 0.1) !important;
  box-shadow: 0 1px 0 0 hsla(210, 4%, 10%, 0.1) inset, 0 0 0 1px rgba(24, 25, 26, 0.35) !important;
}

#main-window:-moz-any([privatebrowsingmode],:-moz-lwtheme-brighttext) #nav-bar-customization-target :-moz-any(#back-button,#forward-button):not([disabled=true]) > .toolbarbutton-icon {
  box-shadow: 0 0 0 1px rgba(240, 250, 255, .8) !important;
  border-color: rgba(0, 0, 0, 0.32) !important;
}

#main-window:-moz-any([privatebrowsingmode],:-moz-lwtheme-brighttext) #nav-bar-customization-target :-moz-any(#back-button,#forward-button):hover:not([disabled=true]) > .toolbarbutton-icon {
  background: rgba(240, 250, 255, .7) !important;
}

#main-window:-moz-any([privatebrowsingmode],:-moz-lwtheme-brighttext) #nav-bar-customization-target :-moz-any(#back-button,#forward-button):-moz-any(:hover:active,[open=true]):not([disabled=true]) > .toolbarbutton-icon {
  background: rgba(240, 250, 255, .8) !important;
  box-shadow: 0 1px 0 0 rgba(240, 250, 255, .8) inset, 0 0 0 1px rgba(240, 250, 255, .8) !important;
}

#main-window:not([tabsontop]) #nav-bar #forward-button > .toolbarbutton-icon {
  box-shadow: none !important;
  border-width: 1px !important;
  -moz-border-start: 0 !important;
}

#main-window:-moz-any([privatebrowsingmode],:-moz-lwtheme-brighttext) #nav-bar-customization-target #forward-button > .toolbarbutton-icon {
  background-clip: padding-box !important;
}

/*
 * Global buttons
 */
.toolbarbutton-menubutton-button {
  -moz-margin-end: -0.5px !important;
}

.toolbarbutton-menubutton-dropmarker {
  -moz-margin-start: -0.5px !important;
}

.toolbarbutton-menubutton-dropmarker:before {
  box-shadow: none !important;
}

#main-window:-moz-any([privatebrowsingmode],:-moz-lwtheme) .toolbarbutton-menubutton-dropmarker:before {
  background: rgba(240, 250, 255, 0.3) !important;
}

#nav-bar #bookmarks-menu-button > .toolbarbutton-menubutton-button image {
  padding: 2px 5px !important;
}

#main-window menugroup > menuitem {
  height: auto !important;
}

menugroup#context-navigation {
  margin: 0 !important;
  margin-top: -4px !important;
  padding: 4px 0 !important;
  background: #FBFBFB !important;
}

#main-window menugroup#context-navigation + menuseparator {
  margin-top: 0 !important;
}

menu,
menuitem:not(.panel-subview-footer) {
  -moz-appearance: none !important;
  min-height: 23px !important;
  height: 23px !important;
  padding: 0 !important;
  margin: 0 4px !important;
}

.autocomplete-treebody::-moz-tree-row {
  min-height: 20px !important;
}

.autocomplete-treebody::-moz-tree-cell-text {
  color: #333 !important;
}

.autocomplete-treebody::-moz-tree-cell(suggesthint) {
  margin-top: -1px !important;
  padding-top: 1px !important;
  border-top: 1px solid rgba(24, 25, 26, 0.15) !important;
}

menu .menu-right {
  -moz-appearance: none !important;
  -moz-margin-start: 4px !important;
  -moz-margin-end: 6px !important;
  height: 10px !important;
  width: 10px !important;
  background: url("chrome://browser/skin/customizableui/menu-arrow.svg#arrow") center no-repeat !important;
}

.menu-iconic-left {
  -moz-appearance: none !important;
  min-width: 15px !important;
  width: 15px !important;
  min-height: 14px !important;
  padding: 0 !important;
  margin:0 6px !important;
  
}
  
.menu-text {
  -moz-appearance: none !important;
  -moz-padding-start: 27px !important;
}

menuitem[checked="true"]:not([name]) .menu-iconic-left,
menuitem[type="checkbox"][checked="true"] .menu-iconic-left {
  background: url("chrome://global/skin/in-content/check.svg#check-native") no-repeat center center / 18px !important;
  visibility: visible !important;
}

menuitem[type="radio"][checked="true"] .menu-iconic-left {
  background: url("chrome://global/skin/in-content/radio.svg#radio-native") no-repeat center center !important;
}

menupopup menuseparator {
  -moz-appearance: none !important;
  min-height: 0 !important;
  padding: 0 !important;
  border-top: 1px solid hsla(210,4%,10%,.15) !important;
  margin: 4px 0 !important;
}

#nav-bar > toolbarbutton:not(.toolbarbutton-1) > .toolbarbutton-icon,
#nav-bar-customization-target > toolbarbutton:not(.toolbarbutton-1) > .toolbarbutton-icon {
  margin: 0 5px !important;
}
  
.findbar-container toolbarbutton:not([class^="findbar-find"]) {
  -moz-appearance: none !important;
  border: 0 !important;
  padding: 0 !important;
}
  
.findbar-container toolbarbutton:not([class^="findbar-find"]) > .toolbarbutton-text {
  padding: 2px 6px !important;
}

/**Variables test*/
:-moz-any(menu,menuitem):not(.panel-subview-footer),
#main-window :-moz-any(menu,menuitem):not(.panel-subview-footer),
:-moz-any(#navigator-toolbox, #browser-bottombox, #placesToolbox) toolbar:not(#TabsToolbar):not(#nav-bar):not(#developer-toolbar):not(#PersonalToolbar) toolbarbutton,
treechildren::-moz-tree-row,
.findbar-container toolbarbutton:not([class^="findbar-find"]) > .toolbarbutton-text,
#nav-bar:not([currentset*="unified-back-forward-button,urlbar-container"]) #unified-back-forward-button .toolbarbutton-1 > .toolbarbutton-icon,
#nav-bar .toolbarbutton-1:not(#back-button):not(#forward-button) > :-moz-any(.toolbarbutton-icon, .toolbarbutton-text, .toolbarbutton-badge-container),
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button > .toolbarbutton-icon, 
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
  background:  transparent !important;
  border: 1px solid transparent !important;
  border-radius: 0 !important;
  box-shadow: none !important;
  /*transition-duration: 250ms !important;*/
  /*transition-property: background-color, border-color !important;*/
}

:-moz-any(#downloadsListBox, #downloadsRichListBox) richlistitem[exists][state="1"] > vbox:hover,
:-moz-any(#downloadsListBox, #downloadsRichListBox) .downloadButton:hover,
#downloadsRichListBox > richlistitem:-moz-any([selected],:hover),
#downloadsPanel:not([keyfocus]) > #downloadsListBox > richlistitem:hover,
:-moz-any(menu,menuitem)[disabled="true"]:hover,
#main-window :-moz-any(menu,menuitem)[disabled="true"]:hover,
treechildren::-moz-tree-row(selected,current),
#nav-bar .toolbarbutton-1:-moz-any(:hover, [open]) > .toolbarbutton-menubutton-button > .toolbarbutton-icon,
#main-window:not(:-moz-lwtheme) #nav-bar #bookmarks-menu-button:not(:hover) > .toolbarbutton-menubutton-button[open] + .toolbarbutton-menubutton-dropmarker > image,
#nav-bar .toolbarbutton-1:hover > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
  background: var(--toolbar-button-disable-backgroundcolor) !important;
  border-color: var(--toolbar-button-disable-bordercolor) !important;
  box-shadow: var(--toolbar-button-disable-boxshadow) !important;
}

:-moz-any(menu,menuitem):-moz-any(:active:hover,:hover,[open],[selected="true"]):not([disabled="true"]):not(.panel-subview-footer),
#main-window :-moz-any(menu,menuitem):-moz-any(:active:hover,:hover,[open],[selected="true"]):not([disabled="true"]):not(.panel-subview-footer),
.findbar-container toolbarbutton:hover:not([class^="findbar-find"]):not([disabled]) > .toolbarbutton-text,
:-moz-any(#navigator-toolbox, #browser-bottombox, #placesToolbox) toolbar:not(#TabsToolbar):not(#nav-bar):not(#developer-toolbar):not(#PersonalToolbar) toolbarbutton:hover:not([disabled]),
treechildren.autocomplete-treebody::-moz-tree-row(hover,current),
treechildren.autocomplete-treebody::-moz-tree-row(selected,current),
treechildren:not(.autocomplete-treebody)::-moz-tree-row(hover,current),
treechildren:not(.autocomplete-treebody)::-moz-tree-row(hover),
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button:hover:not([disabled]):not([open]):not(:active) > .toolbarbutton-icon, 
#nav-bar .toolbarbutton-1:hover:not([buttonover]):not([open]):not(:active) > .toolbarbutton-menubutton-dropmarker:not([disabled=true]) > .dropmarker-icon,
#nav-bar .toolbarbutton-1:hover:not([disabled]):not([type="menu-button"]):not([checked]):not([open]):not(:active):not(#back-button):not(#forward-button) > :-moz-any(.toolbarbutton-icon, .toolbarbutton-text, .toolbarbutton-badge-container) {
  background: var(--toolbarbutton-hover-background) !important;
  border-color: var(--toolbarbutton-hover-bordercolor) !important;
  box-shadow: var(--toolbarbutton-hover-boxshadow) !important;
}

:-moz-any(#downloadsListBox, #downloadsRichListBox) richlistitem[exists] > vbox:hover:active,
:-moz-any(#downloadsListBox, #downloadsRichListBox) .downloadButton:hover:active,
window menuitem:active:hover:not([disabled="true"]):not([open="true"]):not(.panel-subview-footer),
.findbar-container toolbarbutton:-moz-any(:hover:active,[checked]):not([class^="findbar-find"]):not([disabled]) > .toolbarbutton-text,
treechildren::-moz-tree-row(selected,focus),
treechildren::-moz-tree-row(current,focus),
:-moz-any(#navigator-toolbox, #browser-bottombox, #placesToolbox) toolbar:not(#TabsToolbar):not(#nav-bar):not(#developer-toolbar):not(#PersonalToolbar) toolbarbutton:-moz-any(:hover:active,[open]):not([disabled]),
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button:hover:active:not([disabled]) > .toolbarbutton-icon,
#nav-bar .toolbarbutton-1:-moz-any([checked], *:hover:active):not([disabled=true]):not(#back-button):not(#forward-button) > :-moz-any(.toolbarbutton-icon, .toolbarbutton-text, .toolbarbutton-badge-container) {
  background: var(--toolbarbutton-active-background) !important;
  border-color: var(--toolbarbutton-active-bordercolor) !important;
  box-shadow: var(--toolbarbutton-active-box-shadow) !important;
  /*transition-duration: 10ms !important;*/
}

:-moz-any(menu,menuitem):not([disabled="true"]),
#main-window :-moz-any(menu,menuitem):not([disabled="true"]),
treechildren:not(.autocomplete-treebody)::-moz-tree-cell-text(selected) {
  color: unset !important;
}
  
#main-window menulist > menupopup menuitem:not(.panel-subview-footer) {
  margin: 0 !important;
  padding: 0 4px !important;/*
  -moz-appearance: menuitem !important;*/
  
}
  
#main-window menulist > menupopup > :-moz-any(menu,menuitem):-moz-any(:active:hover,:hover,[open],[selected="true"]):not([disabled="true"]):not(.panel-subview-footer) {
  background: none !important;
  border-color: transparent !important;
}

#main-window menulist > menupopup > :-moz-any(menu,menuitem)[_moz-menuactive="true"]:not([disabled="true"]):not(.panel-subview-footer) {
  background-color: highlight !important;
  color: highlighttext !important;
}

#main-window[privatebrowsingmode] :-moz-any(#nav-bar, #PersonalToolbar) :-moz-any(#home-button, #print-button, #downloads-button, #bookmarks-menu-button, #new-tab-button, #new-window-button, #fullscreen-button, #sync-button, #feed-button, #tabview-button, #social-share-button, #open-file-button, #find-button, #developer-button, #preferences-button, #privatebrowsing-button, #save-page-button, #switch-to-metro-button, #add-ons-button, #history-panelmenu, #nav-bar-overflow-button, #PanelUI-menu-button, #characterencoding-button, #email-link-button, #sidebar-button, #zoom-out-button, #zoom-reset-button, #zoom-in-button, #cut-button, #copy-button, #paste-button, #e10s-button, #panic-button)[cui-areatype="toolbar"],
#main-window[privatebrowsingmode] #bookmarks-menu-button > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon,
#main-window[privatebrowsingmode] :-moz-any(#back-button, #forward-button, #PanelUI-menu-button),
#nav-bar > #bookmarks-menu-button[open],
#nav-bar :-moz-any(#downloads-button, #sync-button, #feed-button, #tabview-button, #webrtc-status-button, #social-share-button, #developer-button, #history-panelmenu, #nav-bar-overflow-button, #PanelUI-menu-button, #characterencoding-button, #email-link-button, #sidebar-button, #loop-button)[open], 
#bookmarks-menu-button > :-moz-any(.toolbarbutton-menubutton-dropmarker,.toolbarbutton-menubutton-button)[open] > image {
  list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important;
}

#loop-button[cui-areatype="toolbar"][open]  > .toolbarbutton-badge-container {
  list-style-image: url("chrome://browser/skin/loop/toolbar-inverted.png") !important;
}

#main-window[privatebrowsingmode] :-moz-any(#nav-bar, #PersonalToolbar) .toolbarbutton-1 > .toolbarbutton-menu-dropmarker,
#main-window[privatebrowsingmode] :-moz-any(#nav-bar, #PersonalToolbar) .toolbarbutton-1:not(#bookmarks-menu-button) > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon,
#nav-bar .toolbarbutton-1[open] > .toolbarbutton-menu-dropmarker,
#nav-bar .toolbarbutton-1[open]:not(#bookmarks-menu-button) > .toolbarbutton-menubutton-dropmarker:not([disabled=true]) > .dropmarker-icon {
  list-style-image: url("chrome://browser/skin/toolbarbutton-dropdown-arrow-inverted.png") !important;
}

#nav-bar .toolbarbutton-1[open]:not([disabled]):not(#back-button):not(#forward-button) > :-moz-any(.toolbarbutton-badge-stack, .toolbarbutton-badge-container, .toolbarbutton-icon),
