  opacity: 1 !important;
}

#main-window findbar toolbarbutton:-moz-any(*) {
  background: none padding-box !important;
  border: 0 !important;
  padding: 0 9px !important;
  height: 31px !important;
}

#main-window findbar toolbarbutton:hover:not([disabled]) {
  background-color: rgba(24, 25, 26, 0.05) !important;
}

#main-window findbar toolbarbutton:-moz-any(:active,[checked=true]):not([disabled]) {
  background-color: rgba(24, 25, 26, 0.1) !important;
