}

#main-window findbar toolbarbutton.findbar-closebutton {
  padding: 5px 8px !important;
  margin: 0 !important;
  border-left: 1px solid rgba(24, 25, 26, 0.25) !important;
  -moz-image-region: rect(0px, 16px, 16px, 0px) !important;
}

#main-window findbar .findbar-find-next {
  -moz-margin-start: 1px !important;
}

#main-window findbar .findbar-container > .tabbable {
  margin: 0 !important;
  -moz-box-ordinal-group: 233 !important;
  border-left: 1px solid rgba(24, 25, 26, 0.25) !important;
}

#main-window findbar .found-matches {
  border-right: 1px solid rgba(24, 25, 26, 0.25) !important;
