}

/* optional border/separator between posts */
div[id^=edit]
{
margin-top: 10px !important;
}

#collapseobj_threadreview > TABLE > TBODY > TR:nth-child(odd) td /* reply page post */
{
border-top: 10px solid #000 !important;
}

div[id^=edit] img
{
box-shadow: 0px 0px 1px 1px #fff !important;
background-color: #fff !important;
}
div[id^=edit] td.posthead img
{
box-shadow:none !important;
background-color:transparent !important;
}

td.posthead:first-child /* post top */
{
color: #999 !important;
background-color: #ced3ec !important;
background-image: linear-gradient(to bottom, #444, #333 40%, #222)!important;
border-top-left-radius: 5px !important;
}
td.posthead:last-child /* post top */
{
color: #999 !important;
background-color: #ced3ec !important;
background-image: linear-gradient(to bottom, #444, #333 40%, #222)!important;
border-top-right-radius: 5px !important;
}
td.posthead a /* post # */
{
color: #999 !important;
}
.alt2 /* post left */
{
background-color: rgba(255,255,255,0.05) !important;
}
tr:last-child .alt2 /* post left */
{
border-bottom-left-radius: 5px !important;
}
.alt1:not([id]) /* post right */
{
border-right: 1px solid #222 !important;
border-bottom: 1px solid #222 !important;
border-bottom-right-radius: 5px !important;
}
.alt1 /* post right */
{
border-right: 1px solid #222 !important;
}
.quotebox
{
color: #999 !important;
background-color: rgba(255,255,255,0.1) !important;
border-style: solid !important;
border-width: 1pt !important;
border-color: #333 !important;
}
.quotebox a /* quote posted by */
{
color: #ccc !important;
}

/* post text */

.alt1 {
color: #999 !important;
padding: 20px !important;
}

body:not(#foo) .alt1 a {color: #99c !important;}
body:not(#foo) .bigusername /* member name */
{
color: #aac !important;
font-size: 17px !important;
}

div[id^="postmenu"]~div.smallfont /* member rank */
{
color: #99b !important;
font-size: 13px !important;
}

div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-child(2) /* member date */
{
color: #888 !important;
}

div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-child(3) /* member location */
{
color: #aaa !important;
}

div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-last-child(2) /* member posts */
{
color: #888 !important;
}

