css:
@-moz-document domain("deviantart.com"), domain("sta.sh") {
.user-symbol {display: none !important;}
.talk-post-reply .simple-symbol {display: inline !important;}
.ccwriter-preview .simple-symbol {display: none !important;}

.username {color: #2C3635 !important;}
#overhead .username {color: #c0d0ca !important;}
#depths .username {color: #d8e4d8 !important;}
.collections .username {color: #CCC !important;}
.journal .username {color: inherit !important;}

.external:after {display: none;}
.text a:not(.external) {font-weight: inherit !important;}

.username:before {color: #304030 !important;}
.catbar .username:before {color: #405147 !important;}
#overhead .username:before {color: #9eb1a3 !important;}
.mcb-line .username:before {color: #89A08E !important;}
#depths .username:before {color: #90a193 !important;}
.journal .username:before {color: inherit !important;}

.banned.username {text-decoration: none !important;}

.banned.username:before {content:"!" !important;}
.group.username:before {content:"#" !important;}
.regular.username:before {content:"~" !important;}
.premium.username:before {content:"*" !important;}
.hell.username:before {content:"*" !important;}
.beta.username:before {content:"=" !important;}
.hell-beta.username:before {content:"=" !important;}
.senior.username:before {content:"`" !important;}
.volunteer.username:before {content:"^" !important;}
.admin.username:before {content:"$" !important;}

.dAmnChatMember .banned:before {content:"!" !important;}
.dAmnChatMember .regular:before {content:"~" !important;}
.dAmnChatMember .premium:before {content:"*" !important;}
.dAmnChatMember .hell:before {content:"*" !important;}
.dAmnChatMember .beta:before {content:"=" !important;}
.dAmnChatMember .hell-beta:before {content:"=" !important;}
.dAmnChatMember .senior:before {content:"`" !important;}
.dAmnChatMember .volunteer:before {content:"^" !important;}
.dAmnChatMember .admin:before {content:"$" !important;}
}
