css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

html, body {
    color: white !important;
    background-color: rgb(8, 8, 8) !important;
}

a, a:visited, a:active {
    color: #00dbff !important;
    text-decoration: none !important;
}

a:hover {
	text-shadow: 0 0 0.2em #00dbff;
}

.ad, .chitika-ad {
    display: none !important;
}

#Head, .DiscussionsTabs, .HeadingTabs,
.ConversationsTabs, .TabLink,
.BigButton, .Category, .BoxCategories {
    background: -moz-linear-gradient(center top , rgb(32, 32, 32), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
    border-color: black !important;
    text-shadow: none !important;
}

.New, .MessageForm, .Group, .About {
    background-color: rgb(16, 16, 16) !important;
    border-color: black !important;
}

.PanelCategories, .Depth1, .Active,
.PanelCategories li, .PanelInfo,
.Group li {
    background-color: transparent !important;
    border: 1px solid rgba(0, 0, 0, .5) !important;
}

.MenuTitle, .Border h1, .Border h2,
.Warning, .Info {
    background: -moz-linear-gradient(center top , rgb(32, 32, 32), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
    color: rgb(0, 153, 255) !important;
    border-color: black !important;
}

.Comment, #Form_User, #Form_Form,
#Form_Form td, .Item {
    background-color: rgb(16, 16, 16) !important;
    color: rgb(0, 153, 255) !important;
    border-color: black !important;
}

.ProfileTabs {
    background-color: rgb(16, 16, 16) !important;
    color: rgb(0, 153, 255) !important;
    border: 1px solid black !important;
}

.Profile h1, #Form_User label, #Form_Form h3 {
    color: rgb(0, 153, 255) !important;
}

#main-header {
    background: -moz-linear-gradient(center top , rgb(32, 32, 32), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
    border: 1px solid rgb(0, 0, 0) !important;
    border-radius: 16px !important;
}

#left-sidebar {
    margin-top: 8px !important;
}

#left-sidebar nav {
    background: -moz-linear-gradient(center top , rgb(32, 32, 32), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
    border: 1px solid rgb(0, 0, 0) !important;
    border-radius: 12px !important;
}

#related {
    background: -moz-linear-gradient(center top , rgb(32, 32, 32), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
    border: 1px solid rgb(0, 0, 0) !important;
    border-radius: 12px !important;
}

.more {
    border-top-color: #000 !important;
}

#content-wrapper {
    background-color: rgb(16, 16, 16) !important;
    border: 1px solid #000 !important;
    border-radius: 16px !important;
}

.style-warning {
    background: -moz-linear-gradient(center top , rgb(24, 24, 24), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
    border: 1px solid #000 !important;
    color: red !important;
}
.btn-google, .btn-yahoo, .btn-openid {
    background: -moz-linear-gradient(center top , rgb(24, 24, 24), rgb(8, 8, 8)) repeat scroll 0% 0% transparent !important;
    border-color: black !important;
}
.btn-google:hover, .btn-yahoo:hover, .btn-openid:hover {
    background: -moz-linear-gradient(center top , rgb(32, 32, 32), rgb(16, 16, 16)) repeat scroll 0% 0% transparent !important;
}
.btn-password {
    background-color: rgb(16, 16, 16) !important;
    border-color: black !important;
}
.btn-password:hover, pre#view-code {
    background-color: rgb(24, 24, 24) !important;
}

#main-article h2:before {
    content: "Estilos (";
}
#main-article h2:after {
    content: ")";
}

#main-article dt:before {
    content: "Contactar (";
}
#main-article dt:after {
    content: ")";
}

a[href="/styles/new"]:before {
    content: "Crear un Estilo Nuevo (";
}
a[href="/users/250030/edit"]:before {
    content: "Cambiar Detalles (";
}
a[href="/users/edit_login_methods/250030"]:before {
    content: "Cambiar los métodos de inicio de sesión (";
}
a[href="http://forum.userstyles.org/?Discussion/StyleAuthorID=250030"]:before {
    content: "Discuciones sobre mis estilos (";
}
a[href="/logout"]:before {
    content: "Cerrar Sesión (";
}
a[href="/styles/new"]:after,
a[href="/users/250030/edit"]:after,
a[href="/users/edit_login_methods/250030"]:after,
a[href="http://forum.userstyles.org/?Discussion/StyleAuthorID=250030"]:after,
a[href="/logout"]:after,
a[href="http://forum.userstyles.org/messages/add/OniBrian"]:after,
a[href="/contact"]:after,
a[href="/categories"]:after,
a[href="/help"]:after,
a[href="http://forum.userstyles.org"]:after,
a[href="/help/stylish"]:after,
a[href="https://github.com/JasonBarnabe/stylish/wiki"]:after,
a[href="/help/userstylesorg"]:after,
a[href="/help/coding"]:after,
a[href="/help/other"]:after {
    content: ")";
}
a[href="http://forum.userstyles.org/messages/add/OniBrian"]:before {
    content: "Enviar un Mensaje Privado (";
}
a[href="/contact"]:before {
    content: "Pónerse en contacto con el administrador. (";
}
a[href="/categories"]:before {
    content: "Navegar (";
}
a[href="/help"]:before {
    content: "Ayuda (";
}
a[href="http://forum.userstyles.org"]:before {
    content: "Foro (";
}
a[href="/help/stylish"]:before {
    content: "Instalación y uso de Stylish (";
}
a[href="https://github.com/JasonBarnabe/stylish/wiki"]:before {
    content: "Escribiendo estilos de usuario (";
}
a[href="/help/userstylesorg"]:before {
    content: "Usando userstyles.org (";
}
a[href="/help/coding"]:before {
    content: "Publicando estilos en userstyles.org (";
}
a[href="/help/other"]:before {
    content: "Otra Información (";
}
}
