/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
body, div, h1, h2, h3, h4, h5, h6, h7, h8, h9, table, tbody, thead, tr, td, th, ul, ol, li, p, i, dd, dt, strong, small, code, iframe, embed, section, blockquote, legend, label, form, pre, dl, sup, img {color: #999 !important; text-shadow: 0px 0px 1px #000 !important; box-shadow: none !important; background-color: rgb(0,0,0)/*transparent*/ !important; border-color: #444 !important; border-top-color: #444 !important; border-bottom-color: #444 !important; border-left-color: #444 !important; border-right-color: #444 !important}
a:not([style*="color"]), span:not([style*="color"]), span[style*="color: #000000"], hr {color: #999 !important; text-shadow: 0px 0px 1px #000 !important; background-color: transparent !important; border-color: #444 !important; border-top-color: #444 !important; border-bottom-color: #444 !important; border-left-color: #444 !important; border-right-color: #444 !important}
*[style*="color"]:not([style="abcxyz"]), *[style*="color"]:not([style="abcxyz"]) * {text-shadow: 0 0 3px #000 !important}

table {background-color: rgba(40,30,30,1) !important; border-radius: 6px !important}
div[style] > form {background-color: rgba(10,20,20,1) !important; border-radius: 6px !important}

a:not([style*="abcxyz"]), a:not([style*="abcxyz"]) * {color: rgb(0,175,175)/*#409B9B*/ !important; text-decoration: none !important; background-color: rgb(0,0,0) !important;}
a:hover, a:hover *, a:visited:hover, a:visited:hover *, span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .link:hover, .link:hover *, .sn-link:hover, .gac_b:hover *, .google-ac-b *, .yt-suggest-selected *, .yt-uix-button-menu-item:hover, .acl div.sug .sel *, .acl div.sug .sel, .ac_active, .ac_active *, .yui-ac-bd li:hover {
