a:visited, a:visited * {color: rgb(150,100,150)/*#607069*/ !important;}
a.highlight, a.highlight *, a.active, a.active *, .selected, .selected *, .gtab-selected, .gtabi-mc, [href="#"] {color: #DDD !important; font-weight: bold !important}

h1, h2, h3, h4, h5, h6, h1 *, h2 *, h3 *, h1 span:not([style*="color"]), strong {color: #DDD !important}
[class*="error"], [class*="alert"], code, span[onclick], div[onclick] {color: #900 !important}

