/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input, select, button, a[href="javascript:;"]:not([t]):not([onclick]), a.button, a.submit, a.BigButton, a.TabLink, .install[onclick] {
  -moz-appearance: none !important;
  -moz-transition-duration: 0.3s !important;
