  -webkit-appearance: none !important;
  -webkit-transition-duration: 0.3s !important;
  -webkit-transition-property: border-color !important;
  background: #060606 !important;
  color: #BBB !important;
  text-shadow: 0 1px #000 !important;
  border: 2px solid #333 !important;
  border-radius: 4px !important;
  box-shadow: 0 0 2px rgba(0,0,0,1) !important}
input *, select *, button *, a.button *, a.submit * {color: #BBB !important; text-shadow: none !important}
input:hover, input[type="button"]:hover, input[type="checkbox"]:hover, input[type="radio"]:hover, select:hover, button:hover, a.button:hover, a.submit:hover, a.BigButton:hover, a.TabLink:hover {
  border: 2px solid #555 !important;
  border-top-color: #555 !important;
  border-bottom-color: #555 !important;
  border-left-color: #555 !important;
  border-right-color: #555 !important;
  }
input:focus, select:focus {
  box-shadow: 0 0 5px #077 !important}
input[type="checkbox"], input[type="checkbox"]:focus {
  border: 2px solid #333 !important;
  background-color: #111 !important;
  color: #DDD !important;
  border-radius: 4px !important}
input[type="radio"], input[type="radio"]:focus {
  border: 2px solid #333 !important;
  background-color: #111 !important;
  color: #DDD !important}
input[type="checkbox"], input[type="radio"] {min-width: 10px; min-height: 10px}

select input[type="button"], select button {border: none !important}
