button:active, input[type="submit"]:active, a.button:active, a.submit:active, a.BigButton:active, a.TabLink:active, .Active .TabLink {background: #292929 !important; color: #FFF !important}

input *, input, select {color: #F0F0F0  !important}
input *:hover * {color: #F0F0F0 !important; text-shadow: 0 0 2px #FFF !important}

textarea {-moz-appearance: none !important; background: rgba(0,0,0,1) !important; border-radius: 3px !important; border: 1px solid #000 !important; box-shadow: inset 0 0 8px #000 !important}
textarea:focus {background: rgba(0,0,0,1) !important}
textarea, textarea * {color: #C8C8C8 !important; text-shadow: 0 0 1px gray !important}
textarea:focus, textarea:focus > * {text-shadow: none !important; box-shadow: none !important}

option {
	-moz-appearance: none !important; background: none !important; color: #555 !important}
