css:
@-moz-document domain("forums.digitalspy.com") {
        body {background: #EFF5FB !important}
		table {background: #EFF5FB}
		.login_box {background: #EFF5FB }
		.nav-sticky {background: #7798BA !important}
		.MeBox {background: #EFF5FB}
		.DataTable > thead:nth-child(1) > tr:nth-child(1) {background: #CEDCE9}
		.Item-BodyWrap {background: #EFF5FB }
		.PageControls:nth-child(7) > div:nth-child(2) > div:nth-child(1) > * {background: #7798BA !important}  
		.CrumbLabel:nth-child(2) > a:nth-child(1) > span:nth-child(1) {color: #7798BA}
		.profile-nav {color: #7798BA}
		.SpInbox {color: #7798BA}
		.SpNotifications {color: #7798BA}
		.ToggleFlyout:nth-child(3) > a:nth-child(1) > span:nth-child(1) {color: #7798BA}
		.SpOptions {color: #7798BA}
		.CrumbLabel:nth-child(1) > a:nth-child(1) > span:nth-child(1) {color: #7798BA}
		.Options { background: #7798BA !important;} 
        #Item_0 > div:nth-child(1) {background: #EFF5FB !important}
        /*.Item:first-child.has-ad {display: none }*Hiding first  post of a page replaced below*/
        .markering:nth-child(2) {display: none}
        .bodybox-wrap {background: #7798BA !important;}
        .Form-BodyWrap {background: #7798BA !important;}
        .Buttons { background: #7798BA !important;}
        .Back > * {color: #000000 }
        .editor-help-text > a:nth-child(1) {color: #FFF } 
        .Button:nth-child(2)  { background: #000 !important;}
        a.Button:nth-child(4) { background: #000 !important;}
        #Form_PostComment { background: #000 !important;}
        .info-panel a {color: #7798BA !important;}
        .MessageList.Discussion .ItemDiscussion::before {background: #7798BA !important;}
        .PageControls:nth-child(10) > div:nth-child(2) > div:nth-child(1) >*  { background: #7798BA !important;}
        .PageControls:nth-child(12) > div:nth-child(2) > div:nth-child(1)>*{ background: #7798BA !important;}
        .PageControls:nth-child(9) > div:nth-child(2) > div:nth-child(1) >* { background: #7798BA !important;}
        td:nth-child(1) > div:nth-child(1) > span:nth-child(1) {background: #EFF5FB !important}
        td:nth-child(1) > div:nth-child(1) > div:nth-child(1) {background: #EFF5FB !important}
        .ReactButton.Quote {color: #000 !important; background: #7798BA}
        .ReactButton-Flag .ReactLabel {color: #000 !important; background: #7798BA}
        .MessageList .Item .CommentHeader {background: #E4EEF8 }
        .page-content .PageDescription {background: #E4EEF8}
        .CategoryBox .H, .CategoryGroup .H, .MessageList .Item-Header, .MessageList.Conversation .Meta, .panel-default > .panel-heading {background: #E4EEF8}
        .page-content .MessageList .Item:first-child.has-ad, .page-content .MessageList .ItemDiscussion.has-ad {min-height: 0}
        .page-content .CategoryGroup .H {background-color: #7798BA !important;}
        .DataTableWrap {background: #EFF5FB !important}
.FilterMenu .Active > a, .FilterMenu .Active > a:hover {background-color: #7798BA !important}
.FilterMenu .Active > a, .FilterMenu .Active > a:focus, .FilterMenu .Active > a:hover, .nav-pills > li.active > a, .nav-pills > li.active > a:focus, .nav-pills > li.active > a:hover {color: #000}
body a, body a:hover, body a:focus {color: #020BBF}
section .Button {background: #7798BA !important;}
.PanelInfo .Active a, .PanelInfo .Active a:hover {background: #7798BA !important;}
.blockquote {color: #000000 }
}

@-moz-document domain("digitalspy.com") {
        .header-content {background: #7798BA !important}
		.nav {background: #7798BA !important}
		.landing-feed-alternative-container {background: #7798BA !important}
		.ad-gpt-breaker {background: #7798BA !important}
		.leaderboard {background: #7798BA !important}
		.column:nth-child(3){background: #EFF5FB !important}
		.column:nth-child(4){background: #EFF5FB !important}
		.column:nth-child(5){background: #EFF5FB !important}
		.column:nth-child(5) > div:nth-child(6) {display: none}
		.landing-feed--ad-vertical:nth-child(32) {display: none}
		.ad-article-breaker:nth-child(4) > div:nth-child(1) > div:nth-child(1) {display: none}
		.ad-article-breaker-mobile {display: none}
		.leaderboard {display: none}
		.nav-sticky.nav-scrolled {position: static;} /* Allow Menu to scroll */
}
