css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("s12.photobucket.com") {

#containerSecondaryNavigation,  #columnLeft_po, #columnLeft_pr, #columnLeft_ap, #columnLeft_tg, #panelAdv, #bubbleLocalUploadHelp, #tabUpload_Mobile, #tabUpload_Email, .containerSearchCtrls, #submitHeaderSearch, .title, #panelFooterSearch, #cell_Groups, #cell_FindStuff, #cell_WhatsNew, #cell_Search, .albumViewLinks, #uploadLinks, #sponsorMiniPromo, #panelErrorDetails, #changeLinkOptions{
display:none!important;
}

.pbUploadPanel{
height: 200px!important;
}

#containerUploadPanels{
position: relative;
left: 50%;
margin-left: -150px;
}

.image{
height: 165px;
}

#containerThumbnails li{
border: 1px solid #333333;
text-align: center;
width: 25%!important;
margin-left: 5%!important;
}
#containerThumbnails li:hover{
border-color: #ee00ff;
background-color: #ffeeff;
}

.frame{
text-align: center!important;
margin: auto;
}
.frame div{
margin: auto!important;
float: none!important
}

}
