css:
@-moz-document url-prefix("http://forums.warriorcats.com") {
/*Global background*/
html {
    background: none !important;
}
body { 
	background-image: url('http://i.imgur.com/WsuhVnO.jpg') !important;
    background-attachment: fixed !important;
}

/*Global fonts*/
* {
  color: #999;
}
.sharedContentBody, .sharedContentBody * {
  color: #fff;
}
.sharedContentBody a, .sharedContentBody > a, .sharedContentBody * a {
  text-decoration: underline !important;
}
strong, b, em, i, sub, sup, span span {
  color: inherit !important;
}

/*Links. I'm aware that this is very messy. If you have questions, send a message at http://axlepaw.tumblr.com/ask */
.forumTopicSubject a, .forumTopicNavigation a, .shadedContentArea.backgroundColor1OnHover:hover a:visited, .shadedContentArea a:visited, .shadedContentArea a, div.widgetCssDEFAULT A, div.widgetCssDEFAULT .widgetHeader A, div.widgetCssDEFAULT .sidebarItemBody A, div.widgetCssDEFAULT .sidebar_section_header A, .breadcrumb a, #userInformationPopupRightColumn a, showResultsWrapper a ,.primaryContentBox A, .primaryContentBox A:visited, .search_type_title a, #addAttachmentsLinkId, #postReplyAjaxFormId .small_print, .requiredLabelMarker {
    color: #fff !important;
}

/*Search page*/

.searchPageTitle, .medbold, .wwgrp label, .userSearchAndSelectionUserFieldLabel, #tab_allMembersDivId a, #tab_selectedUsersDivId a {
    color: #fff !important;
}
div.unselectedUserDiv { 
    background-color: #333 !important;
}
.personalHeaderTabOn {
    background-color: #222 !important;
}

/*Buttons*/
.yui-skin-sam .yui-button button, .yui-skin-sam .yui-button a, .yui-skin-sam .yui-button a:visited  {
  color: #000 !important;
}
  
/*Profile fixes*/
.selectedUserSectionTitle {
  background-color: #222 !important;
}
.requiredMarkerDescription {
  background-color: #222 !important;
}
tr.altRow td {
  background-color: #222 !important;
}
.shadedContentArea.backgroundColor1OnHover:hover, .shadedContentArea {
    background: #333 !important;
}

/*FAN ART*/

/*Actions*/
.shadedContentArea.backgroundColor3 {
  background-color: #161616 !important;
}
  
/*HEADER*/

/*Sign-in header (horizontal bar across the top)*/
#signInHeader {
  background-color: #222 !important; 
  border-bottom: 1px solid #333 !important;
}

#alertsHeaderButton {
  color: #fff !important;
}

/*User info (pops up when hovering over user icon on the header)*/
#userInformationPopup {
  background-color: #222 !important;
  border-color: #333 !important;
}

/*Header image*/
.customHeaderHTML {
  margin-left: -1px !important;
  border-left: 1px solid #333 !important;
  border-right: 1px solid #333 !important;
}

/*Navigation bar*/
#horizontalNavMenuWrapper {
  width: 1000px !important;
  border: 1px solid #333 !important;
}
#horizontalNavMenuSecondaryWrapper {
  background-color: #222 !important;
  border: 1px solid #111 !important;
}

/*CONTENT BODY*/

/*Wrapper*/
.primaryWrapper {
  background-color: #111 !important;
  border-left: 1px solid #333 !important;
  border-right: 1px solid #333 !important;
}

/*Topic title bars*/
.statusBackgroundColor,
.info_bar_section_alt {
  background-color: #333 !important;
  box-shadow: 2px 2px 5px #000 !important;
}
.topicHeaderTitleColumn {
  color: #fff !important;
}

/*Content area (when hovering over thread info)*/ .backgroundColor2OnHover:hover {
  background-color: #333 !important;
}

/*Thread info bar*/
.info_bar_section {
  background-color: #222 !important;
  box-shadow: 2px 2px 5px #000 !important;
}
/*When posting content*/
#tinymce {
    background: #fafafa !important;
}
 #tinymce > p, #tinymce > p *, #tinymce > div, #tinymce > a, #tinymce > a * {
    color: initial;
}
.mceContentBody .quotedText * {
	color: #fff;
}
/*Source editor*/
.forceColors {
    background: none !important;
}
.title label {
    color: #2B6FB6 !important;
}
#htmlSource {
    color: initial !important;
}

/*RIGHT SIDEBAR*/

/*Actions widget*/
.rightSidebar .shadedContentArea {
  background-color: #222 !important;
}
.actionBoxWrapper {
  box-shadow: 2px 2px 5px #000 !important;
}

#watchForumBody * {
  color: #fff !important;
}

/*Sidebar widget*/

/*Widget title*/
div.widgetCssDEFAULT .sidebar_section_header, div.widgetCssDEFAULT .sidebar_section_header td {
  background-color: #222 !important;
}
.widgetTitleText {
  color: #fff !important;
}
/*Widget content*/
div.widgetCssDEFAULT .sidebar_section_header, div.widgetCssDEFAULT .sidebarItemBody {
  padding-top: 5px !important;
  border-color: #111 !important;
  box-shadow: 2px 2px 5px #000 !important;
}
div.widgetCssDEFAULT .sidebarItemBody {
  border-top: 1px solid #000 !important;
  background-color: #222 !important;
  box-shadow: 2px 2px 5px #000 !important;
}

/*THREAD*/

/*Poll*/
.pollDetails * {
  box-shadow: none !important;
}
  
/*Quote*/
.info_bar_section_alt .quotedText, .commentWrapper .quotedText {
  background: #333 !important; 
  border: 1px solid #000 !important;
}
.info_bar_section_alt .quotedText, .commentAltRow .quotedText {
  background: #333 !important; 
  border: 1px solid #000 !important;
}

/*Blockquote*/
.sharedContentBody blockquote {
  font-style: italic !important; 
  padding: 0.25em 40px !important; 
  line-height: 1.45 !important; 
  position: relative !important;
  border: none !important;
}
.sharedContentBody blockquote:before {
  font-family: Georgia !important; 
  display:block !important; 
  content: "\201C" !important; 
  font-size: 75px !important; 
  position: absolute !important; 
  left: -5px !important; 
  top: -20px !important; 
  color: #fff !important;
}

/*FOOTER*/
#page_footer * {
  color: #ccc !important;
}
.footerWrapper {
  background-color: #111 !important;
  border-top: 1px solid #333 !important;
}
.poweredByLine {
  background-color: #111 !important;
  border-left: 1px solid #333 !important;
  border-right: 1px solid #333 !important;
  margin-left: -1px !important;
}
.quotedText .quotedText .quotedText .quotedText,
.quotedText .quotedText .quotedText .quoteHeading {
  display: none !important;
}
}
