css:
@-moz-document domain("live.com") {
@font-face {
        font-family:'Lato';
        font-style: normal;
        font-weight: 400;
        src: local('Lato Regular'), local('Lato-Regular'), url(http://themes.googleusercontent.com/static/fonts/lato/v6/9k-RPmcnxYEPm8CNFsH2gg.woff) format('woff');
    }
    body {
        font-family:'Lato', 'calibri', "Segoe UI", "Segoe UI Web Regular", "Segoe UI Symbol", "Helvetica Neue", "BBAlpha Sans", "S60 Sans", Arial, "sans-serif" !important;
        font-size:75% !important;
    }
    :root input[type=checkbox]+label {
        background-position: left -40px;
    }
    :root input[type=checkbox]:checked+label {
        background-position: left 38px, left -116px, left -40px;
    }
    .LeftNav LI.lnav_item .lnav_itemLnk, .LeftNav LI.lnav_item .lnav_newLnk, .LeftNavBasic LI A {
        min-height:1em;
    }
    .FmB {
        color: #AAA !important;
        font-style:italic;
    }
    .InboxTableBody li {
        border-bottom:1px solid #EEEEEE;
    }
    .ContentRight {
        z-index: 6 !important;
        margin-left: 3px;
    }
    .Managed #messageListContainer {
        overflow-y: auto !important;
        padding-top: 0 !important;
        margin-top: 51px !important;
        z-index: 7 !important;
        box-shadow:-2px 0 5px 0 rgba(0, 0, 0, .3) !important;
    }
    .Managed .RPOn .PaginationContainer {
        z-index: 7 !important;
    }
    .t_body .t_sbgc {
        background-color: #fff !important;
        background: linear-gradient(to bottom, #ffffff 0%, #fafafa 55%, #ededed 100%);
    }
    ::-webkit-scrollbar:vertical {
        width:10px;
    }
    ::-webkit-scrollbar:horizontal {
        height:10px;
    }
    ::-webkit-scrollbar-track {
        width: 17px;
        height: 17px;
        background-color: #f0f0f0;
        border-radius: 10px;
        border: none;
        box-shadow: inset 0 0 10px 0 rgba(0, 0, 0, .3);
    }
    ::-webkit-scrollbar-thumb:vertical {
        background-color: #CDCDCD;
        border: 1px solid #F0F0F0;
        border-radius: 10px !important;
        background: linear-gradient(to right, #3b679e 0%, #2b88d9 50%, #207cca 51%, #7db9e8 100%);
    }
    ::-webkit-scrollbar-thumb:horizontal {
        background-color: #CDCDCD;
        border: 1px solid #F0F0F0;
        border-radius: 10px !important;
        background: linear-gradient(to bottom, #3b679e 0%, #2b88d9 50%, #207cca 51%, #7db9e8 100%);
    }
    ::-webkit-scrollbar-button {
        height:0 !important;
        background-color: #f0f0f0;
        background-image: none;
        background-repeat: no-repeat;
    }

    #RightRailContainer{
        width:0;
    }

    .App.Managed .RPOn .WithRightRail .FooterContainer, .App.Managed .RPOff .WithRightRail #ManagedContentWrapper>.FooterContainer, .Managed .WithRightRail #MainContent {
        right: 0;
    }
    .Managed .ReadingPaneContainer {
        left: 1px;
        overflow-y: auto !important;
        overflow-x: auto;
        box-shadow: -1px 0 5px 0 rgba(0, 0, 0, .3);
    }

    #sidebar {
        box-shadow: inset 1px 0 5px 0 rgba(0,0,0,.5);
    }

    /************** contacts ******************/
    .CP_Header {
        font-size: 230% !important;
        height: 70px !important;
        line-height: 70px !important;
    }

    .CP_ViewDetailsBody {
        top:70px !important;
    }

    .CP_DetailsContainer {
        box-shadow: -1px 0 5px 0 rgba(0, 0, 0, .3);
    }
    .CP_DN {
        letter-spacing: .1em;
        word-spacing: .3em;
        font-family: 'Lato' !important;
        font-weight: lighter !important;
    }
    .cntPrfV_grpT {
        font-size: 150% !important;
    }

    [id^=grpf_]{
        padding-left: 20px !important;
    }

    .mlRule{
        border:none !important;
  }


    /************** calendar ******************/
    .RE_WebKit .dayView .contentTableContainer {
        padding-right: 11px !important;
    }
    .RE_WebKit .inside-unselected, .RE_WebKit .inside-selected, .inside-unselected, .inside-selected {
        padding-top:0 !important;
    }
    .monthGrid .segmentContainer {
        border-radius: 10px;
        height:20px !important;
        box-shadow:0 1px 1px 0 rgba(0, 0, 0, .3);
    }
    .monthGrid thead tr td {
        font-size: 120% !important;
        line-height: 120% !important;
        height: 24px !important;
        color: #666 !important;
        background-color: #fff !important;
        border-right:1px solid #ddd !important;
        border-top:none !important;
    }

    .monthGrid thead tr td:last-child{
        border-right:none !important;
    }

    .weekday {
        text-transform:capitalize !important;
        text-shadow: 0 1px 0 #fcfcfc, 0 1px 1px rgba(0, 0, 0, .5);
    }

    #calendarViewsContainer{
        width:90% !important;
        margin:0 auto;
        box-shadow: 0 0 5px 0 rgba(0,0,0,.5)
    }

    .contentTableContainerInner{
        overflow-y:auto !important;
    }

    .contentTable td.lightbg {
        background-color: #fff !important;
        border-right:1px solid #ddd !important;
    }

    .contentTable td.hCell {
        border-top: 1px solid #ddd !important;
    }

    .contentTable td.bgCell{
        border-top:none !important;
    }

    .contentTable td.bgCell:not(.lightbg){
        border-bottom:none !important;
    }

    .contentTable td:nth-child(n+6):not(.lightbg){
        background-color: rgba(100,120,250,.1) !important;
        border-right:1px solid #fff !important;
    }
    .contentTable td.lightbg:nth-child(n+6){
        background-color: rgba(200,210,255,.8) !important;
        border-right:1px solid #fff !important;
    }

    td.today, .contentTable td.today:nth-child(n+6)  {
        background-color: rgba(255,0,0,.7) !important;
    }
	
	td.bgCell.today{
  		border-bottom:1px solid #fff !important;
	}

	.SortFilterContainer .SortFilterItems {
  		padding-left:0 !important;
    }
    
    .Managed #sortFilterContainer {
    	z-index:8 !important;
	}
  
  	#sortMenu {
    	padding-right:0 !important;
	}
}
