@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
	html, body {
	background: #585858 !important;
	font-size: 12px !important;
	color: white !important;
	margin: 0 auto 20px auto !important;
	position: relative !important;
	/*width: 1234px !important;*/
}
#header-bar {
	background: url('data:image/png;base64,') repeat-x top left !important;
	border: 0 !important;
	margin: 0 !important;
	padding: 5px !important;
	height: 48px !important;
}
#logo {
	margin: 6px 0 0 20px !important;
}
#logo a {
	background: url('data:image/png;base64,') no-repeat top left !important;
	display: block !important;
	font-size: 0px !important;
	height: 35px !important;
	width: 155px !important;
}
img[src*="star_1"] {
	width: 0 !important;
	height: 10px !important;
	padding-left: 10px !important;
	margin-left: 1px !important;
	margin-bottom: -1px !important;
	background-image: url('data:image/gif;base64,R0lGODlhCgAKAMQAAP//AP/6AP/7AP/0AP/1AP/tAP/lAP/mAP/eAP/dAP/WAP/XAP/QAP/RAGZmZgAAAP///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABAALAAAAAAKAAoAAAUyoCOOpAOc6CkKbMCyIiHPxOA8Ra7nz2McwIPh10sYEUhEoqdYLBTNZ6/RYFivvay2FwIAOw%3D%3D') !important;
}

img[src*="star_05"] {
	width: 0 !important;
	height: 10px !important;
	padding-left: 10px !important;
	margin-left: 1px !important;
	margin-bottom: -1px !important;
	background-image: url('data:image/gif;base64,%3D%3D') !important;
}

img[src*="star_0.png"] {
	width: 0 !important;
	height: 10px !important;
	padding-left: 10px !important;
	margin-left: 1px !important;
	margin-bottom: -1px !important;
	background-image: url('data:image/gif;base64,') !important;
}
#feedBody {
        background: #617488 !important;
}
p {
	padding: 0 30px 0 20px !important;
	text-align: justify !important;
}
a {
	color: #cccccc !important;
	text-decoration: none !important;
}
a:hover, a:active {
	color: #fff !important;
}
a:visited {
	color: #999 !important;
}
#account-link a {
	color: white !important;
	text-decoration: none !important;
}
#account-link a:hover {
	text-decoration: underline !important;
}
.ad {
	display: none !important;
}
#Session {
	margin: 0 !important;
}
#navigation a, #navigation a:visited, #navigation .current-page {
	background-color: #999 !important;
	color: white !important;
}
#navigation .current-page {
	background-color: #585858 !important;
	border: 1px solid transparent !important;
	border-bottom: none !important;
	color: white !important;
}
#navigation a:hover {
	background-color: #777 !important;
	color: white !important;
}
#search-submit {
	background-color: #999 !important;
	color: white !important;
}
#search-submit:hover {
	background-color: #777 !important;
}
#search-terms:focus {
	background-color: #eee !important;
}
#obsoletion-message {
	background: #777 !important;
	border-color: #777 !important;
}
#view-code {
	background: #777 !important;
}
}

@-moz-document url-prefix("http://blog.userstyles.org/") {

#header a:hover {
        color: #999 !important;
}
#sidebar {
	background: #777 !important;
}
#description {
	background: #777 !important;
}
.page_item a:hover {
	background: #585858 !important;
}
#pagestripe {
	display: none !important;
}
#header a:hover {
        background: transparent !important;
}
pre {
        background: #777 !important;
}
body {
        margin-top: -3px !important;
}
}

@-moz-document url-prefix("http://userstyles.org/forum/") , url-prefix("https://userstyles.org/forum/") {

#Body {
        margin-left: 30px !important;
        margin-right: 30px !important;
}

#Header {
	background: url('data:image/png;base64,') repeat-x top left !important;
        height: 58px !important;
        margin-left: 30px !important;
        margin-right: 30px !important;
        margin-top: -10px !important;
        -moz-border-radius: 0px 0px 10px 10px !important;
        border-top: none !important;
        border-bottom: none !important;
}

#Discussions li ul {
        background: url('data:image/png;base64,') repeat-x top left !important;
        background-color: #585858 !important;
        -moz-border-radius: 10px 10px 0px 0px !important;
}

#Panel {
        background: none !important;
}
#Panel ul ul li a:hover {
        background: #485669 !important;
}
#AboutVanilla {
        background: none !important;
        width: 130px !important;
        text-align: left !important;
}
#Discussions ul span, .DiscussionComments {
        color: white !important;
}
#Discussions li li {
        color: white !important;
}
#Discussions li {
        background: none !important;
}
#Discussions li ul {
        border: none !important;
}
#Panel h2, #DiscussionsPage .ContentInfo h1 {
        color: white !important;
}
#Session {
        color: white !important;
        margin-top: -20px !important;
        margin-right: 30px !important;
        font-size: 10pt !important;
}
#Form form {
        background: #777 !important;
        border: 1px solid white !important;
}
#Form.StartDiscussion label, #Form li {
        color: white !important;
}
#Header h1 a {
	background: url('data:image/png;base64,') no-repeat top left !important;
	font-size: 0px !important;
	height: 35px !important;
	width: 155px !important;
        margin-left: 25px !important;
        position: absolute;
        left: 30px;
        top: 11px;
}
#Header ul li a {
        color: white !important;
        font-size: 10pt !important;
        font-weight: bold !important;
        background-color: #999 !important;
        border-top: 1px solid transparent !important;
        border-left: 1px solid transparent !important;
        border-right: 1px solid transparent !important;
        border-bottom: 1px solid #999 !important;
        -moz-border-radius: 5px 5px 0px 0px !important;
}
#Header ul li a:hover {
        background-color: #777 !important;
        border-top: 1px solid transparent !important;
        border-left: 1px solid transparent !important;
        border-right: 1px solid transparent !important;
        border-bottom: 1px solid #777 !important;
        -moz-border-radius: 5px 5px 0px 0px !important;
}
#Header ul li.TabOn a {
        color: white !important;
        background-color: #585858 !important;
        border-top: 1px solid transparent !important;
        border-left: 1px solid transparent !important;
        border-right: 1px solid transparent !important;
        border-bottom: 1px solid #585858 !important;
        -moz-border-radius: 5px 5px 0px 0px !important;
}
#Header ul li.TabOn {
        background: none !important;
        border: none !important;
}
#Header ul {
        text-align: right !important;
        position: relative !important;
        top: 38px;
        right: 15px;
}
#AccountProfile, #AccountProfile .ProfileTitle * {
        color: white !important;
}
#Comments .CommentBody, #CommentsPage .ContentInfo h1 {
        color: white !important;
}
#Comments code {
        background: #777 !important;
        border-left: 6px solid #999 !important;
}
.CommentBody blockquote {
        background-color: #999 !important;
        border-left: 6px solid #777 !important;
        color: white !important;
}
.CommentBody blockquote cite {
        color: white !important;
}
#Form.AddComments label, #CommentFormats label, #Form fieldset legend {
        color: white !important;
}
#Form.Account label {
        color: white !important;
}
#CategoryPage div.ContentInfo h1 {
        color: white !important;
}
#Form.Account h2, #Form.Account p, #Form.Account label small, .Description * {
        color: white !important;
}
#Categories li ul {
        background: url('data:image/png;base64,') repeat-x top left !important;
        background-color: #585858 !important;
        -moz-border-radius: 10px 10px 0px 0px !important;
        border: none !important;
}
#Categories li {
        background: none !important;
}
.CategoryDescription {
        color: white !important;
}
.CategoryDiscussionCount, #Categories ul span {
        color: #bbb !important;
}
}
