css:
@-moz-document url-prefix("http://www.cnbeta.com/articles"), url-prefix("http://cnbeta.com/articles") {
footer,.global_head>*, .dig_btn, #back_top, #fixed_body, aside .cb_box,.nav_new, .yellow_bottom_v2,.main_content_right>*,
.digbox, .gbox830,.bfd_img_logo,.white_bottom_v2,.commt_list nav,#backToTop,.navi,.main_content + section,iframe{
	display: none !important;
}
#main, #content, #news, #news_title, #news_content, .main_content_right>.cber.hotcomments{
	display: block !important;
}
#J_commt_list {
	border: none !important;
}
body,html{
min-height:100%;
}
html{
	background:#494949  url('https://res.wx.qq.com/zh_CN/htmledition/images/bg_universal17ced3.png')!important;

}
body {
}

#news_title {
	margin-bottom: 20px !important;
	font-size: 24px !important;
}

#newsBox #mark {
	display: none !important;
}
#post .subTitle {
	background: none!important;
	border: none !important;
}
#newsBox #post {
	font-size: 14px !important;
	border: none !important;
}
#post .subTitle, .replydiv label, #post label {
	color: RGB(0,0,0)!important;
	font-size: 15px !important;
}
input#subject0.input, input#name0.input, input#page0.input, input#email0.input, textarea#comment0, input#valimg0.input {
	background: #aaa !important;
	border: none !important;
}
.replydiv .button, #post .button {
	background: RGB(45,45,45) !important;
	padding: 0px 5px 5px 5px!important;
}
a:link, a:visited, a:hover {
	color: RGB(45,45,45)!important;
}
* {
	font-family:"FZLanTingHei-R-GBK","Microsoft YaHei", Helvetica, Arial, "Arial Unicode MS", Sans-Serif!important;
}

.global_head {
	height: 44px !important;
}
.global_head:before {
	color: #fff;
	font-size: 40px;
	content: "cnBeta";
	text-shadow: 0 0 10px #000;
}
.global_head:after {
	color: #fff;
	font-size: 24px;
	content: " 中文业界资讯站";
	text-shadow: 0 0 10px #000;
}
.blue_bar, .yellow_bar ,.blue_bar_v2{
	background: none !important;
}
#job_box{
	background: rgba(0,0,0,0)!important;
	padding:0px !important;
}
#job_box dd,#job_box a{
	color:#ddd !important;
}
#job_box dd,#job_box dt{
	margin:5px;
}
.hotcomments header h4 {
	color: #FFF !important;
}

.hotcomments ul li, #job_box dl {
	float: left;
	margin-bottom: 8px;
	width: 292px;
	padding: 4px!important;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #222 !important;
	background: rgba(0,0,0,0.3)!important;
	color: #ddd;
	border-radius: 5px;
	font-size: 14px;
}
.hotcomments {

	position: absoulete;
	right: 16px;
	top: 16px;
}
.hotcomments ul {
	background: none!important;
	padding: 0px!important;
}
.hotcomments .title {
	border-bottom: 1px solid #444 !important;
}
.title {
	border-bottom: 1px solid #666 !important;
}
.userName {
	color: #ddd!important;
}
.con {
	margin-top: 0.25em;
}
.re_mark span {
	color: #888!important;
}
.commt_a_link span {
	padding: 0 6px;
}
span em {
	font-weight: bold;
	color: #999!important;
}
.cb_box.article {
	background: #eee !important;
	border: none!important;
	box-shadow: 0 0 5px!important;
}
.introduction {
	background: #ddd !important;
	border-top: thin solid #ccc !important;
	border-bottom: thin solid #ccc !important;
}
p img, embed {
	box-shadow: 0 0 5px #ccc;
}
.commt_list blockquote,.left_art_box a {
border: 0px!important;
box-shadow: 0 0 2px rgba(0,0,0,0.3);
border-top: 1px solid #666 !important;
	background: rgba(72,72,72,0.3) !important;
	border-radius: 5px;
	color: #eee !important;
	border-bottom: 1px solid #222 !important;
	
	
}

.content_body {
	clear:both;
	background: none !important;
	border-left: 0px solid #E8E8E8 !important;
	border-right: 0px solid #E8E8E8 !important;
}
.commt_list .comment_body {
	padding-left: 60px;
}
.commt_list blockquote p.re_text, .commt_list .re_text {
	color: #ddd !important;
}
.commt_list dt {
	padding: 5px 10px;
	background: none !important;
}
.commt_list dl {
	z-index:100;
	padding: 1px;
	margin-bottom: 16px;
	background: rgba(0,0,0,0.3);
	border-left: 0px solid #ccc !important;
	border-right: 0px solid #ccc !important;
	border-radius: 5px;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #222 !important;
}
.commt_list nav {
	background: none !important;
	border: 0px !important;
	color: #eee;
}
.cb-reply-sub {
    border-radius: 5px;
    overflow: hidden;
    background: #eee!important;
    box-shadow: 0 0 5px  #000;
}
.cb-reset .cb-rounded-top {
    border-radius: 5px 5px 0 0!important;
    border: 0px !important;
}
.cb-thread .cb-reset .cb-post-options {
    border-left: none!important;
    border-right: none!important;
}
.cb-post-box {
    margin-top: 5px!important;
    padding: 0 4px;
}
.cb-post-button {
    right: 4px!important;
    top: -1px!important;
}
.re_lou {
	color: #eee;
}


.post_commt .commt_l {
	margin-left: 10px;
}
.blue_bar{
	width:662px; 
	clear:right;
}
.blue_bar em {
	color:#fff !important;
}
.post_commt .login .logoA,.post_commt .active,.post_commt .disabled {
	border-radius: 4px;
}
.seccode_box img{
	border-radius: 4px;
	background:#eee;
}

.post_commt:hover {
	left: -4px;
	opacity: 1;
	background: #fff;
	transition: all cubic-bezier(.5,0.25,.25,1.1) 0.5s;
}
.post_commt {
	background: rgba(64,69,74,0.9);
	border-left: 0px solid #ccc !important;
	border-right: 0px solid #ccc !important;
	border-radius: 5px;
	border-top: 1px solid #444 !important;
	border-bottom: 1px solid #222 !important;
	box-shadow: 0 0 10px #000;
	z-index: 100;
	position: fixed !important;
	bottom: 10%;
	opacity: 0.75;
	padding: 20px;
	left: -404px;
	transition: all cubic-bezier(1,0,.8,1.1) 1.25s 0.25s;
}
.cb-post-toolbar {
	background: #eee;
}
#J_emotions_pop {
	z-index: 1000!important;
}
.rating_box.clearfix {
	background: rgba(69,69,74,1);
	border-radius: 5px;
	box-shadow:0 0 10px #000;
	z-index:100;
	bottom: -125px;
	padding:2px 5px;
	position: fixed !important;
	right: -595px;
	transition: all ease 0.3s;
	width:640px;
}
.rating_box.clearfix:hover {
	background: rgba(236,236,236,1);
	bottom:-5px;
	right: -20px;
}
.mod{
	padding:0px !important;
}
.mod .hd h3{
	color:#999 !important;
}
.commt_list
{
	z-index:100;
}
.commt_list img
{
	border-radius: 5px;
}
.content p img{
	box-shadow: 0 0 5px rgba(0,0,0,0.5);
}
.fr,.navi div
{
	transition: all ease 0.5s;
	opacity:0.1;
}
.fr:hover,.navi div:hover
{
	opacity:1;
}
.navi
{
	transition: all ease 0.5s;
	height:30px;
}
.navi:hover
{
	opacity:1;
	height:125px;
}
.new_cmnt{
	display:none!important;
	background:none!important;
}
header,.content_body{
	border-top:none!important;
	border-bottom:none!important;
	border-left:none!important;
	border-right:none!important;
}
#job_box dl
{
	opacity:0.1;
	transition: all ease 0.5s;
}
#job_box dl:hover
{
	transition: all ease 0.5s;
	opacity:1;
}
.popup_area .close a, .new_cmnt, .support_tip, .against_tip, .commt_a_link:hover, .commt_a_link:active, .commt_a_link:hover span, .commt_a_link:active span{
	transition: all ease 0.25s;
	background-image:none !important;
	background:#ddd !important;
	border-radius:5px;
}
.left_art_box a,.left_art{
	transition: all ease 0.5s;
	background-image:none !important; 
	padding:0px!important;
	color:#aaa!important;
	font-size: 12px!important;
	height:auto!important;
}
img.J_emotion {
	background: #fff;
	box-shadow: 0 0 10px rgba(0,0,0,0.5);
	margin: auto 0.5em;
}
}

style_setting_options.value:
border-left: 1px solid #282828 !important;
border-right: 1px solid #282828 !important;
border-top: 1px solid #555 !important;
padding: 2px !important;
