css:
@-moz-document url-prefix(http://couchtuner.) {
#page {width:100%;}
#container {position:relative; width:100%;}
#header {display:none;}
div>center>iframe {display:none;}
#sidebar {display:none; width:0px;}
#footer {display:none; width:0px; height:0px;}
#content {width:100%}
.post {width:100%;}
.post h2 {font-size:22px; text-align:center;}
.postTabs_divs {padding:0px;}
.postTabs_curr_div>iframe {width:100%;}
.meta {display:none;}
.comments_part {display:none;}
iframe {
    width: -webkit-fill-available;
    height: -webkit-fill-available;
}
}
