css:
@namespace url(http://www.w3.org/1999/xhtml);
      
@-moz-document url-prefix("http://www.psu.kz/student_cabinet")
{
html, body
{
    background: #444 url(http://www.look.com.ua/pic/201209/1920x1080/look.com.ua-13042.jpg)
        center fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
}

#header, #sidebarleft, #contentstud
{
    background: url(http://www.look.com.ua/pic/201209/1920x1080/look.com.ua-15949.jpg)
        center fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    border-radius: 5px !important;
    box-shadow: inset 0px 0px 2px #666 !important;
}

#header, #sidebarleft, .logoname
{
    margin-top: 10px !important;
}

#header
{
    width: 99% !important;
    margin-left: auto !important;  
}

#sidebarleft
{
    width: 218px !important;
    left: 10px !important;
    margin-bottom: 10px !important;
}

.containerstud
{
    left: 240px !important;
    width: 86% !important;
}

#contentstud
{
    padding-top: 25px !important;
    padding-bottom: 25px !important;
    margin: 10px 10px 10px 20px !important;
}

div
{
    box-shadow: 0px 0px #111 !important;
}

#sidebarleft img, #sidebarleft embed, .status
{
    visibility: hidden !important;
}

#logo
{
    opacity:0.001;
}

.logoname
{
    background-image: url(http://s61.radikal.ru/i171/1312/9c/06cfddd69537.png) !important;
    background-repeat: no-repeat;
    margin-left: 30px !important;
    height: 400px !important;
    width: 600px !important;
    
}

a, h3, b, .logoname, .user, .pageheader
{
    color: silver !important;
}

p, div, .text, table, .user a
{
   color: #999 !important;
}

.table
{
    border-collapse: collapse !important;    
    background-color: #808080 !important;
}

.table th
{
    background-color: #e9e9e9 !important;   
}

.table hr
{
    color: silver !important;
}

.table td, .even, .odd, .table th
{
    border: 1px solid #808080 !important;
}

.table p, .table b, .table, .table1
{
    color: #111 !important;
}

.table span, .table a
{
    color: darkblue !important;
}

.odd
{
    background-color: #ffffff !important;
}

.even
{
    background-color: lightblue !important;
}
}
