/*====================================
  gogo.gs ja jp liquid compact 2013/12
  ====================================*/
@-moz-document url-prefix('http://gogo.gs/rank/') {
/*--- unwanted contents ---*/
[id$='Ads'],
#dummy.dummy {display: none !important;}

/*--- page ---*/
#wrap {width: auto !important;}

/*--- header ---*/
#head #siteSummary {width: auto !important; float: none !important;}
#head #siteSummary p {padding: 0 !important;}

/*--- menu bar ---*/
#headNav {
 width: auto !important; height: auto !important;
 background-size: 100% 100% !important;
 }
#headNav #mainNav, #headNav #userNav
 {width: auto !important; height: auto !important;}
#headNav #userNav p, #headNav #userNav ul
 {display: inline-block !important;}

/*--- main contents ---*/
#main {width: 100% !important; margin: 0 -300px 0 0 !important;}
#main>div {width: auto !important; margin: 0 300px 0 0 !important;}
#main .divide4 input {width: 100% !important;}

/*--- footer ---*/
#foot #footNavWrap  {width: auto !important;}
#foot #footNavWrap #footNav, #foot #footNavWrap #footPrefList
 {width: 50% !important;}
}
