css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.animeultima.tv") {

body {
    background: url("http://antiwrinklereview.info/skincare/dailyconsumeralerts/4/seamless-floral-pattern-vector-background2.jpg") repeat scroll 0 0 #02354F !important;
    color: #000000;
    font: 12px Arial,Helvetica,sans-serif;
    margin: 0;
    padding: 0;
}

#header-span {
    background: none repeat scroll 0 0 #000000 !important;
    border-bottom: 3px double #434343 !important;
    padding-bottom: 0;
}

#header-container {
    background: none repeat scroll 0 0 #000000;
    margin: 0 auto;
    padding-top: 15px !important;
    text-align: inherit;
    width: 1070px !important;
}

a:link, body_alink {
}
a:visited, body_avisited {
    color: #006699;
}
a:hover, a:active, body_ahover {
    background: none repeat scroll 0 0 #F5FFFA;
    border-radius: 20px 2px !important;
    color: #000000;
    padding-left: 4px !important;
    padding-right: 4px !important;
    transition: all 0.5s ease 0s !important;
}
.page {
    background: none repeat scroll 0 0 #FFFFFF;
    color: #000000;
}
td, th, p, li {
    font: 10pt 'Lucida Console',Helvetica,sans-serif;
}
.tborder {
    background: none repeat scroll 0 0 #484D46 !important;
    color: #000000;
}
.tcat {
    background: none repeat scroll 0 0 #484D46 !important;
    color: #FFFFFF;
    font: bold 10pt Arial,Helvetica,sans-serif;
}
.tcat a:link, .tcat_alink {
    color: #FFFFFF;
    text-decoration: none;
}
.tcat a:visited, .tcat_avisited {
    color: #FFFFFF;
    text-decoration: none;
}
.tcat a:hover, .tcat a:active, .tcat_ahover {
    color: #FFFFFF;
    text-decoration: underline;
}
.thead {
    background: none repeat scroll 0 0 #EEEEEE;
    color: #444444;
    font: bold 11px Arial,Helvetica,sans-serif;
}
.thead a:link, .thead_alink {
    color: #666666;
}
.thead a:visited, .thead_avisited {
    color: #666666;
}
.thead a:hover, .thead a:active, .thead_ahover {
    color: #000000;
    text-decoration: underline;
}
.tfoot {
    background: none repeat scroll 0 0 #484D46 !important;
    color: #FFFFFF;
}
.tfoot a:link, .tfoot_alink {
    color: #FFFFFF;
}
.tfoot a:visited, .tfoot_avisited {
    color: #FFFFFF;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover {
    color: #FFFFFF;
    text-decoration: underline;
}
.alt1, .alt1Active {
    background: none repeat scroll 0 0 #A1A99E !important;
    color: #000000 !important;
}
.alt2, .alt2Active {
    background: none repeat scroll 0 0 #484D46 !important;
    color: #9D9D9D;
    padding: 3px !important;
}
.inlinemod {
    background: none repeat scroll 0 0 #FFFFCC;
    color: #000000;
}
.wysiwyg {
    background: none repeat scroll 0 0 #F5F5FF;
    color: #000000;
    font: 10pt Arial,Helvetica,sans-serif;
    margin: 5px 10px 10px;
    padding: 0;
}
.wysiwyg a:link, .wysiwyg_alink {
    color: #22229C;
}
.wysiwyg a:visited, .wysiwyg_avisited {
    color: #22229C;
}
.wysiwyg a:hover, .wysiwyg a:active, .wysiwyg_ahover {
    color: #FF4400;
}
textarea, .bginput {
    font: 10pt Arial,Helvetica,sans-serif;
}
.bginput option, .bginput optgroup {
    font-family: Arial,Helvetica,sans-serif;
    font-size: 10pt;
}
.button {
    font: 11px Arial,Helvetica,sans-serif;
}
select {
    font: 11px Arial,Helvetica,sans-serif;
}
option, optgroup {
    font-family: Arial,Helvetica,sans-serif;
    font-size: 11px;
}
.smallfont {
    font: 11px Arial,Helvetica,sans-serif;
    text-align: right !important;
}
.time {
    color: #FF4F00;
}
.navbar {
    font: 11px Arial,Helvetica,sans-serif;
}
.highlight {
    color: #FF4F00;
    font-weight: bold;
}
.fjsel {
    background: none repeat scroll 0 0 #3E5C92;
    color: #E0E0F6;
}
.fjdpth0 {
    background: none repeat scroll 0 0 #F7F7F7;
    color: #000000;
}
.panel {
    background: url("http://static.cdn.animeultima.tv/forums/images/gradients/gradient_panel.gif") repeat-x scroll left top #E4E7F5;
    border: 2px outset;
    color: #000000;
    padding: 10px;
}
.panelsurround {
    background: url("http://static.cdn.animeultima.tv/forums/images/gradients/gradient_panelsurround.gif") repeat-x scroll left top #D1D4E0;
    color: #000000;
}
legend {
    color: #22229C;
    font: 11px Arial,Helvetica,sans-serif;
}
.vbmenu_control {
    background: none repeat scroll 0 0 #A1A99E !important;
    color: #FFFFFF;
    font: 13px 'Lucida Console',Helvetica,sans-serif !important;
    padding: 7px 9px;
    white-space: nowrap;
}
.vbmenu_control a:link, .vbmenu_control_alink {
    color: #FFFFFF;
    text-decoration: none;
}
.vbmenu_control a:visited, .vbmenu_control_avisited {
    color: #FFFFFF;
    text-decoration: none;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover {
    border-radius: 20px 5px !important;
    color: #FFFFFF;
    text-decoration: none !important;
}
.vbmenu_popup {
    background: none repeat scroll 0 0 #FFFFFF;
    border: 1px solid #CCCCCC;
    color: #333333;
}
.vbmenu_option {
    background: none repeat scroll 0 0 #EEEEEE;
    color: #333333;
    cursor: pointer;
    font: 11px Arial,Helvetica,sans-serif;
    white-space: nowrap;
}
.vbmenu_option a:link, .vbmenu_option_alink {
    color: #333333;
    text-decoration: none;
}
.vbmenu_option a:visited, .vbmenu_option_avisited {
    color: #333333;
    text-decoration: none;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
    color: #333333;
    text-decoration: none;
}
.vbmenu_hilite {
    background: none repeat scroll 0 0 #DDDDDD;
    color: #000000;
    cursor: pointer;
    font: 11px Arial,Helvetica,sans-serif;
    white-space: nowrap;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink {
    color: #000000;
    text-decoration: none;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited {
    color: #000000;
    text-decoration: none;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover {
    color: #000000;
    text-decoration: none;
}
.bigusername {
    font-size: 14pt;
}
td.thead, th.thead, div.thead {
    padding: 4px;
}
.pagenav a {
    text-decoration: none;
}
.pagenav td {
    padding: 2px 4px;
}
.shade, a.shade:link, a.shade:visited {
    color: #777777;
    text-decoration: none;
}
a.shade:active, a.shade:hover {
    color: #FF4400;
    text-decoration: underline;
}
.tcat .shade, .thead .shade, .tfoot .shade {
    color: #DDDDDD;
}
.fieldset {
    margin-bottom: 6px;
}
.fieldset, .fieldset td, .fieldset p, .fieldset li {
    font-size: 11px;
}
.forumavatar {
    background: none repeat scroll 0 0 #FFFFFF;
    border: 1px solid #CCCCCC;
    padding: 2px;
}

}
