css:
@charset "utf-8";
@-moz-document domain('news.ycombinator.com'), url-prefix('https://news.ycombinator.com') {

body center table {
    background-color: #272822 !important;
    width: 750px !important;
    max-width: 750px !important;
}
table {
    background-color: #272822 !important;
    width: 750px !important;
}
tbody tr td {
    background-color: #272822 !important
}
table tbody {
    background-color: #272822 !important
}
body {
    font-family: Georgia, Times, serif !important;
    font-size: 12pt !important;
    color: #F8F8F2 !important;
    background-color: #272822 !important;
}
span.pagetop {
    color: #bbbbbb !important;
    padding-bottom: 40px !important;
    font-family: Georgia, Times, serif !important;
    font-size: 10pt !important;
    line-height: 10px !important;
}
span.pagetop a:link,
span.pagetop a:visited {
    font-family: Georgia, Times, serif !important;
    font-size: 10pt !important;
    color: #bbbbbb !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    margin-bottom: 10px !important;
    padding-bottom: 4px !important;
    font-weight: normal !important;
    border-bottom: 0px dotted #fff !important;
}
span.pagetop a:hover,
span.pagetop a:active {
    font-family: Georgia, Times, serif !important;
    font-size: 10pt !important;
    color: #f60 !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    margin-bottom: 10px !important;
    padding-bottom: 4px !important;
    font-weight: normal !important;
    border-bottom: 0px dotted #ffa86e !important;
}
td {
    font-family: Georgia, Times, serif !important;
    font-size: 12pt !important;
    color: #999999 !important;
    padding: 3px !important;
}
.admin td {
    font-family: Georgia, Times, serif !important;
    font-size: 8.5pt !important;
    color: #75715E !important;
}
td.subtext {
    font-family: Georgia, Times, serif !important;
    font-size: 12px !important;
    color: #75715E !important;
}
form {
    margin-bottom: 0px !important
}
input,
input[type="text"], 
input[type="submit"] {
    font-family: Georgia, Times, serif !important;
    font-size: 15px !important;
    color: #000 !important;
    padding: 6px !important;
    background-color: #D8D8D8 !important;
    width: auto !important;
    font-family: Georgia, Times, serif !important;
    font-size: 15px !important;
    border: none !important;
    border-radius: 4px !important;
    margin: 0 !important;
}
input,
input[type="text"] {
    color: #000 !important;
    background-color: #F8F8F2 !important;
}
textarea {
    font-family: Georgia, Times, serif !important;
    font-size: 15px !important;
    color: #F8F8F2 !important;
    background-color: #757575 !important;
    column-span: 200px !important;
    border: 1px solid #757575 !important;
    width: 668px !important;
    height: 100px !important;
}
a:link {
 	color: #ECE47E !important;
  	text-decoration: none !important;
}
.subtext a:link,
.comhead a:link,
.adtitle {
    color: #A7EC21 !important;
    text-decoration: none !important;
}
a:hover,
a:active,
.subtext a:hover,
.subtext a:active,
.comhead a:hover,
.comhead a:active {
    color: #A7EC21 !important;
    text-decoration: none !important;
}
a:visited,
.subtext a:visited {
    color: #A7EC21 !important;
    text-decoration: none !important;
}
a[href^="reply"] {
  	color: #C42672 !important;
}
.default {
    font-family: Georgia, Times, serif !important;
    font-size: 12pt !important;
    color: #999999 !important;
    border-bottom: 1px solid #D0D0D0 !important;
    padding: 0px !important;
}
.admin {
    font-family: Georgia, Times, serif !important;
    font-size: 8.5pt !important;
    color: #222222 !important;
}
.title {
    font-family: Georgia, Times, serif !important;
    font-size: 12pt !important;
    color: #999999 !important;
    padding-top: 12px !important;
    font-weight: normal !important;
}
.title a:link {
    font-weight: normal !important;
    font-family: Georgia, Times, serif !important;
    font-size: 14pt !important;
    color: #D9E577 !important;
    margin-right: 6px;
}
.title a:visited {
    color: #CC9900 !important;
}
.yclinks {
    font-family: Georgia, Times, serif !important;
    font-size: 8pt !important;
    color: #999999 !important;
    padding-top: 15px !important;
}
.yclinks a:link,
.yclinks a:visited {
    color: #bbb !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    font-weight: normal !important;
}
.yclinks a:hover,
.yclinks a:active {
    color: #f60 !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    font-weight: normal !important;
}
.comhead {
    font-family: Georgia, Times, serif !important;
    font-size: 9pt !important;
    color: #888888 !important;
}
.comhead a:link {
    padding-left: 2px !important;
    padding-right: 4px !important;
    font-weight: normal !important;
    color: #C42672 !important;
}
.comhead a:hover,
.comhead a:active {
    padding-left: 2px !important;
    padding-right: 4px !important;
    font-weight: normal !important;
}
.dead a:link,
.dead a:visited {
    color: #bbbbbb !important
}
.pagetop a:visited {
    color: #222222 !important
}
.topsel a:link,
.topsel a:visited {
    color: #ffffff !important
}
.subtext {
    font-family: Georgia, Times, serif !important;
    font-size: 8pt !important;
    color: #999999 !important;
    padding-bottom: 20px !important;
    font-style: italic !important;
}
.subtext a:link,
.subtext a:visited {
    padding-left: 2px !important;
    padding-right: 4px !important;
    font-weight: normal !important;
}
.comment,
.comment font {
    color: #F8F8F2 !important;
    font-family: Georgia, Times, serif !important;
    font-size: 15px !important;
    line-height: 22px !important;
    padding-top: 10px !important;
  	text-decoration: none !important;
}
.pagetop {
    font-family: Georgia, Times, serif !important;
    font-size: 12pt !important;
    color: #222222 !important;
}
.dead {
    font-family: Georgia, Times, serif !important;
    font-size: 9pt !important;
    color: #dddddd !important;
}
i {
    font-style: italic !important
}
u {
    text-decoration: none !important;
    font-size: 12px !important;
    margin-top: -10px !important;
}
.default p {
    margin-top: 2px !important;
    margin-bottom: 0px !important;
    padding-bottom: 5px;
    padding-top: 15px !important;
    text-rendering: optimizeLegibility !important;
}
.pagebreak {
    page-break-before: always !important
}
pre {
    overflow: auto !important;
    padding: 2px !important;
    max-width: 600px !important;
}
pre:hover {
    overflow: auto !important
}
.votearrow {
  	background: none;
	width: 0;
    height: 0;
    border-left: 5px solid rgba(0, 0, 0, 0);
    border-right: 5px solid rgba(0, 0, 0, 0);
    border-bottom: 10px solid #F8F8F2;    
}
  
.votearrow:hover {
	border-bottom: 10px solid #D0D0D0;
}

}
