css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
I can't upload any regex values, how do I escape in css? or what is valid for this site? 
@-moz-document regexp("^https?:\/\/www\\.autohotkey\\.com/board/(?index\\.php|forum).*")
*/

@-moz-document url-prefix('http://www.autohotkey.com/board/index.php'),
url-prefix('http://www.autohotkey.com/board/forum/')
{
.wrapper {
max-width: 95%!important;
min-width: 800px!important;
}

/*search within topics*/
.ipsBox_container{text-align:center!important;}

.ipsBox_container > .ipsBox{
margin: 0 auto!important;
max-width: 800px!important;
min-width: 600px!important;
}

.ipsBox_container > .ipsBox *{
text-align: left!important;
}

.ipsBox_container .searchlite {
background-color: #6BACE4!important;
color: #FFFFFF!important;
padding: 0 3px!important;
margin: 0 -3px!important;
font-size: inherit!important;
text-shadow: 0 0 1px white;
border-radius: 2px;
}

.ipsLayout_left{
float:right!important;
margin-top:-50px!important;
visibility: hidden!important;
}

.ipsLayout:hover .ipsLayout_left{
visibility: visible!important;
}

.ipsLayout.ipsLayout_withleft {
padding-left:0!important;
padding-top:0!important;
}

#secondary_navigation, .ipsLayout_left *, table.ipb_table td{
padding: 0!important;
margin: 0!important;
height: auto!important;  
border: none!important;
}

#secondary_navigation *{
line-height:inherit!important;
}

#secondary_navigation > *, table.ipb_table td{
padding: 2px !important;
}

#secondary_links, #secondary_navigation a{
height: 20px!important;
}

.ipsLayout_left h4{
display:none!important;
}

.ipsSideMenu ul li.active a{
background: none repeat scroll 0 0 #55728B!important;
}

table.ipb_table td
{
padding: 0 5px!important;
}

.col_f_views, .col_m_replies, .col_f_post {
white-space: normal!important;
width: auto !important;
}

.col_f_post li{
}

.last_post{
color:rgb(200, 230, 255)!important;
}

#content {
line-height: 100% !important;
}

table.ipb_table, #secondary_navigation, #forum_table h4, 
.ipsType_pagedesc, .last_post, .col_c_stats, .col_f_views, 
.line_height, .ipsList_withminiphoto .list_content{
line-height: inherit !important;
}

#forum_table h4, .col_f_content > a{
float:left!important;
max-width: 80%!important;
}

.col_f_views, .col_m_replies {
text-align: left!important;
}

.col_f_views .ipsBadge{
margin-left: -38px!important;
padding: 0 4px!important;
}

.col_f_content .ipsBadge{
margin: 0 5px!important;
}

#forum_table > tbody > tr > td:nth-of-type(2) span.blend_links, 
#forum_table > tbody > tr > td:nth-of-type(2) .mini_pagination{
line-height: 150% !important;
float: right!important;
margin-left: 6px!important;
}

#forum_table > tbody > tr > td:nth-of-type(1) > span[title="This topic is locked"]{
margin-left: -22px!important;
}

#forum_table > tbody > tr > td:nth-of-type(2) .mini_pagination{
display: inline-block!important;
clear: both!important;
width: auto!important;
}

#pu_____hover___member_inner{
width: auto!important;
}

span.toggle_notify_off br, #forum_table > tbody > tr > td br{
display:none!important;
}

}


@-moz-document url-prefix('http://www.autohotkey.com/board/topic/'),
url-prefix('http://www.autohotkey.com/board/user/'){
.wrapper {
max-width: 800px!important;
min-width: 600px!important;
}
}

@-moz-document url-prefix('http://www.autohotkey.com/board/'){

.ipsBox_container, .ipsFilterbar{
margin-bottom:15px!important;
}

.signature, .col_f_post .ipsUserPhotoLink{
display:none!important;
}

.col_f_post .last_post{
margin:2px !important;
}

#user_navigation, #logo>a>img, li.disabled, .post_block {
margin-top: 0!important;
margin-bottom: 0!important;
}

pre {
margin-top: 4px!important;
margin-bottom: 4px!important;
}

body.fixedHeader {
padding-top: 70px!important;
}

#community_app_menu > li > a, #search *:not(#main_search), .author_info, .entry-content, .pagination, .topic_controls, .topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton, a.ipsButton {
line-height: 125% !important;
height: auto!important;
}

#forum_table .__topic > td, #content > .ipbfs_titlebox, .pagination, .horizontalInfoList, .post_body, .post, .horizontalInfoList li {
padding-top: 0!important;
padding-bottom: 0!important;
}

.maintitle, .notnew > td, .citation, .ipsBlockquote, .author_info {
padding-top: 4px!important;
padding-bottom: 4px!important;
}

.topic_controls {
min-height: 22px!important;
}

.horizontalUsername, .post_body > div.clearfix {
display: inline-block!important;
clear: none!important;
float: left!important;
margin-right: 20px!important;
}

.post_controls{
display: inline-block!important;
clear: none!important;
float: right!important;
border-top: 0!important;
padding: 0!important;
opacity: .01!important;
/*transition: <property> <duration> <timing-function> <delay>;*/
transition: opacity 2s linear 4s!important; 
}

.post_block:hover .post_controls{
opacity: 1!important;
transition: opacity .8s linear .2s!important; 
}

.post_controls > li > a, .postInfoBottom {
background: none repeat scroll 0 0 rgb(100, 160, 240)!important;
color: #ffffff !important;
height: 23px!important;
line-height: 18px!important;
padding: 0 4px!important;
margin-right: 2px!important;
margin: 3px 1px 3px 0 !important;
border-radius: 4px!important;
}

.post_controls > li > a:hover{
background-color: rgb(132, 179, 255)!important;
}

a.userNavPhoto:after, a.ipsUserPhotoLink:after {
box-shadow: none!important;
}

.author_info{
margin-top: 4px!important;
border: 1px solid rgb(100, 160, 240)!important;
background: none repeat scroll 0 0 rgb(227, 240, 255)!important;
box-shadow: inset 0 0 2px #777777!important;
border-radius: 6px!important;
}

.author_info:hover{
box-shadow: inset 0 0 4px #5B65BF!important;
transition: box-shadow .5s linear 0s!important; 
}

.ipbfs_titlebox{
border: none!important;
}


.author_info:hover, .postInfoBottom, .post_controls > li > a{
border: 1px solid rgb(57, 97, 240)!important;
}

.maintitle, .postInfoBottom > span, .post_controls > li > a, .horizontalUsername {
font-size: 110%!important;
font-weight: bold!important;
box-shadow: none!important;
}

.group_title > span{
color: red !important;
}

.postInfoBottom, .post_controls {
visibility: visible!important;
}

.postInfoBottom a {
padding-left: 8px!important;
color: black!important;
}

#ips_Posts .horizontalInfoList {
font-size: 100%!important;
font-weight: normal!important;
width: 300px!important;
}

#ips_Posts .horizontalInfoList *, .postInfoBottom > span * {
font-size: inherit!important;
font-weight: inherit!important;
}

#ips_Posts .ipsUserPhoto_large {
max-height: 40px!important;
max-width: 40px!important;
}

.statistics, #footer {
line-height: 100%!important;
margin: 0!important;
padding: 0!important;
overflow: hidden;
}
}
