css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {

/*--------------------THEME MADE BY spookmycraic.tumblr.com AKA FAYE
DO NOT REDISTRIBUTE
DO NOT USE AS A BASE CODE
DO NOT TAKE PARTS OF MY THEME TO USE
FOLLOW THE RULES OR THERE WILL BE CONSEQUENCES
------------------------*/

body {

  background: #000000 url("http://i1286.photobucket.com/albums/a604/emmasae-payne/kjhfdhfghjlkjhhgfchgvhjkjk_zps0750fc01.jpg") bottom right fixed no-repeat !important; 
}



.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
  display: none !important; 
}



  .help_text, label {
color:#c9c9c9 !important;
}
  .vertical_tab.selected {
color:#000 !important;
        text-shadow: 1px 5px 1px #000000 !important;
}




/* --- SCROLLBAR ---*/
     
::-webkit-scrollbar-thumb{
background-color: #ddccdd;
border: 2px solid #ffffff;
height:auto;
}
 
::-webkit-scrollbar {
height:9px;
width:9px;
background-color: #ddccdd;
border: 4px solid #fff;
}

::selection {
background-color:#f9f9f9;
}







.search_form_row  {
background: transparent !important;
color: #fff !important;
  }







#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #fff !important; 
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Followers" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Draft" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}











#right_column:after { 
display: none !important; 
}


.new_post_label/*_icon*/ > I {
display: none !important
}
  
  
.new_post_label { 
  padding-top: 75px !important; 
  color: rgba(0,0,0,0.5) !important; 
}
    
    
.new_post_label:hover { 
      color: rgba(204,204,221,0.9) !important; 
}
        
        
#new_post {
background: #fff url(http://static.tumblr.com/kuehu0n/paOmr3g9a/liam_payne_bw.jpg) no-repeat center top;
      padding: 10px !important; 
}
        
        
#new_post:hover {
background: #fff url(http://static.tumblr.com/kuehu0n/XR8mr3gco/liam_payne.jpg) no-repeat center top; 
      padding: -20px;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: transparent !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(236,191,201, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(236,191,201, 0.50)  !important;
}




.mceContentBody {
  background: url("http://i1286.photobucket.com/albums/a604/emmasae-payne/kjhfdhfghjlkjhhgfchgvhjkjk_zps0750fc01.jpg") !important; 
  background-size: 75% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}
#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes,
.selected .activity  {
  color: rgba(255,255,255, 0.50) !important; 
  background: transparent !important; 
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -1px !important; 
}

}
