/* Terminal Theme for IRCCloud mod, version 1.5 | by Specimen <spinifer at gm ail dot com> | licensed under the MIT License <http://specimen.mit-license.org> | Based on | Terminal Theme for IRCCloud, version 2.0 | by Ad@m <http://adam.hbang.ws> | licensed under the MIT License <http://adam.mit-license.org> */

@-moz-document url-prefix("https://irccloud.com/"), url-prefix("https://www.irccloud.com/"), url-prefix("https://alpha.irccloud.com/") {
/*Lines where the placeholders for the modifications over the original Terminal Theme can be found: 20, 28, 76, 148, 435, 497, 515, 524, 562*/

	::selection {
		background: #9a9a9a !important;
		color: #fff !important;
	}

	div.viewport, .connectionstatuscell {
		background: #000 !important;
	}

	body, body.app, div#sidebar {
		background: #000 !important;
	}

	div.log span.date, td.timecell {
		color: #eaeaea !important;
	}

/* Change IRCCloud Logo colours */

	span.logotype {
		color: #00afff !important; /* #4b8eff */
	}

	span.cloud {
		color: #0000ff !important; /* #4165a8 */
	}

/* */
/* */
	body, #headercell, #statusActions, #statusActions b, #statusActions strong, div.log, div.log div.chat, div.log div.status, div.log div.notice, div.log div.groupedLines, div.log div.warning, div.log div.error, div.log div.table, div.log div.me, div.log div.hover span.date, .contextMenu ul.actions li a, .bufferContextMenu.active ul.actions li a, .bufferContextMenu ul.actions li a.delete, #channelContextMenu.mod ul.actions li a.topic, #channelContextMenu.mod ul.actions li a.topic:hover, .contextMenu a, .accountContainer, h1#title span.beta, div.log div.lastSeen span.text, div.log div.bottomSeen span, div.log div.focusSeen span, div.log div.fetch span.text, td.nickcell a.nick,  td.inputcell, td.inputcell textarea, .form .input, #buffers p.archiveToggle a:hover, p#addNetwork a, p#addNetwork a:hover, p#addNetwork.selected a, p#addNetwork.selected a:hover, #headercell a, h1#title a, td.statuscell div.status p.buttons button span, .overlaycontainer button.confirm span, .overlaycontainer button.close span, button.action span, button span, button a, td.statuscell div.status p.buttons button.active span, .overlaycontainer button.confirm.active span, .overlaycontainer button.close.active span, button.action.active span, button span:hover, button a:hover, button:hover span, button:hover a, button.action:hover span, button.action:hover a, div.log div.groupedLines, #inviteForm h1, .explanation, .mainForm, .mainContent, div.mainContent a, table.shortcuts, .mainOverlayContent {
		color: #fff !important;
	}

/* */
	ul.memberList a {
    	color: #fff;
	}


/* Moding the self messages bg so it's darker */

	.viewport .self {
		background: #121212 !important;
	}

/* Setting the font sizes */

	div#statusActions { /* The top menu */
  		font-size: 11px !important;
	}

	div#settingsContainer, div.mainOverlayContent, div.accountContainer, div.accountContainer h2, div.contextMenu, div.bufferContextMenu { /* The Dialog Boxes */
  		font-size: 12px !important;
	}

	#sidebar {
  		font-size: 12px !important;
	}

	#footer {
  		font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  		font-size: 10px !important;
  		color: #aaa !important;
	}

	#footer a {
  		color: #777 !important;
	}

	div.viewport, .mono, input, textarea, button, select, div.shim, td.nickcell a.nick, td.inputcell textarea, div.log div.messageRow, div.log div.me a.user, td.statuscell div.status h2.bufferHeading span.label, span.topic, ul.memberList, td.nickcell a.nick, td.inputcell textarea, div.log div.messageRow, div.log div.me a.user, div.log div.me a.server, h1#title span.logotype span.beta, .form .input, div.log div.monospace span.message, #channelContextMenu ul.modeList {
		font-family: "SourceCodePro-ExtraLight", "SourceCodePro-Light", "Source Code Pro", Inconsolata, Menlo, "Ubuntu Mono", Consolas, monospace !important;
  		font-size: 12px !important;
	}
	
	body.app, div#channelContextMenu ul.modeList li {
  		font-size: 12px !important;
		font-family: "SourceCodePro-ExtraLight", "SourceCodePro-Light", "Source Code Pro", Inconsolata, Menlo, "Ubuntu Mono", Consolas, monospace !important;
	}

/* Changing the bg colours of the changelog */

	table.changelog tr {
		background-color: black !important;
		font-family: "SourceCodePro-ExtraLight", "SourceCodePro-Light", "Source Code Pro", Inconsolata, Menlo, "Ubuntu Mono", Consolas, monospace !important;
  		font-size: 12px !important;
	}

/* */

	::-webkit-scrollbar-track-piece, ::-webkit-scrollbar-thumb, * {
		border-radius: 0 !important;
		-webkit-border-radius: 0 !important;
		-moz-border-radius: 0 !important;
		box-shadow: 0 !important;
		-webkit-box-shadow: none !important;
		-moz-box-shadow: none !important;
		border-top-left-radius: none !important;
		-webkit-border-top-left-radius: 0 !important;
		-moz-border-radius-topleft: 0 !important;
		border-top-right-radius: 0 !important;
		-webkit-border-top-right-radius: 0 !important;
		-moz-border-radius-topright: 0 !important;
		border-bottom-left-radius: 0 !important;
		-webkit-border-bottom-left-radius: 0 !important;
		-moz-border-radius-bottomleft: 0 !important;
		border-bottom-right-radius: 0 !important;
		-webkit-border-bottom-right-radius: 0 !important;
		-moz-border-radius-bottomright: 0 !important;
	}

	h1#title span.logo, body.disconnected h1#title span.logo {
		background-image: url("data:image/png;base64,") !important;
		background-color: #333 !important;
		background-size: 18px 14px !important;
		background-position: center center !important;
		width: 24px !important;
		height: 24px !important;
		margin: 0 4px 0 8px !important;
	}

	body.disconnected h1#title span.logo {
		background-color: #ac6f00 !important;
	}

/* This is where there was a duplicate of the IRCCloud logo */

	ul.memberList, div.memberwrapper {
		background: #000 !important;
		border-width: 0 !important;
	}

	ul.memberList li h2, ul.memberList li h2 span.memberCount {
		border-width: 0 !important;
	}

	ul.memberList li.owner ul, ul.memberList li.owner h2 {
		background: #454545 !important;
	}

	ul.memberList li.admin ul, ul.memberList li.admin h2 {
		background: #343434 !important;
	}

	ul.memberList li.ops ul {
		background: #232323 !important;
	}

/* */
	ul.memberList li.ops h2 {
	    color: #999 !important;
    	background-color: #232323 !important;
	}

/* */
	ul.memberList li.ops h2 span.memberCount {
    	color: #999 !important;
	}

	ul.memberList li.halfops ul, ul.memberList li.halfops h2 {
		background: #121212 !important;
	}

	ul.memberList li.voiced ul, ul.memberList li.voiced h2 {
		background: #010101 !important;
	}

	ul.memberList li.members ul, ul.memberList li.members h2 {
		background: #000 !important;
	}

/* */
	ul.memberList li h2, ul.memberList li h2 span.memberCount {
    	color: #666 !important;
	}

	ul.memberList li ul {
		border-color: #555 !important;
	}

	ul.memberList li h2 {
		font-weight: bold !important;
	}

/* */
	ul.memberList li.user a.away {
    	color: #aaa !important;
	}

	.bufferHead {
		background-color: #333 !important;
		border-width: 0 !important;
	}

	td.statuscell div.status h2.bufferHeading span.label, #buffers .active a.buffer {
		color: #efefef !important;
	}

	td.statuscell div.status h2.bufferHeading {
		color: #eee !important;
	}

	div.log div.status {
		background: #565656 !important;
	}

	div.log span.message a.bufferLink.user {
		color: #dedede;
	}

	div.log span.message a.bufferLink.user:hover {
		color: #ededed !important;
	}

	div.log div.lastSeen span.text, div.log div.bottomSeen span, div.log div.focusSeen span, div.log div.fetch span.text {
		background: #111 !important;
	}

/* Highlight mod */

	div.log div.highlight {
		background: #3d0000 !important;
	}

	div.log div.highlight span.date {
		color: #f00 !important;
	}

/* */

	div.log div.groupedJoinPart {
		background: #494949 !important;
		border-color: #767676 !important;
	}

	div.log div.groupedJoinPart:hover, div.log div.groupedJoinPart.expanded {
		background: #767676 !important;
		border-color: #878787 !important;
	}

	div.log div.groupedJoinPart.expanded:hover {
		background: #878787 !important;
		border-color: #8a8a8a !important;
	}

	div.log span.prefix {
		color: #bbb !important;
		font-family: arial, sans-serif !important;
	}

	div.log div.dateChange p, div.dateWrapper table, div.dateWrapper td {
		border-width: 0 !important;
		color: #212121 !important;
		background: #bababa !important;
	}

	div.log div.dateChange p {
		padding-bottom: 5px !important;
	}

	div.log div.pending {
		opacity: 0.85 !important;
	}

	div.log div.pendingError {
		background: #fda400 !important;
	}

	div.log div.warning {
		background: #252525 !important;
	}

	td.bufferinputcell {
		background: #010101 !important;
	}

	td.nickcell {
		background: #000 !important;
		border-width: 0 !important;
	}

	div.nickinputwrapper {
		border-width: 0 !important;
		background: #000 !important;
	}

	.contextMenu, .bufferContextMenu, .accountContainer {
		border-color: #767676 !important;
		background-color: #494949 !important;
	}

	.contextMenu ul.actions li a:hover, .bufferContextMenu.active ul.actions li a:hover, .bufferContextMenu ul.actions li a.delete:hover, #channelContextMenu.mod ul.actions li a.topic:hover, #memberContextMenu div.userContext {
		background-color: #676767 !important;
	}

	#channelContextMenu ul.modeList {
		color: #aaa !important;
	}

	#channelContextMenu ul.actions li.showMembers a, .bufferContextMenu ul.actions li.trackUnread a, .bufferContextMenu ul.actions li.showJoinPart a {
		background-image: url("data:image/png;base64,") !important;
		background-size: 13px 10px !important;
	}

	#channelContextMenu .modeLine, #memberContextMenu ul.actions {
		border-top: 1px solid #767676 !important;
	}

	.contextMenu .info {
		color: #ddd !important;
	}

	.extras span.extrasBar {
		background: #1010ef !important;
		color: #fafafa !important;
	}

	.extras span.extrasBar:hover {
		background: #2020d0 !important;
		color: #dcdcdc !important;
	}

	.extras span.extrasDismiss {
		background: #0055ff !important;
	}

	.extras span.extrasDismiss:hover {
		background: #0090ff !important;
	}

	td.inputcell, td.inputcell textarea, .form .input {
		background: #121212 !important;
	}

	.form .input {
		border: 1px solid #444 !important;
	}

	.form .input:focus {
		border: 2px solid #222 !important;
	}

	#buffers h2 a.join {
		background: url("data:image/png;base64,") center center no-repeat !important;
		opacity: 0.8 !important;
	}

	#buffers h2 a.join:hover {
		background-position: center center !important;
		opacity: 1 !important;
	}

	div.log div.groupedJoinPart span.mode {
		color: #9e9e9e !important;
	}

	#buffers h2 a.buffer {
		background-color: #464646 !important;
/*		background-image: none !important;   This disables ssl status images   */
		color: #eee !important;
		font-weight: bold !important;
		border-width: 0 !important;
	}

	#buffers h2 a.buffer:hover {
		background-color: #373737 !important;
	}

	table.buffer {
		border-width: 0 !important;
	}

	::-webkit-scrollbar-thumb {
		background: #313131 !important;
		border-width: 0 !important;
	}

	::-webkit-scrollbar-track-piece {
		background: #646464 !important;
		border-width: 0 !important;
	}

	::-webkit-scrollbar-thumb:inactive {
		background: #535353 !important;
	}

	::-webkit-scrollbar-track-piece:inactive {
		background: #868686 !important;
	}

	.sidebarIndicator, .sidebarIndicatorBadged {
		background: #1010ef !important;
		border-width: 0 !important;
	}

	.sidebarIndicatorBadged {
		background: #f00000 !important;
	}

/* */
	#buffers .selected a.buffer, #buffers .selected a.buffer:hover, #buffers .active.selected a.buffer, #buffers .active.selected a.buffer:hover {
		background-color: #000080 !important;
		border-width: 0 !important;
	}

	#buffers li.connection {
		background: #222 !important;
	}

	#buffers li.connection.disconnected {
		opacity: 0.75 !important;
	}

	#buffers a.buffer {
		color: #eee !important;
	}

	#buffers a.buffer:hover {
		background-color: #757575 !important;
	}

/* */
	#buffers ul.buffers .active.unread a.buffer, #buffers h2.active.unread {
		border-left-color: #0000ff !important;
	}

	#buffers p.archiveToggle a:hover {
		background: #858585 !important;
	}

	p#addNetwork a, p#addNetwork a:hover, p#addNetwork.selected a, p#addNetwork.selected a:hover {
		background: transparent !important;
		padding: 0 4px !important;
		margin: 0 !important;
		border-width: 0 !important;
	}

	p#addNetwork.selected a, p#addNetwork.selected a:hover {
		background: #333 !important;
	}

	p#addNetwork a:hover, #headercell a:hover, h1#title a:hover, #footer a:hover {
		color: #bbb !important;
	}

	div.log span.author a, div.log div.me span.message a.author, td.nickcell a.nick {
		color: #ddd !important;
	}

	button b.bullet {
		color: #eee !important;
	}

	.overlay_container_prompt .overlayHead {
		background-color: #181818 !important;
		border-color: #555 !important;
	}

	td.statuscell div.status p.buttons button span, .overlaycontainer button.confirm span, .overlaycontainer button.close span, button.action span, button span, button a, td.statuscell div.status p.buttons button.active span, .overlaycontainer button.confirm.active span, .overlaycontainer button.close.active span, button.action.active span, button span:hover, button a:hover, button:hover span, button:hover a, button.action:hover span, button.action:hover a {
		border-color: #121212 !important;
		background: #121212 !important;
	}

	#sidebar #limits {
		background: #333 !important;
		color: #efefef !important;
	}

	#limits p#upgradeLink a {
		background: #85de85 !important;
		color: #000 !important;
	}

	#limits p#upgradeLink a:hover {
		background: #90ef90 !important;
		color: #222 !important;
	}

	#buffers ul.archives {
		background: #565656 !important;
	}

	#buffers ul.archives a:hover {
		background: #777 !important;
		color: #ccc !important;
	}

	#buffers p.openArchiveToggle a, #buffers p.openArchiveToggle a:hover {
		background: #777 !important;
		color: #eee !important;
		font-weight: bold !important;
	}

	.viewport .overlaycontainer {
		background: #555 !important;
		border-width: 0 !important;
	}

	div.log div.notice, div.log div.joinPartGroup.expanded div.messageRow {
		background: #444 !important;
	}

	div.log div.groupedLines {
		background: #636363 !important;
		border-width: 0 !important;
	}

/*Links in chat Mod*/

	a, div.log div.chat a, div.log div.status a, div.log div.notice a, div.log div.groupedLines a, div.log div.warning a, div.log div.error a, div.log div.table a, div.log div.me a {
		color: #005fff;
	}

/*End of Links in chat Mod*/

	form.addNetworkForm, .mainContainer {
		background: #000 !important;
		border-color: #000 !important;
	}

	body.disconnected, body.disconnected td#headercell, body.disconnected div#sidebar {
		background: #fda400 !important;
	}

	.overlayTable tr.even td {
		background: #767676 !important;
	}

	.overlay table thead th, .overlay table thead td {
		background: #424242 !important;
	}

	.overlay .heading h2 {
		font-weight: bold !important;
	}

	.overlay table td, .overlay table th {
		border-left-color: #252525 !important;
	}

	#sidebar .info {
		background: #212121 !important;
	}

	#sidebar .info p.button, #sidebar .info p.more {
		background: #343434 !important;
	}

	#sidebar .info p.form .input, #sidebar .info p.form .input:focus {
		border-color: #555 !important;
	}

	form.addNetworkForm h2 span, form.addNetworkForm h2 a {
		background-color: #000 !important;
	}

	.mainOverlayContent {
		background: #222 !important;
	}

	table.shortcuts kbd {
		color: #ccc !important;
	}

	table.shortcuts kbd span {
		color: #aaa !important;
	}


/*Theming the trial box, this is only intended to make it match the rest of the theme not to hide it*/

	#sidebar #limits a{
		background-color: #313131 !important;
	}

	#sidebar #limits h2.limitedTrial {
		border-color: #3d0000 !important;
		background-color: #3d0000 !important;
  		font-size: 12px !important;
	}

	#sidebar #limits .price .badge{
		background-color: #3d0000 !important;
		width: 45px !important;
		height: 45px !important;
		line-height: 45px !important;
		border-radius: 50% !important;
  		font-size: 12px !important;
	}

/*End of trial box theming mod*/

/*Theming the buttons on hover*/

	button:not([disabled]):hover span, button:not([disabled]):hover a {
    	border-color: #eee #aaa #aaa #eee !important;
    	background-color: #999 !important;
    	background-image: -webkit-linear-gradient(top, #444, #999) !important;
    	background-image: linear-gradient(to bottom, #444, #999) !important;
  	}
  
/* Theme Pastebin */

    div.paste {
        border: 1px solid #000 !important;
        background: #1c1c1c !important;
    }

    div.paste h1 {
        background: #1c1c1c !important;
        border-bottom: 1px solid #000 !important;
    }

    div.paste span.info {
        color: #fff !important;
    }
    div.paste pre.body ol li {
        background-color: #808080 !important;

    }
    div.paste p.truncated a {
        border-top: 1px solid #000 !important;
    }

    div.paste pre.body ol.linenums li.L1,
    div.paste pre.body ol.linenums li.L3,
    div.paste pre.body ol.linenums li.L5,
    div.paste pre.body ol.linenums li.L7,
    div.paste pre.body ol.linenums li.L9 {
        background-color: #8c8c8c !important;
    }

    div.paste.nocode pre.body span {
        color: #000 !important;
        font-family: "Source Code Pro", Inconsolata, Menlo, "Ubuntu Mono", Consolas, monospace !important;
        font-size: 12px !important;
    }

    div.paste pre.body span {
        font-family: "Source Code Pro", Inconsolata, Menlo, "Ubuntu Mono", Consolas, monospace !important;
        font-size: 12px !important;
    }

/* Make day separators darker */

    div.log div.dateChange p, div.dateWrapper table, div.dateWrapper td {
        background: #888888 !important;
    }

/* */
}
