css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gpforums.co.nz") {

/* ---- GENERAL ---- */

body { background: #0F0F0F none !important; color: #8FA1B3 !important  }
.body_wrapper { background: #0F0F0F none !important }
a { text-decoration: none !important }

#pagetitle h1,
    #pagetitle h1 a { color: #eeeeee !important }

.navbit a { color: #8A9199 !important }
.navbit a:hover { color: #EEEEEE !important }

#forumdisplaypopups a:hover { color: #EEEEEE !important }

/* ------ SIDEBAR -------- */

.widget_post_bit .widget_post_content  { background: #333333 none !important; color: #dddddd !important }
.widget_post_bit .widget_post_content .post_bubble_arrow { display: none !important }
.widget_post_comment .date .time { color: #8A9199 !important }

#page_sidebar .sidebar_block { border-bottom: 1px solid #8FA1B3 !important }

#block_html_3 .sidebar_mm_block { background: none repeat scroll 0 0 #8FA1B3 !important }

/* ------ PAGINATION -------- */
dl.pagination dd a, 
    div.threadpagenav a,
    div.pagination_top a,
    div#pagination_top a,
    div.pagination_bottom a,
    div#pagination_bottom a { color:#c1c2c9 !important; background-color:#0f0f0f !important; border-color:#333333 !important; }

dl.pagination dd a.selected, 
    div.threadpagenav a.selected,
    div.pagination_top a.selected,
    div#pagination_top a.selected,
    div.pagination_bottom a.selected,
    div#pagination_bottom a.selected { background-color: #333333 !important; color: #c1c2c9 !important; }

/* ------ FILTER BOX -------- */
.forum_info_form_block .forum_info_form { /*background-color:#333940 !important;*/ background-color: #333333 !important; }

/* ------ SEARCH -------- */
div#pagetitle p.description a { color: #b1b3bc !important; }

/* ------ PROFILE -------- */
div.profile_content div.blockbody,
    dl.stats dt { background-color: #333333 !important; color: #b1b3bc !important; }

div.profile_content div.blockbody dl.stats dd a,
    div.profile_content div.blockbody ul.group li a { color: #8A9199 !important }
div.profile_content div.blockbody dl.stats dd a:hover,
    div.profile_content div.blockbody ul.group li a:hover { color: #EEEEEE !important }

/* ------ TOPIC/THREAD LIST -------- */

table.threadbit,
    table.forumbit { background: #0F0F0F none !important; border: none !important }

.forumbit a,
    .forumbit a .time { color: #8A9199 !important }
.forumbit a:hover,
    .forumbit a:hover .time { color: #EEEEEE !important; text-decoration: none !important }

.threadbit a.title,
    .threadbit a.username,
    .threadbit a.lastposticon { color: #8A9199 !important }

.threadbit a.title:hover,
    .threadbit a.username:hover,
    .threadbit a.lastposticon:hover,
    .threadbit a.lastposticon:hover .time { color: #EEEEEE !important; text-decoration: none !important }

/* ------ PROFILE  -------- */

#activitylist .activitybit { background: #0F0F0F none !important; border: none !important; color: #8A9199 !important; }

#activitylist .activitybit a,
    #usermenu li a,
    .profile_header .albumslist .textcontrol,
    dl.tabslight dd a { color: #8A9199 !important }
#activitylist .activitybit a:hover,
    #usermenu li a:hover,
    .profile_header .albumslist .textcontrol:hover { color: #EEEEEE !important }

dl.tabslight dd.selected a { background-color: #333333 !important; border-top-color: #111111 !important; border-left-color: #111111 !important; border-right-color: #111111 !important; }
dl.tabslight dd.selected a:hover { background-color: #555555 !important; border-top-color: #111111 !important; border-left-color: #111111 !important; border-right-color: #111111 !important; }
dl.tabslight dd a:hover { color: #EEEEEE !important; background-color: #333333 !important; border-top-color: #111111 !important; border-left-color: #111111 !important; border-right-color: #111111 !important; }

#activity_tab_container div { background-color: #aaaaaa !important; }

/* ------ THREAD CONTENT -------- */

.postbit { background: #0F0F0F none !important }
.postbit .postbody { background: #333333 none !important; color: #dddddd !important }

.postbit .postbody a { color: #8A9199 !important }
.postbit .postbody a:hover { color: #EEEEEE !important }

.postbit a.username { color: #aaaaaa !important; }
.postbit a.username:hover { color: #dddddd !important; }

.postbit .postmain { margin-left: 125px !important }

.bbcode_container .bbcode_quote,
    .bbcode_container .bbcode_code { background: #222222 none !important  }
    
.bbcode_container .bbcode_quote strong.username { color: #aaaaaa !important  }

.lastedited, .lastedited .time { color: #a1a1a1 !important }

#threadrating a, .thread_tags a { color: #8A9199 !important }
#threadrating a:hover, .thread_tags a:hover { color: #EEEEEE !important }

.postbody_bubble, .quote_bubble{ display:none;}

/* --- END --- */

}
