css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://xianguo.com/reader") {


/**
 * 鲜果阅读器 clean style
 * Ethan Chen
 * 2013-12-17
 */

/* 所有滚动的地方 */
:hover::-webkit-scrollbar-thumb { background-color:rgba(0,0,0,0.2);  } 
:hover::-webkit-scrollbar-track { background-color:rgba(0,0,0,0.02); box-shadow:0 0 3px rgba(0,0,0,0.1) inset; } 
::-webkit-scrollbar{ width:6px; height:6px; }
::-webkit-scrollbar-track{ background-color:rgba(0,0,0,0);  }
::-webkit-scrollbar-track:hover{ }
::-webkit-scrollbar-track:active{ background-color:rgba(0,0,0,0.1) }
::-webkit-scrollbar-thumb{ border-radius:6px; background-color:rgba(0,0,0,0) }
::-webkit-scrollbar-thumb:hover{background-color:rgba(0,0,0,0.2)}
::-webkit-scrollbar-thumb:active{background-color:rgba(0,0,0,0.4)}
/* 滚动带阴影盒子，仿GR */
#Sublist {    
	background:
	/* Shadow covers */
	-webkit-linear-gradient(#EFEFF4 30%, rgba(255,255,255,0)),
	-webkit-linear-gradient(rgba(255,255,255,0), #EFEFF4 70%) 0 100%,
	/* Shadows */
	-webkit-radial-gradient(50% 0, farthest-side, rgba(0,0,0,0.1), rgba(0,0,0,0)),
	-webkit-radial-gradient(50% 100%,farthest-side, rgba(0,0,0,0.2), rgba(0,0,0,0)) 0 100%;
	background-repeat: no-repeat;
	background-color: white;
	background-size: 100% 30px, 100% 30px, 100% 10px, 100% 13px;
	/* Opera doesn't support this in the shorthand */
	background-attachment: local, local, scroll, scroll;
}
/* 多余的隐藏 */
#header,
#header + div[style],
#contextPannel>a{ display:none}
/* 统一颜色 */
#spliter,
#listPannelTop,
#itemList_header>div:first-child,
#Sublist { background-color:#EFEFF4 !important; }
/* 左右分割线 */
#spliter { margin-top:0; position: relative; background-image:none; }
#spliter:hover { background-color:#e5e5ee !important; }
#spliter:before { content: ''; display: block; border: 4px dotted transparent; position: absolute; top: 50%; }
.spliter_hide:before{ border-right-color: #666 !important; right:0; }
.spliter_expand:before { border-left-color: #666 !important; left:0; }
/* 订阅列表框 */
#listPannel_box { border-color:#EFEFF4 !important; }
#listPannelTop { border-bottom:1px solid #EFEFF4; }
#spliter:before { content:''; display:block; }
/* 内容框 */
.itemPannel_hr,
.item_bottom { background-color:#EFEFF4; }
.item_bottom .more_option,
.item_bottom .icon { color:#333}
.item .item_descr { font:18px/1.8 'Microsoft JhengHei',STHeiti, STXihei, 'Microsoft YaHei', sans-serif; padding-right:0; width:46em; overflow:visible;  }
.item .item_descr h1 {font-size:2em; font-weight:bold; }
.item .item_descr h2 {font-size:1.8em; font-weight:bold; }
.item .item_descr h3 {font-size:1.5em; font-weight:bold; }
.item .item_descr h4 { font-size:1.3em;  font-weight:bold; }
.item .item_descr strong { font-weight:bold; }

}
