@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.free-tv-video-online.me") {
#plot {
	padding: 5px 10px 10px 0 !important;
	color: #333!important;
	font: 12px/20px verdana!important
}
#vposter {
	margin: 0 14px 0 0!important;
	padding: 0 !important;
    -webkit-box-shadow: 0px 1px 2px #000 !important;  
}
#tooltip {
 	background-color: #000 !important;
  	border: 1px solid #333 !important;
  	color: #efefef !important;
  	padding: 8px !important;
    -webkit-box-shadow: 0px 3px 18px #000 !important;
    opacity: 0.84!important;
  	max-width: 240px !important;
}
#tooltip h3 {
 	font: normal 12px/12px Verdana !important;
  	padding-bottom: 3px;
  	color: #69EBFF !important;
  	text-transform: uppercase !important;
}
#tooltip .body {
	font: normal 11px/14px Verdana !important; 

}
a:link, a:visited {
	color: #000000!important;
	font: 12px verdana!important
}
a:hover {
	color: #479C4A !important;
}
a.date:hover, a.char:hover {
	background-color: #479C4A!important;
	border-color: #2E6630!important
}
a[style="font-family:Arial; width:283px; text-align:left; font-size:12px; color:black; display:block; padding-left:17px; background: transparent url(/img/shutoffbanner.png) no-repeat 1px 2px;"] {
	display: none !important
}
b {
	font-weight: normal !important
}
b[style] {
	padding-left: 5px !important;
	color: #000!important;
	font: bold 14px/20px verdana !important;
	text-transform: uppercase !important;
	font: normal 13px/30px verdana !important;
	margin: 10px 0 10px 0 !important;
}
a[name="#"]+b {
 	margin-top: 0px !important; 
}
body, html {
	width: 1054px!important;
	margin: 0 auto!important;
	padding: 0 !important;
	background-color: #1E1E20!important
}
body:before {
	display: block;
	height: 160px;
	width: 614px;
	margin: 0 auto !important;
	content: url(http://i.imgur.com/g2GrQ3z.png)
}
body+br, p+br {
	display: none !important
}
table {
	margin: 0!important;
	padding: 0!important;
	background-color: #fff!important;
	border-spacing: 0!important
}
html body table tbody tr td table[style][width="100%"] {
	border-bottom: 5px solid #178B91!important;
	border-top: 5px solid #1CABB2!important;
}
html body table tbody tr td div+table[width="100%"] {
	display: none !important;
}
html body table tbody tr td table[align][width][bordercolor] {
	border: none!important
}
html body table tbody tr td script+table[width="100%"][cellpadding="3"][border="0"] {
	display: none !important;
}

div[style="height:49px"]{
	display: none !important;	
}
div[style*="background-image"] {
	background-image: none!important;
	height: 0 !important;
}
div[style*="white-space"] {
	margin-bottom: 40px !important;
	text-transform: uppercase !important;
	border-top: 3px dashed #464A4A !important;
	background-image: none !important;
	background-color: #1E1E20 !important;
}
div[style*="white-space"] {
	font: 13px/40px verdana !important; 	 
}
div[style*="white-space"] a  {
	color: #fff !important;
	font: 13px/40px verdana !important;
  	margin: 0px !important;
}
div[style*="white-space"] a:hover {
	border-bottom: 2px solid #479C4A!important
}
div[style="padding: 5px"][align="center"] {
	padding: 0!important;
	color: #333!important;
	font: 0/0 verdana!important
}
div[style="padding: 5px"][align="center"] h1 {
	color: #fff!important;
	text-transform: uppercase!important;
	font: 14px/30px verdana!important
}
div[style="height:49px"]{
	display: none !important;	
}
div[style="padding:5px;"]{
 	padding: 0 !important; 
}
div[style*="padding:10px"]:before {
 	content:"Disclosure"; 
    font: bold 14px/14px Verdana !important;
    color: #2A2A2D !important;
    margin: 10px 0 6px 0px !important; 
    display: block !important;
}
div[style*="padding:10px"] {
 	font: 12px/18px Verdana !important;
  	width: 860px !important;
  	margin: 0 auto !important;
  	display: block !important;
    margin-top: 30px !important;
}
i {
	font-style: normal!important;
}
img[src*="images/triangle_bullet.gif"] {
	display: none!important
}
sub {
	margin: 0 !important;
	padding: 0 0 0 8px !important;
  	clear: none !important;
	font: normal 12px/0 verdana !important;
	color: #179ca3 !important;
  	vertical-align: super !important;
  	position: relative !important;
  	top: 0.454em !important;
}
table {
	max-width: 1054px!important
}
table+br {
 	display: none !important;
}table:first-child[width="100%"]
{
	border-top: 5px solid #1CABB2 !important;
}
table[align="center"] tbody tr td table[cellpadding="3"]+table[width][cellspacing="1"]:before {
  content: "SEASONS";
  font: bold 14px/14px Verdana !important;
  color: #2A2A2D !important;

  margin: 10px 0 0 10px !important; 
  display: block !important;
  
}
/*

body>table:nth-of-type(1)
{
    background-color: #1E1E20 !important;
    border-top: 3px dashed #464A4A !important
}
*/
td {
	margin: 0px!important;
	padding: 0px !important
}
td.schedule {
	max-width: 150px!important;
	padding: 0!important
}
td[height="44"] {
	height: 30px !important;
	background-color: #1E1E20!important;
	border-top: 3px dashed #464A4A !important; 
	border-bottom: 5px solid #1CABB2 !important;
}
td[width="50%"] {
	padding: 10px 10px!important;
	font: 12px/13px verdana !important;
	width: 33%!important;
}
td[valign="top"][style] {
	margin: 0!important
}
td[align][colspan="2"] {
	color: #fff!important;
	font: 0/0 verdana!important
}
td[style="padding:10px; font-size:9pt; font-family:Verdana;"] {
	padding: 10px!important
}
td[width="6"], img[height="6"] {
	display: none!important
}
td[height="44"] a:link, td[height="44"] a:visited {
	color: #fff!important
}
td[width="160"], td[width="12"], div[align="center"][style="background-color:#FFA628; height:125px"], html body table tbody tr td table[align][bgcolor="#FFFFFF"] tbody tr td table[cellpadding="4"] tbody tr:first-child, tr[valign="middle"], p[align="center"] {
	display: none!important;
	height: 0!important;
	width: 0!important;
}
td[align="center"][colspan="2"]{
 	background-color: #000 !important;
  	padding-bottom: 50px !important;
}
td[style="padding:14px;"]{
 	display: none !important; 
}
td.episode {
    font: bold 14px/14px Verdana !important;
    color: #2A2A2D !important;
    margin: 0!important; 
  	padding-left: 10px !important;
    display: block !important;
  	text-transform: uppercase !important;
}
td.episode b {
 	font-weight: bold !important;
}
th[width="50%"][scope].style3 {
	border-bottom: solid 15px #fff !important;  
	padding: 0px 0 3px 100px !important;
    text-align: left !important;
}
tr[valign="top"] td[nowrap] {
	display: none!important
}
.date, .char, a.date, a.char {
	margin: 10px 0px 10px 0 !important;
	padding: 4px 6px!important;
	background-color: #2a2a2d!important;
	color: #fff!important;
	font-weight: normal!important
}
.date:nth-of-type(1), .char:nth-of-type(1) {
	margin-left: 10px !important
}
.info {
	width: 15px !important;
    height: 15px !important;
    background-size: 100% auto !important;
    margin-top: 1px !important;
}
.footer {
	padding: 5px!important;
	border: none!important
}
.ftoday .smore {
	background-color: #178B91!important;
	color: #fff!important;
	border: 2px solid #16848A!important
}
.ftoday .smore:hover {
	background-color: #efefef!important;
	color: #333!important;
	border: 2px solid #fff!important
}
.mnlbreadcrumbs a {
	color: #fff !important;
	font: 13px/30px verdana !important
}
.mnlcategorylist {
	margin-bottom: 0 !important;
	padding: 0 !important;
	font: normal 12px/14px verdana !important;
    color: #179ca3  !important;
}
.mnlcategorylist a {
	height: 0 !important;
	margin: 0!important;
	padding: 0 !important;
	text-align: left !important;
	font: normal 12px/17px verdana !important
}
.mnlhighlightheading, .mnlbreadcrumbs {
	width: 100% !important;
	margin: 0!important;
	padding: 0!important;
	background-color: #2a2a2d!important;
	color: #fff!important;
	text-align: center!important;
	text-transform: uppercase!important;
	font: normal 13px/40px verdana !important
}
.mnllinklist {
	padding: 10px !important;
    border: none !important;
    border-bottom: dashed 1px #ccc !important;
}
.mnllinklist a {
	color: #000!important;
	font: bold 12px/20px verdana !important
}
.mnllinklist div:hover {
	color: #479C4A !important;
}    
.mnllinklist a:hover div {
	color: #479C4A !important;
}   
.mnllinklist span {
	color: #666!important;
	font-weight: normal !important;
    line-height: 15px !important;
    padding: 5px 0 !important;
}
.schedule {
	border: none !important;
}
.schedule a {
	font: 12px/18px verdana!important
}
.sdate {
	display: none!important;
	margin: 0!important;
	padding: 0 0 7px!important;
	color: #000!important;
	font: 12px verdana!important
}
.today span.sheader, .sdate {
 background-color: #1CABB2!important;
 text-transform: uppercase!important
}
.sepisodes {
	width: 130px!important;
	margin: 6px 10px 0!important;
	padding: 0!important;
	font: 12px verdana!important;
	overflow: none!important;
	text-overflow: ellipsis!important;
}
.sheader {
	background-color: #178B91!important;
	font-weight: 400 !important;
	text-transform: uppercase!important;
	margin: 0!important;
	padding: 7px 0!important;
  	font-size: 13px !important;
}

.smore {
	margin-top: 4px!important;
	padding: 4px 0!important;
	background-color: #efefef!important;
	text-decoration: none!important;
	border: 2px solid #dfdfdf!important
}
.smore:hover {
	background-color: #999!important;
	color: #fff!important;
	border: 2px solid #888!important
}
.style3 {
	margin-top: 15px!important;
	padding: 7px!important;
	background-color: #1E1E20!important;
	text-transform: uppercase!important;
	font: normal 13px/40px verdana!important
}
.style3 a {
	padding-bottom: 2px !important;
	color: #fff !important;
	font: normal 13px/30px verdana !important
}
.style3 a:hover {
	border-bottom: 2px solid #479C4A!important
}
.style3[style="background-color: rgb(255, 102, 0); padding-left:5px"] {
	display: none !important;
}
.tleft {
	padding: 0px 0 0px 100px !important;
    text-align: left !important;
  	border-right: 2px solid #efefef !important;
  	border-bottom: 1px dashed #efefef !important;
  	vertical-align: middle !important;
  	width: 200px !important;
}
.tleft a {
	font: 12px/26px verdana !important;
  	text-decoration: none !important;
  	text-transform: uppercase !important;
  	padding: 0px !important;
}
.today, .ftoday {
	background-color: #1CABB2!important;
	color: #fff!important;
	border-left: 2px solid #178B91!important;
	border-right: 2px solid #178B91!important
}
.today, .schedule {
	border-bottom: none!important;
	border-top: none!important;
}
.today .sheader {
	font-weight: normal !important;
	background: none!important
}
.today a, .today .sepisodes {
	color: #E3F4FA!important
}
.today a:hover {
	color: #fff!important;
    text-decoration: none !important;
    border-bottom: solid 2px #69EBFF!important;
}

}
