css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("4chan.org") {

/*Colors tables  bg etc*/

* {
font-family:"Tahoma", sans-serif !important;
font-size:16px !important;
-moz-appearance: none !important;
}

body {
background-color:#F8C295 !important;
}

.delform {
color:rgb(0,0,0) !important;
}

.pages {
background-color:transparent !important;
}

span.plus {
color:rgb(0,0,0) !important;
}

.hr {
color:rgb(0,0,0) !important;
}

.omittedposts{
color:rgb(0,0,0) !important;
}

table.blockquote {
position: absolute !important;
left: 200px !important;
}

blockquote{-moz-border-radius:12px !important;opacity:100 !important;background-color:rgb(255,230,190) !important;color:rgb(0,0,0)!important;
width:500px !important;
border:solid 0px rgb(0,0,0) !important;
margin-left:30px !important;
margin-right:5px !important;
margin-top:5px !important;
margin-bottom:15px !important;
padding-left:20px !important;
padding-right:20px !important;
padding-top:20px !important;
padding-bottom:20px !important;
}

table {
color:rgb(0,0,0) !important;
}

.filetitle {
color:rgb(0,0,0) !important;
}

textarea{
height:8em !important;
width:22em !important;
}

img.thumb,img[src*="/thumb/"] {
background:transparent !important;
border:solid 3px rgb(0,0,0) !important;
-moz-border-radius:6px !important;
opacity:100 !important;
z-index:3 !important;
}

html,body {
font-size:16px !important;
background-color:#FED699 !important;
background-image:url('http://quiche-skins.googlecode.com/files/yotsubabg.jpg') !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
background-position:95% 0% !important;
}

.replymode {
background-color:transparent !important;
color:#000000 !important;
}


th {
background-color:rgb(0,0,200) !important;
opacity:0 !important;
-moz-border-radius:10px !important;
}

tr {
background-color:transparent !important;
color:rgb(0,0,0) !important;
}

.unkfunc{
color:rgb(230,140,0) !important;
}




#bod1 {
background-color:transparent !important;
}




[class*="postername"] {
background-color:transparent !important; 
color:rgb(190,100,60) !important
}

[class*="postertrip"] {
color:rgb(190,100,60) !important
}

div {
color:rgb(0,0,0) !important;
}



input[type="file"] {
background-color:transparent !important;
}


.postarea .postblock{
-moz-border-radius:6px /*2px 0 0 2px*/ !important;
margin-right:-1px !important;
color:rgb(190,100,60) !important;
padding:0 3px !important;
border:solid 0px #555 !important;
opacity:100 !important;
border-right-style: dashed !important;
border:solid 1px rgb(0,0,0) !important;
text-align:center !important;
background-color:rgb(255,230,190) !important
}

/* hides ads */

div.logo img{display:none !important;}

iframe{display:none !important;}

/* hr */

hr{display:none !important;}

form hr{display:block !important;}

hr{height:0px !important;border:none !important;background:#999 !important;}


/* rounded corners */

td.replyhl, td.reply{-moz-border-radius:12px !important;opacity:100 !important;background-color:rgb(255,230,190) !important;color:rgb(0,0,0) !important;
border:solid 0px rgb(0,0,0) !important;
padding-bottom:10px !important;
}



/* links */

a{text-decoration:none !important;color:rgb(230,60,0) !important;font-weight:bold !important;}

a.quotejs:hover, a:hover{font-weight:bold !important;text-decoration:underline !important;color:rgb(230,60,0) !important;}



/* removes ugly borders on page overview [0,1,2...] */

table,td{border:none !important;}

td.rules{display:none !important;}



/* logo */

img + br {display:none !important;}




/* blurry thumbnails fix */

img[md5] {

height: auto !important;

width: auto !important;
}

/*4chan extension stuff*/

.hidethread,.unhidethread,.watchthread,.expandthread,.quickreply,.hidewatchedthreads,.refreshwatchedthreads,.restorewatchedthreads,* html .hidethread,.unhidethread,.watchthread,.expandthread,.quickreply,.hidewatchedthreads,.refreshwatchedthreads,.restorewatchedthreadsimg,img[src^="chrome://4chan/skin/buttons/"],img[alt="Locked"],img[alt="closed"] {
background-image:url('http://quiche-skins.googlecode.com/files/yotsuba_icons.png') !important;
}
img[src^="chrome://4chan/skin/buttons/"],img[alt="Locked"],img[alt="closed"] {

height:0 !important;

margin:0 2px !important;

padding-top:16px !important;

width:16px !important;

background-color:transparent !important;
}



[src$="post_expand_plus.png"] {

background-position:-48px 0 !important

}

.reply{background-color:rgb(255,230,190) !important;-moz-border-radius:8px}

[src$="arrow_up.png"] {

background-position:-64px 0 !important;
}



[src$="quote.png"] {

background-position:-80px 0 !important

}



[src$="stop.png"] {

background-position:-96px 0 !important

}



[src$="arrow_right.png"] {

background-position:-112px 0 !important

}



[src$="post_expand_minus.png"] {

background-position:-32px -16px !important

}



[src$="arrow_down.png"] {
background-position:-64px -16px !important
}



[src$="arrow_down2.png"] {
background-position:-80px -16px !important;
}


[src$="empty.png"] {

background-position:-96px -16px !important

}

[id^="4chan_ext"][class="reply"],.reflinkpreview{
background:transparent !important;
border:0px !important;
}



img[alt="Locked"],img[alt="closed"] {

background-position:-112px -16px !important
}

#twi_table_div{border:none !important}
[id="twi_table"]{background-color:rgb(255,230,190) !important;-moz-background-clip:padding !important;border:none !important}
#watchedthreadsdraghandle,#twi_table_div > .postblock{background-color:#FED699 !important;color:rgb(230,60,0) !important;border:1px solid black !important;-moz-border-radius:12px  12px 0 0 !important;padding:2px 0 !important;}
#twi_table{-moz-border-radius:0 0 12px 12px !important;border:1px solid black !important}
#watchedthreads{border:0px solid black !important}
}
/* QUICHE */
