css:
@-moz-document domain("fimfiction.net") {
/*pin rule*/
  div#add_comment_box {
    position:fixed !important;
    left:0;
    right:0;
    bottom:0;
    z-index:5;
    margin-bottom:0 !important;
  }

/*autoscroll kill*/
  a#new_comment{
    position:fixed !important;
    top:0 !important;
    right:0;
  }

/*[[compactify]]*/
/*Collapse Rules*/
  #add_comment_box .comment_content_container {
    display:none !important;
  }
  #add_comment_box:hover .comment_content_container {
    display:table !important;
  }
  #add_comment_box #comment_preview{
    min-height: 32px;
    max-height: 300px;
    overflow: auto;
    background-image:url('data:image/png;base64,') !important;
    background-repeat:no-repeat !important;
    background-position: center top !important;
  }
  #add_comment_box #comment_preview #comment_0{
    display:none !important;
  }
  #add_comment_box #comment_preview:hover #comment_0{
    display:block !important;
  }
/*[[pabst]]*/
}

style_setting_options.value:
/*Preview Above Buttons [Normal Mode]*/
  #add_comment_box .add_comment_toolbar {
    position:fixed !important;
    bottom:0 !important;
    left:0 !important;
    right:0 !important;
    border: 1px solid rgba(0,0,0,0.15) !important;
    border-top: 1px solid #ccc;
  }
  #add_comment_box {
    padding-bottom:65px !important;
  }

/*Preview Above Buttons [Compact Mode]*/
  #add_comment_box .add_comment_toolbar {
    position:fixed !important;
    bottom:0 !important;
    left:0 !important;
    right:0 !important;
    border: 1px solid rgba(0,0,0,0.15) !important;
    border-top: 1px solid #ccc;
  }
  #add_comment_box {
    padding-bottom:45px !important;
  }
