css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://i1305.photobucket.com/albums/s547/momotar/Image2_zps56688dae.png') !important;}

body {
  background:url("http://i1305.photobucket.com/albums/s547/momotar/Image0_zps8a74b561.png");
  background-color: #000000 !important;
  background-position: 50% 30% !important;
  background-size: 180px 180px  !important;
}

#logo {
  width: 210px !important;
  height: 56px !important;
}

#logo a {
  background:url("http://i1305.photobucket.com/albums/s547/momotar/Image7_zps183786ab.png") no-repeat!important;
}

#logo a:active {
  background-position: 0px 1px !important;
}

div.l-header .selection_nipple {
  top: 4px !important;
border-bottom: 12px solid rgba(50,50,50,.50)!important;
}


div.l-header .selection_nipple.white{
  border: none !important;
  border-bottom: none !important;
}

.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}



div.l-header .tab .tab_notice, #popover_blogs .tab_notice, #dashboard_controls_open_blog .tab_notice {font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif !important;}

.pill.notice{
color: rgba(255,255,255,0.50)!important;}

.search_form_row {
  background-color:rgba(50,50,50,0.55) !important;
}

.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/like"), url-prefix("http://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/reblog"), regexp("^http://www.tumblr.com/blog/[A-Za-z0-9._%+-]+/settings") , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/like"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/reblog") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background: rgba(50,50,50,0.55) !important;
  }

#posts .post .post_permalink {
width: 40px !important;
height: 40px !important;
background: url('http://i1305.photobucket.com/albums/s547/momotar/Image14_zps2e10ce42.png')!important;
background-size: 100% 100% !important;
position: absolute !important;
top: 6px !important;
right: -28px !important;
}

#posts .post .post_permalink:hover {
background: url('http://i1305.photobucket.com/albums/s547/momotar/Image15_zpsda3a7bb1.png') !important;
width: 40px !important;
height: 40px !important;
background-size: 100% 100% !important;
position: absolute !important;
top: 6px !important;
right: -28px !important;
}

#posts .post{
 background: #ffffff; }

.post_avatar:after {
  left:96%!important;
    }
.post_form_wrapper.detached .arrow {
            display:none !important;
          }
    
body .radar .radar_footer .radar_avatar:before,
body .controls_section.user_list li a:before {
	background: none;
  border-right: none;
}

body .radar .radar_footer .follow {
  color: #FFFFFF;
}

}



#posts .post.same_user_as_last .post_avatar, #posts .post.same_user_as_last .post_avatar_link, #posts .post.same_user_as_last .sub_avatar, #posts .post.same_user_as_last .user_menu_info_button {
 display:inherit !important;
}

div#right_column,
div#right_column:after {
  background: none !important;
}

#right_column .controls_section .with_subtitle small {
 color: #929292 !important;}

#posts .post .post_controls .reblog_count span {
  color: #ffffff !important;}

#posts .notification a:not(.avatar_frame) {
 border-bottom: 1px solid #626262 !important;}
#posts .notification .nipple.border, #posts .notification.alt .nipple.border {
 border-right: 7px solid transparent !important;
 border-left: 15px solid #626262 !important;
 left:-20px !important;
 top:12px !important;}
#posts .notification .nipple, #posts .notification.alt .nipple  {
 border-right: 6px transparent !important;}
#posts .notification .avatar_frame {
border: 2px solid #626262 !important;
border-radius: 60px !important;
top: 3px !important;}
#posts .notification a:not(.avatar_frame) {
text-decoration: none !important;
border-bottom: none !important;}



.notification .notification_sentence {
font-weight: normal !important;
font-size: 11px !important;

}
.notification .notification_sentence a{
font-weight: bold !important;
}

.notification .notification_right .preview_frame {
border: 1px solid #000000 !important;}

#toaster .toast {
background-color: rgb(0, 0, 0) !important;
}

.notification .report, .notification .block {
 color:#323232 !important;}
.notification .report, .notification .block:hover {
 color:#626262 !important;}




#posts .notification .avatar{
  border-radius: 60px !important;}

.pinned_sidebar_footer {
  opacity: 0 !important;}
.pinned_sidebar_footer:hover{
  opacity: 0.999 !important;}
.hide_footer .pinned_sidebar_footer {
  display: inherit !important;}


.no_posts_found, h1.dashboard_header {
color: rgba(255,255,255,0.75) !important;}

#posts .notification .notification_type_icon.like_icon {
background-position: -720px -20px;}
#posts .notification .notification_type_icon.reblog_icon {
background-position: -720px -40px;}
#posts .notification .notification_type_icon.follow_icon{width:14px;height:13px;background-position:-720px 0 !important;}
#posts .notification .notification_type_icon.answer_icon{background-position:-720px -60px !important;}


#pagination a{
background-color: rgba(0,0,0,0) !important;
}

img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
    opacity: 0 !important;
}

.new_post_label i {
    display: none !important;
}


#new_post_label_text,
#new_post_label_photo,
#new_post_label_quote,
#new_post_label_link,
#new_post_label_chat,
#new_post_label_audio,
#new_post_label_video {
  background:
 url("http://i1305.photobucket.com/albums/s547/momotar/Image20_zps71d09fc1.png") no-repeat !important;
}


#posts .post.new_post .new_post_label{
padding-top: 70px !important;
padding-bottom: 0px !important;
color: rgba(0,0,0,0) !important;
}

#new_post_label_text {
  background-position: 2px 5px !important;
  border-top-left-radius: 8px !important;
  border-bottom-left-radius: 8px !important;
}
#new_post_label_photo {
  background-position: -73px 5px !important;
}
#new_post_label_quote {
  background-position: -148px 5px !important;
}
#new_post_label_link {
  background-position: -223px 5px !important;
}
#new_post_label_chat {
  background-position: -298px 5px !important;
}
#new_post_label_audio {
  background-position: -373px 5px !important;
}
#new_post_label_video {
  background-position: -448px 5px !important;
  border-top-right-radius: 8px !important;
  border-bottom-right-radius: 8px !important;
}

#posts .post.new_post .new_post_label:hover{
color: rgba(80,80,80,.55) !important;  
}


#new_post_label_text:hover {
  background-position: 2px -100px !important;
}
#new_post_label_photo:hover {
  background-position: -73px -100px !important;
}
#new_post_label_quote:hover {
  background-position: -148px -100px !important;
}
#new_post_label_link:hover {
  background-position: -223px -100px !important;
}
#new_post_label_chat:hover {
  background-position: -298px -100px !important;
}
#new_post_label_audio:hover {
  background-position: -373px -100px !important;
}
#new_post_label_video:hover {
  background-position: -448px -100px !important;
}
body .footer_links a {
color: rgba(50,50,50,0.55);
}
