css:
/* AGENT_SHEET */
/*********************************************************************
*********************************************************************/
/*** SITE-WIDE STYLES ***/

@-moz-document domain(4chan.org), domain(boards.4chan.org), url(http://www.4chan.org/), domain(rs.4chan.org), domain(status.4chan.org) {

html, body {
	background: #FFF !important;
	color: #000 !important;
	}

*{
	font-size: 11px !important;
	font-family: Open Sans !important;
	}

}

/*********************************************************************
*********************************************************************/
/*** BOARDS & RAPIDSHARES ***/

@-moz-document domain("boards.4chan.org"), domain(rs.4chan.org) {

#boardNavDesktopFoot, #navbot {
	position: static !important;
	background: -moz-linear-gradient(top, #F1F1F1, #F9F9F9) !important;
	color: #555 !important;
	text-align: center;
	text-shadow: #fff 0 1px 0px;
	padding: 3px 0 3px 0 !important;
	bottom: 0;
	right: 0;
	left: 0;
	-webkit-box-shadow: inset 0 2px 2px -2px rgba(0,0,0,.5);
	-moz-box-shadow: inset 0 2px 2px -2px rgba(0,0,0,.5);
	z-index: 6;
	}
	
#boardNavDesktopFoot a:link, #boardNavDesktopFoot a:visited, 
#navbot a:link, #navbot a:visited {
	color: #555 !important;
	}

#boardNavDesktopFoot a:hover,
#navbot a:hover {
	color: #26A1D9 !important;
	}

#boardNavDesktop { 
	font-size: 11pt;
             }
}

/*********************************************************************
*********************************************************************/
/*** BOARDS & FRONTPAGE ***/

@-moz-document domain(boards.4chan.org), url(http://www.4chan.org/) {

#hd, hr, th, small, #boardNavDesktop, .boardBanner, .sideArrows, .prev, .next,
#option-button,  #absbot, .rules {
	display: none !important;
	}

.bf, blockquote embed, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], 
#recaptcha_logo, #recaptcha_tagline, .postForm > tbody > tr > td:first-child {
	display: none !important;
	}
	
.postingMode, #postForm span, .postingMode, .navLinks, .deleteform, .stylechanger  {
	display: none !important;
	}
}

/*********************************************************************
*********************************************************************/
/*** BOARDS ***/

@-moz-document domain("boards.4chan.org") {

   .boardTitle { 
	font-size: 24pt;
	color: #555;
	font-weight: 700;
	margin-top: 0px;
	}
  .globalMessage { 
                  color: black;
                  text-align: center;
                  }

.subject, .globalMessage strong, .pages, .pages strong, h2, textarea {
	color: #555 !important;
	}

.globalMessage a:hover, #delform a:hover, .pages a:hover, h1 a:hover {
	color: #333 !important;
	}

a, a:visited { 
                  color: #555555;
                  }

.summary, .abbr {
	color: #808080 !important;
	}

.globalMessage a, #delform a, #delform a:visited, .pages a, h1 a { 
                  color: #555555 !important;
                  text-decoration: none !important;
                  }
div.post div.postInfo span.postNum a:hover, .posteruid .hand:hover { 
                  color: #555555 !important;
                  }

.name { 
                  color: #555555 !important;
                  font-weight: 400 !important;
                  }
.useremail .name, .useremail .postertrip { 
                 color: #555555 !important;
                 }

.unkfunc, .postertrip {
	color: #78A931 !important;
	}
div.reply {
	background-color: #FCFCFC !important;
                   border-left: 0px !important;
	border-right: 0px !important;
	border-bottom: 0px !important;
	}

.pagelist {
	background: none !important;
	border: none  !important;
	margin-bottom: 20px !important;
	}
.preview{
	background-color: #F5F6F7;
	border-top: 1px solid rgba(0, 0, 0, 0.196);
	border-bottom: 1px solid rgba(0, 0, 0, 0.196);
	border-left: 1px solid rgba(0, 0, 0, 0.196);
	border-right: 1px solid rgba(0, 0, 0, 0.196);
}

.reply:target, .reply.highlight { 
	background: #E1E1E1 !important;
}

div.pagelist > div { 
border-bottom: 0px solid gray;
border-left: 0px solid black;
border-right: 0px solid gray;
border-top: 0px solid black;
float: none;
margin-left: 30px;
}

.op .fileText {
	margin-left: 20px !important;
	}
	
.op .fileThumb img, .reply .fileThumb img {
	-webkit-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-moz-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	}
	
#delform {
	margin-bottom: 25px !important;
	}
	
.summary {
	margin-left: 25px !important;
	}
	
.reply .fileText {
	margin-left: 20px !important;
	}

.globalMessage, .thread {
	background-color: #FFF !important;
	margin: 0 25px 10px 30px !important;
	border: 1px solid #D5D5D5 !important;
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	}

.globalMessage {
	padding: 5px 5px 5px 5px !important;
	}

.thread, .opContainer {
	overflow: hidden !important;
	}

.postInfo input[type="checkbox"] {
	background: #FFF !important;
	border: 1px solid #CECECE !important;
	height: 10px !important;
	width: 10px !important;
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	-moz-border-radius: 2px !important;
	-webkit-border-radius: 2px !important;
	}
	
.replyContainer {
	margin: 0 0 0 0 !important;
	}

.postContainer:nth-of-type(even) {
    background-color: #FFF !important;
	}

.post.reply.highlight {
	background-color: #D5D6D7 !important;
	}
	
.post.reply {
	background-color: #FFF !important;
             border-top: 1px solid #E5E5E5 !important;
	display: block !important;
	}
	
.reply .fileText { 
        margin-left:20pximportant;
}
	
.prettyprint {
	border: 1px dotted #D5D5D5 !important;
	-moz-border-radius: 2px !important;
	-webkit-border-radius: 2px !important;
	}
	
.postblock { 
	background-color: #FFF !important; 
	color: #555 !important;
	font-weight: 700;
	padding: 0px 5px;
	}

.quoteLink, .quotelink, .deadlink { 
                 color: #555555 !important;
                 }
	

#qr {
	background-color: rgb(255,255,255) !important;
	border: 1px solid #FFF !important;
	margin: 1px 6px 1px 6px !important;
	-webkit-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-moz-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	}
	
#qr select {
	background: #FFF !important;
	border: 1px solid #D5D5D5 !important;
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	}
	
form div {
	margin: 1px 1px 1px 1px !important;
	}
	
.postContainer.inline {
	background-color: #F5F6F7 !important;
	border: 1px solid #D5D5D5F !important;
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	}
	
.postContainer.inline .reply {
	border: none !important;
	}

#autohide, #spoiler {
	background: #FFF !important;
	border: 1px solid #D5D5D5 !important;
	height: 10px !important;
	width: 10px !important;
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	-moz-border-radius: 2px !important;
	-webkit-border-radius: 2px !important;
	}
	
#options, #watcher {
	background-color: rgb(255,255,255) !important;
	border: none  !important;
	-webkit-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-moz-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	}
	
#updater {
	top: 2px !important;
	right: 0 !important;
	bottom: auto !important;
	left: auto !important;
	z-index: 10 !important;
 	}
}

/*********************************************************************
*********************************************************************/
/*** FRONTPAGE ***/
@-moz-document url(http://www.4chan.org/){

a:link, a:visited {
	color: #555 !important;
	text-decoration: none !important;
	}

a:hover {
	color: #26A1D9 !important;
	}

#ft {
	display: none !important;
	}

html, body  {
	padding: 10px 0px !important;
	}
	
#hd, #close-announce {
	display: none !important;
	}
	
.boxbar, #preview-tooltip-nws H2, #preview-tooltip-ws H2{
	background: none !important;
	text-align: center !important;
	color: #944 !important;
	border-bottom: 1px solid rgba(0,0,0,.2) !important;
	}
	
#announce .boxcontent {
	text-align: center !important;
	}
	
.menubutton {
	background-image: none !important;
	}

#filter-button, #option-button {
	letter-spacing: 0px !important;
	padding : 0px !important;
	margin: 0 !important;
	}

#filter-button:hover, #option-button:hover {
	opacity: .7 !important;
	}

.yui-menu-shadow {
	display: none !important;
	}

#recent-images LI {
	background: none !important;
	}

#boards .column {
	width: 12em !important;
	}

.column, h3, .p_postername
  { color: #4D4D54 !important;
	}

.box-outer, #preview-tooltip-nws, #preview-tooltip-ws {
	background-color: rgb(255,255,255) !important;
	border-top: none !important;
	border-right: none !important;
	border-bottom: 1px solid #FFFFFF !important;
	border-left: none !important;
	color: #4D4D54 !important;
	-webkit-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-moz-box-shadow: 0 0 2px rgba(0,0,0,.3);
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	}

#preview-tooltip-nws, #preview-tooltip-ws {
	padding: 5px !important;
	}

.boxcontent img {
	margin: 0 auto !important;
	}
}

@-moz-document url-prefix(http://www.4chan.org/framesnav){
#logo {
	background: none !important; 
	height: 0 !important;
	padding-bottom: 5px !important;
	}
	
.plus {
	background: #DDD !important;
	border: none !important;
	}

h2 {
	background-color: #FAFAFA !important;
	border: 1px solid #FFFFFF !important;
	color: #4D4D54 !important;
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	-webkit-box-shadow: 0px 0px 4px rgba(0,0,0,.4);
	-moz-box-shadow: 0px 0px 4px rgba(0,0,0,.4);
	}

li{
	padding-left: 5px !important;
	padding-right: 5px !important;
	}
}

/*********************************************************************
*********************************************************************/
/*** RAPIDSHARES ***/

@-moz-document domain(rs.4chan.org) {

.postarea input[type=file] > input[type=button],
.postarea input[name="upfile"] {
	background: #FFF !important;
	height: 19px !important;
	}

input {
	-moz-appearance: none;
	-webkit-appearance: none;
	background-color: #FAFAFA !important;
	border: 1px solid #D5D5D5 !important;
	height: 19px !important;
	padding: 1px 0 1px 0 !important;
	}

div.postarea {
	background-color: #FFF !important;
	border: 1px solid #D5D5D5 !important;
	color: #555 !important;
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	max-width: 300px !important;
	min-width: 300px !important;
	padding: 10px 0px 10px 0px;
	margin-top: 15px;
	margin-right:auto;
	margin-bottom: 3px;
	margin-left:auto;
	}

.postblock {
	background-color: #FAFAFA !important;
	border: 1px solid #D5D5D5 !important;
	color: #555 !important;
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	}

.pages {
	background-color: #FFF !important;
	border: none  !important;
	}

.postarea input[type=text],
.postarea input[type=password],
.postarea input[type=file][name="upfile"] > input[type=text],
.postarea input[type=file] > input[type=button],
.postarea textarea.inputtext {
	-moz-appearance: none;
	-webkit-appearance: none;
	border: 1px solid #D5D5D5 !important;
	height: 19px !important;
	padding: 1px 0 1px 0 !important;
	}

body .postarea input[name="sub"],
.postarea input[type=file][name="upfile"] > input[type=text] {
	margin: 0 1px 0 0 !important;
	vertical-align: middle;
	}

.postarea #com_submit,
.postarea input[type=file] > input[type=button] {
	height: 19px !important;
	margin: 0 0 0 0 !important;
	padding: 0 !important;
	vertical-align: middle;
	font-size: 10px !important;
	font-family: "Helvetica Neue", Arial, Helvetica, sans-serif !important;
	color: #555 !important;
	}
	
.postarea input[type="file"]::-webkit-file-upload-button,
#quickrep input[type="file"]::-webkit-file-upload-button {
	-moz-appearance: none;
	background: transparent;
	border: 1px solid #D5D5D5 !important;
	height: 19px !important;
	padding: 0 !important;
	}

.postarea textarea.inputtext,
textarea#message {
	height: 19px !important;
	}

.postarea textarea#message {
	height: 19px !important;
	}

.postarea input[type=submit] {
	-moz-appearance: none;
	border: 1px solid #D5D5D5 !important;
	height: 19px !important;
	}

.postarea .postblock , #hd, hr, th, small, font[size="1"], font[size="2"], #option-button, #navtopr, #header, 
.rules, .logo, .logo font b span,  img.rotating,  table[align="right"], body > table + span, 
body > form > hr:last-of-type + span {
	display: none !important;
	}

.bf, blockquote embed, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], 
#recaptcha_logo, #recaptcha_tagline {
	display: none !important;
	}
}
