css:
@-moz-document url-prefix("http://loading.se/")
{

/* 
NYA loading.se remake av lassekongo83
---------*Tahehoo Edition*---------
Testad i Google Chrome 11.0.696.65
*/

/* Bredden på huvudkolumnen */
#main_col 
{ 
	width: 97% !important;
}

/* Tar bort ett antal saker som inte behövs */
.ox_zone, #sec_col, #header_news, #footer, #fb-root, #header span.vert_sep, .fblike, #ebDiv22851915005594492, img[height="1000"], div[style="display: inline; width: 974px; height: 300px; margin: 0px auto;"]
{
	display: none !important;
}

/* Färger, form och herpy derp! :D */
body 
{ 
	background-color: #f4f4f4 !important;
	font-family: arial, verdana, helvetica neue, helvetica, sans-serif !important;
	padding-top: 0 !important;
}

.theme_light
{
	background-image: url(https://i.imgur.com/6F0hYq1.png) !important;
}

.theme_dark
{
	background-image: url(https://i.imgur.com/KWEbyJn.png) !important;
}

*
{
	text-shadow: none !important;
}

/* Fyller ut innehållet på andra sidor istället för bara på forumet */
.main_left_col
{
	width: 688px !important;
}

/* Rundar kanterna längst ner och tar bort det onödiga utrymmet */
#site_wrapper 
{
	border-radius: 0px 0px 7px 7px !important;
	margin-bottom: 0px !important;
}

/* Färgen på headern */
#header 
{
	margin-top: 0px !important;
	background-color: #112f49 !important;
	background-image: none !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #112f49, #477094) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#112f49), to(#0A4070)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#0A4070, #112f49) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#0A4070, #112f49) !important;
}

/* Avataren i headern */
#header .avatar 
{ 
	background-color: #345c80 !important; 
	border: none !important;
	border-radius: 2px 2px 2px 2px !important;	
}

#header .avatar img 
{
	box-shadow: 0px 0px 3px #000 !important;
}

/* Färgen på profilsidornas header */
#user_header
{
	background-image: none !important;
	margin-top: -22px !important;
	margin-right: -23px !important;
	background-color: #112f49 !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #112f49, #477094) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#112f49), to(#0A4070)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#0A4070, #112f49) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#0A4070, #112f49) !important;
}

#user_header .frame
{
	background-color: #345c80 !important;
	border: none !important;
	border-radius: 4px 4px 4px 4px !important;
}

#user_header .frame .avatar
{ 
	box-shadow: 0px 0px 3px #000 !important;
	border-color: rgba(0, 0, 25, 0.5) !important;
}

#user_header .meta > span
{
	color: #8EB4D3 !important;
}

/* Den blå navigeringsmenyn */
#site_navigation
{
	position: static !important;
	height: 25px !important;
	width: 974px !important;
	z-index: 1000 !important;
	box-shadow: 0px 0px 3px !important;
	border-top: none !important;
	border-bottom: 0px solid #537592 !important;
	background-color: #345c80 !important;
	background-image: none !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #345c80, #537592) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#345c80), to(#537592)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#537592, #345c80) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#537592, #345c80) !important;
}

/* Färgen på blogg-, quote- och pm-knapparna uppe till höger */
.rgt .pictos
{
	color: #537592 !important;
}

.rgt .pictos:hover
{
	color: #ffffff !important;
}

#site_navigation > ul.nav
{
	height: 20px !important;
}

#site_navigation > ul.nav > li
{
	height: 20px !important;
}

#site_navigation > ul.nav > li div.hit_area
{
	top: 10px !important;
}

#site_navigation > ul.nav > li > a
{
	position: relative !important;
	top: -10px !important;
	height: 20px !important;
}

#site_navigation ul.nav > li > a, ul.nav.dd > li > span { padding-top: 0 !important; }

#site_navigation div.repelled { 
	position: relative !important; 
	top: -8px !important;
}

/* Dropdown-grejen vid avataren uppe i högra hörnet */
#header ul.nav.sub
{
	border-radius: 0px 0px 6px 6px !important;
	box-shadow: 0px 0px 10px #000 !important;
	background-color: rgba(0, 0, 25, 0.8) !important;
}

#header > div > div > ul > li > div > ul > li:hover
{
	background-color: rgba(50, 100, 150, 0.5);
}

/* Dropdown-grejen på den blå navigeringsmenyn */
#site_navigation ul.nav.sub
{ 
	border-radius: 0px 0px 6px 6px !important;
	box-shadow: 0px 0px 10px #000 !important;
	background-color: rgba(0, 0, 25, 0.8) !important;
}

#site_navigation > ul.nav > li > div > ul  > li:hover
{
	background-color: rgba(50, 100, 150, 0.5);
}

#site_navigation > ul.nav > li > div > ul  > li.active
{
	background-color: rgba(50, 100, 150, 0.5);
}

ul.nav.dd > li:hover > a, ul.nav.dd > li:hover > span
{ 
	background-color: rgba(0, 0, 25, 0) !important;
	border-radius: 0px 0px 0px 0px !important;
	min-height: 22px !important;
	max-height: 22px !important;
}

/* Flyttar högerkolumnen på huvudsidan längre upp */
.section_index .main_right_col
{
	top: 0px !important;
	right: 4px !important;
	position: absolute !important;
}

/* Sökrutan */
.g_search
{
	background: none !important;
	border: none !important;
	border-radius: 0px 0px 0px 0px !important;
	max-height: 24px !important;
}

.g_search input
{ 
	border-radius: 12px 12px 12px 12px !important;
	border-top: none !important;
	font: arial !important;
	font-weight: normal !important;
	margin-top: 2px !important;
	box-shadow:inset 0 0 -1px !important;
	background-image: none !important;
	/* fallback */
	background-color: #f0f0f0 !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #dedfe0, #fefefe) !important;
	/* Safari 4-5, Chrome 1-9 */
	/* -webkit-gradient(<type>, <point> [, <radius>]?, <point> [, <radius>]? [, <stop>]*) */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#dedfe0), to(#fefefe)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#fefefe, #dedfe0) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#fefefe, #dedfe0) !important;
}

/* GTFO @ prickikorv */
.noise
{
	background-image: none !important;
}

/* Den blå lilla titeln vid högerkolumnen på huvudsidan bland annat */
.related_box > h3
{
	background-image: none !important;
	box-shadow: 0px 3px 5px #000 !important;
	border-radius: 4px 4px 4px 4px !important;
	background-color: #345c80 !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #345c80, #537592) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#345c80), to(#537592)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#537592, #345c80) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#537592, #345c80) !important;
}

#main_col .related_box ul
{
	padding-top: 3px !important;
}

/* Quote-delen */
.forum_text_quote
{ 
	background-color: #ffffff !important; 
	padding: 8px !important;
	border: 0px solid #CCCCCC !Important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #ededed, #ffffff) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#ededed), to(#ffffff)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#ffffff, #ededed) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#ffffff, #ededed) !important;
}

.quote
{ 
	background-color: #ffffff !important; 
	padding: 8px !important;
	border: 0px solid #cccccc !Important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #ededed, #ffffff) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#ededed), to(#ffffff)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#ffffff, #ededed) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#ffffff, #ededed) !important;
}

.quote p, .forum_text_quote p
{
	font-style: italic !important;
	line-height: 16px !important;
	color: #333 !important;
	font-size: 12px !important;
}

.forum_text_quote .author, .quote .author, .forum_text_quote .author:hover, .quote .author:hover
{
	font-size: 12px !important;
	font-style: bold !important;
	color: #222 !important;
}

.quote .quote_char, .forum_text_quote .quote_char
{
	color: #cccccc !important;
}

/* Det som visar vilken forumdel man är i */
.forum_cat_sep
{
	background-image: none !important;
	background-clip: none !important;
	box-shadow: 0px 0px 0px !important;
	border: 1px solid #ccc !important;
	border-radius: 0px 0px 0px 0px !important;
	/* fallback */
	background-color: #dcdcdc !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #dcdcdc, #e8e8e8) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#dcdcdc), to(#e8e8e8)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#e8e8e8, #dcdcdc) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#e8e8e8, #dcdcdc) !important;
}

.forum_cat_sep a:hover
{
	color: #45abdc !important;
}

.forum_cat_name, .forum_cat_desc, .forum_bc_separator
{
	color: #888888 !important;
}

.forum_cat_name a, .forum_cat_desc a, .forum_bc_separator a
{
	color: #333333 !important;
}

/* Grejen som visar vilken tråd-layout vi valt */
.forum_style_sel
{
	background: none !important;
	border-top: none !important;
	border-bottom: none !important;
	border-left: none !important;
	border-right: 1px solid #cccccc !important;
}

.forum_style_sel.active
{
	background-image: none !important;
	border-top: none !important;
	border-bottom: none !important;
	border-left: 1px solid #f0f0f0 !important;
	border-right: 1px solid #ccc !important;
	background-color: #dcdcdc !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #e8e8e8, #dcdcdc) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#e8e8e8), to(#dcdcdc)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#dcdcdc, #e8e8e8) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#dcdcdc, #e8e8e8) !important;
}

.forum_bc_separator
{
	background: none !important;
	width: 0px !important;
	padding: 0 !important;
	margin: 0 !important;
	border-left: 1px solid #cccccc !important;
	border-right: 1px solid #f0f0f0 !important;
}

/* Trådtiteln längst upp */
.forum_thread_info
{
	background-image: none !important;
	border-bottom: 1px solid #ccc !important;
	/* fallback */
	background-color: #dcdcdc !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #dcdcdc, #e8e8e8) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#dcdcdc), to(#e8e8e8)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#e8e8e8, #dcdcdc) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#e8e8e8, #dcdcdc) !important;
}

.forum_thread_info h3
{
	color: #444 !important;
}

.forum_thread_info
{
	color: #999 !important;
}

/* Knappar */
.forum_cat_sep .button
{
	background-image: none !important;
	border-left: 1px solid #f0f0f0 !important;
	border-right: none !important;
	border-bottom: none !important;
	border-top: 1px solid #e8e8e8 !important;
	background-color: #dcdcdc !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #cdcdcd, #dcdcdc) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#cdcdcd), to(#dcdcdc)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#dcdcdc, #cdcdcd) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#dcdcdc, #cdcdcd) !important;
	border-radius: 0px 0px 0px 0px !important;
}

.forum_cat_sep .button:hover
{ 
	background-image: none !important;
	background-color: #e2e2e2 !important;
	color: #45abdc !important;
	border-radius: 0px 0px 0px 0px !important;
}

.button
{
	background-image: none !important;
	border-left: 1px solid #cccccc !important;
	border-right: 1px solid #cccccc !important;
	border-bottom: 1px solid #cccccc !important;
	border-top: 1px solid #cccccc !important;
	background-color: #dcdcdc !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #dcdcdc, #e8e8e8) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#dcdcdc), to(#e8e8e8)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#e8e8e8, #dcdcdc) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#e8e8e8, #dcdcdc) !important;
	border-radius: 0px 0px 0px 0px !important;
}

.button.ok
{
	color: #333 !important;
}

.button:hover
{
	background-image: none !important;
	/* fallback */
	background-color: #e8e8e8 !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #e8e8e8, #f5f5f5) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#e8e8e8), to(#f5f5f5)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#f5f5f5, #e8e8e8) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#f5f5f5, #e8e8e8) !important;
	border-radius: 0px 0px 0px 0px !important;
}

/* Inloggningsknappen */
#header #login_nav ul.nav.dd > li > a
{
	background-image: none !important;
	color: #ffffff !important;
	border: none !important;
	background-color: #7b95aa !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #5972a7, #637bad) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#5972a7), to(#637bad)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#637bad, #5972a7) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#637bad, #5972a7) !important;
	border-radius: 10px 10px 10px 10px !important;
}

/* Hover-effekter på länkar och dylikt */
.forum_user_off:hover
{
	color: #999999 !important;
}

.forum_user_on:hover
{
	color: #FF66AA !important;
}

a.forum_forum_thread:hover
{
	color: #888888 !important;
}

.forum_text_url:hover, .forum_thread_tbr .action_link:hover, .action_link:hover, .back a:hover, .author:hover,
h3 a:hover, .user_text_url:hover, a[class="action_link"][href="#comment"]:hover
{
	color: #71c6f0 !important;
}

.forum_thread_nav > a 
{
	color: #888888 !important;
}

.forum_thread_nav > a:hover
{
	color: #bbbbbb !important;
}

/* Dold text ska vara tydligare samt visas när musen är över istället för att man ska behöva markera allt ;D */
.forum_text_hidden1
{
	font-weight: bold !important;
	margin: 0 !important;
	color: #ee4793 !important;
}

span.forum_text_hidden2:hover
{
	font-weight: normal !important;
	color: #ffffff !important;
}

/* Bredden på texten */
.forum_thread_text
{
	width: auto !important;
}

/* Fet text till länkar och lite andra fixar med fet text och undertext */
.forum_thread_text strong
{
	font-weight: bold !important;
}

.forum_thread_text u strong, .forum_thread_text u strong a, .forum_thread_text strong u, .forum_thread_text strong u a
{
	text-decoration: underline !important; font-weight: bold !important;
}

.forum_thread_text u a, .forum_thread_text u
{
	text-decoration: underline !important;
}

/* Text area (där man skriver sina inlägg) */
.thread_reply.light textarea
{
	background-color: #f4f4f4 !important;
	border-color: #ccc !important;
	max-width: 618px !important;
	width: 618px !important;
	border-radius: 0px 0px 0px 0px !important;
}

.lform input[type="text"], .lform input[type="password"], .lform select, .lform textarea
{ 
	background-color: #f4f4f4 !important;
	border-color: #ccc !important;
	border-radius: 0px 0px 0px 0px !important;
	font-family: arial, verdana, helvetica neue, helvetica, sans-serif !important;
}

.lform
{
	border-radius: 0px 0px 0px 0px !important;
}

form[id="text"][class="lform"]
{
	background-color: #f0f0f0 !important;
	border-color: #ccc !important;
}

/* Gör inläggen lite mer kompakta samt separerar avatar-delen med en border  */
td[class="forum_thread_tbr_td"][width="160"]
{
	width: 148px !important;
	border-right: 1px dotted #ccc !important;
}

.section_forum .forum_thread_nav
{
	border-color: #ccc !important;
}

.forum_cat_tbr td, .forum_forum_tbr td, .forum_thread_tbr_foot > td, .forum_thread_tbr > td
{
border: none !important;
}

/* Spelgrannar, mestadels fix för Opera */
.match_bar_inner
{
	background-image: none !important;
	/* fallback */
	background-color: #9ad548 !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #9ad548, #c8e870) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#9ad548), to(#c8e870)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#c8e870, #9ad548) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#c8e870, #9ad548) !important;
}

/* Navigeringsknappar */
#user_nav li > a
{
	background-image: none !important;
	/* fallback */
	background-color: #b0b0b0 !important;
	/* Firefox 3.6+ */
	background: -moz-linear-gradient(100% 100% 90deg, #b0b0b0, #d5d5d5) !important;
	/* Safari 4-5, Chrome 1-9 */
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#b0b0b0), to(#d5d5d5)) !important;
	/* Safari 5.1+, Chrome 10+ */
	background: -webkit-linear-gradient(#b0b0b0, #ffffff) !important;
	/* Opera 11.10+ */
	background: -o-linear-gradient(#d5d5d5, #b0b0b0) !important;
}

#user_nav li > a:hover
{
	color: #45abdc !important;
	background-image: none !important;
	background-color: #ffffff !important;
}

#user_nav li.active > a, #user_nav li.active > a:hover
{
	background-image: none !important;
	background-color: #ffffff !important;
}

/* Fyller ut bloggtexten lite mera */
.blogg_text
{
	width: 100% !important;
}

/* Knapparna längst ner i ett inlägg */

/* Positionering av anmäl-knappen */
td[align="left"] span.action_link
{
	position: relative !important;
	left: 580px !important;
}

/* Lite större och annorlunda avatarer */
.forum_thread_tbr_td .avatar img
{
	border: none !important;
	height: 120px !important;
	width: 120px !important;
}

/* Fyller ut bloggtexten lite mer #45abdc */
.blogg_text
{
	width: 100% !important;
}

}
