css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
   
    #statuspanel /* change this section values to finely match your LocationBar position */
{top: 5.4% !important;
left: 9.1% !important;
right: 56.3% !important;
margin-top: 0 !important;
margin-left: 8px;
margin-right: 0px;

text-align: center !important;     
font-size:12px !important;
}

#statuspanel-inner {
-moz-box-align: start !important;
}


#statuspanel-label { 
padding-top: 5px !important;
padding-bottom: 4px !important;
padding-left: 6px !important;
padding-right: 6px !important;
    
background-color:hsla(210,50%,85%,1)!important;
background:-moz-radial-gradient(center bottom,hsla(210,60%,65%,1),transparent),-moz-linear-gradient(center top,hsla(210,55%,17%,1),hsla(190,55%,8%,1)),-moz-linear-gradient(hsla(190,55%,25%,.7), hsla(190,55%,25%,.2) 85%,hsla(190,55%,25%,.4))!important;
box-shadow:0 1px 4px hsla(190,60%,65%,.4)!important;
opacity: 0.9 !important;
color: white !important;
text-shadow: black 1px 1px 1px !important;
font-weight: normal !important;
border-top-style: solid !important;
border-bottom-style: solid !important;
border-right-style: solid !important;
border-left-style: solid !important;
border-color: white !important;
}
#statuspanel[type="status"] #statuspanel-label[value^="Look"],
#statuspanel[type="status"] #statuspanel-label[value^="Connect"],
#statuspanel[type="status"] #statuspanel-label[value^="Send"],
#statuspanel[type="status"] #statuspanel-label[value^="Transfer"],
#statuspanel[type="status"] #statuspanel-label[value^="Read"],
#statuspanel[type="status"] #statuspanel-label[value^="Wrote"],
#statuspanel[type="status"] #statuspanel-label[value^="Wait"],
#statuspanel[type="status"] #statuspanel-label[value*="TLS handshake"],
#statuspanel[type="status"] #statuspanel-label[value*="FTP transaction"] {
 display:none!important;
}
